INSERT INTO checklist_templates (country_code, category_key, title, description, detailed_description, mandatory, category, priority, document_required, sort_order, legal_basis, authority, penalties, tips, links, applicable_products, document_types) VALUES

('DE', 'electronics', 'CE-Kennzeichnung pruefen', 'Pruefen Sie, ob das CE-Zeichen korrekt und sichtbar angebracht ist.', 'Die CE-Kennzeichnung ist fuer alle Elektronikprodukte im europaeischen Wirtschaftsraum verpflichtend. Sie muss mindestens 5 mm hoch sein, gut sichtbar und dauerhaft auf dem Produkt oder dem Typenschild angebracht werden. Der Hersteller oder sein Bevollmaechtigter bringt die CE-Kennzeichnung an und uebernimmt damit die Verantwortung fuer die Konformitaet. Ohne gueltige CE-Kennzeichnung darf das Produkt nicht in der EU in Verkehr gebracht werden.', true, 'Produktsicherheit & CE-Konformitaet', 'critical', true, 1, 'Verordnung (EU) 2023/988 (GPSR), Richtlinie 2014/35/EU (Niederspannung)', 'Bundesanstalt fuer Arbeitsschutz und Arbeitsmedizin (BAuA)', 'Bussgeld bis zu 100.000 EUR, Verkaufsverbot, Produktrueckruf', ARRAY['CE-Zeichen muss mindestens 5 mm hoch sein', 'Anbringung auf dem Produkt selbst, nicht nur auf der Verpackung', 'Konformitaetserklaerung muss vor dem Inverkehrbringen erstellt werden', 'Alle relevanten Richtlinien in der Erklaerung auflisten'], '[{"title": "EU-Produktsicherheitsverordnung GPSR", "url": "https://eur-lex.europa.eu/eli/reg/2023/988"}, {"title": "BAuA CE-Kennzeichnung", "url": "https://www.baua.de/DE/Themen/Anwendungssichere-Chemikalien-und-Produkte/Produktsicherheit/CE-Kennzeichnung"}, {"title": "Blue Guide EU", "url": "https://eur-lex.europa.eu/legal-content/DE/TXT/?uri=uriserv:OJ.C_.2022.247.01.0001.01.DEU"}]'::jsonb, ARRAY['Smartphone', 'Tablet', 'Laptop', 'Monitor', 'Fernseher', 'Kopfhoerer', 'Lautsprecher', 'Smart-Home-Hub', 'Router', 'Drucker'], ARRAY['EU-Konformitaetserklaerung', 'Technische Dokumentation', 'Pruefberichte']),

('DE', 'electronics', 'EU-Konformitaetserklaerung erstellen', 'Vollstaendige EU-Konformitaetserklaerung gemaess GPSR erstellen und bereithalten.', 'Die EU-Konformitaetserklaerung ist ein rechtsverbindliches Dokument, das vom Hersteller oder seinem Bevollmaechtigten ausgestellt wird. Sie muss alle angewandten EU-Rechtsvorschriften, harmonisierten Normen und die Identifikation des Produkts enthalten. Ab 2024 gelten durch die neue GPSR erweiterte Anforderungen. Die Erklaerung muss in der Sprache des Ziellandes verfuegbar sein und auf Anfrage den Marktaufsichtsbehoerden vorgelegt werden koennen.', true, 'Produktsicherheit & CE-Konformitaet', 'critical', true, 2, 'Verordnung (EU) 2023/988 (GPSR) Art. 9', 'Bundesanstalt fuer Arbeitsschutz und Arbeitsmedizin (BAuA)', 'Bussgeld bis zu 100.000 EUR, Vertriebsverbot', ARRAY['Alle angewandten EU-Richtlinien und Verordnungen auflisten', 'Harmonisierte Normen mit Ausgabedatum angeben', 'Erklaerung in Deutsch fuer den deutschen Markt bereithalten', 'Regelmaessig bei Normenaktualisierungen ueberpruefen', 'Unterschrift eines Zeichnungsberechtigten erforderlich'], '[{"title": "GPSR Verordnung", "url": "https://eur-lex.europa.eu/eli/reg/2023/988"}, {"title": "BAuA Konformitaet", "url": "https://www.baua.de/DE/Themen/Anwendungssichere-Chemikalien-und-Produkte/Produktsicherheit"}]'::jsonb, ARRAY['Smartphone', 'Laptop', 'Desktop-PC', 'Monitor', 'Fernseher', 'Drucker', 'Router', 'Smart-Home-Hub'], ARRAY['EU-Konformitaetserklaerung', 'Technische Dokumentation']),

('DE', 'electronics', 'WEEE-Registrierung bei Stiftung EAR', 'Registrierung als Hersteller von Elektrogeraeten bei der Stiftung EAR.', 'Jeder Hersteller oder Importeur von Elektro- und Elektronikgeraeten muss sich vor dem Inverkehrbringen bei der Stiftung Elektro-Altgeraete Register (EAR) registrieren. Die Registrierung erfolgt nach Geraetekategorien und erfordert die Stellung einer insolvenzfesten Garantie. Ohne gueltige Registrierung besteht ein Vertriebsverbot. Die Registrierungsnummer muss auf Rechnungen und in Online-Shops angegeben werden.', true, 'Umwelt & Nachhaltigkeit', 'critical', true, 3, 'ElektroG (Elektro- und Elektronikgeraetegesetz), WEEE 2012/19/EU', 'Stiftung EAR (Elektro-Altgeraete Register)', 'Bussgeld bis zu 100.000 EUR, sofortiges Vertriebsverbot', ARRAY['Registrierung vor dem ersten Inverkehrbringen abschliessen', 'Garantie bei zugelassenem Garantiesystem hinterlegen', 'WEEE-Registrierungsnummer auf allen Rechnungen angeben', 'Jahresmengen bis zum 30. April des Folgejahres melden'], '[{"title": "Stiftung EAR Registrierung", "url": "https://www.stiftung-ear.de"}, {"title": "WEEE-Richtlinie", "url": "https://eur-lex.europa.eu/eli/dir/2012/19"}, {"title": "ElektroG Gesetzestext", "url": "https://www.gesetze-im-internet.de/elektrog_2015/"}]'::jsonb, ARRAY['Smartphone', 'Tablet', 'Laptop', 'Desktop-PC', 'Monitor', 'Fernseher', 'Kopfhoerer', 'Drucker', 'Router', 'Smartwatch', 'Spielkonsole'], ARRAY['WEEE-Registrierungsbescheinigung', 'Garantienachweis', 'Mengenmeldungen']),

('DE', 'electronics', 'RoHS-Konformitaet sicherstellen', 'Einhaltung der Beschraenkung gefaehrlicher Stoffe in Elektrogeraeten.', 'Die RoHS-Richtlinie beschraenkt die Verwendung von sechs Schwermetallen und vier Phthalaten in Elektro- und Elektronikgeraeten. Die Grenzwerte betragen 0,1% fuer Blei, Quecksilber, sechswertiges Chrom, PBB, PBDE und die Phthalate DEHP, BBP, DBP sowie 0,01% fuer Cadmium. Hersteller muessen die Konformitaet durch Materialanalysen und Lieferantenerklaerungen nachweisen. Die Dokumentation muss 10 Jahre aufbewahrt werden.', true, 'Chemikalien & Inhaltsstoffe', 'high', true, 4, 'RoHS 2011/65/EU, Delegierte Richtlinie (EU) 2015/863', 'Bundesanstalt fuer Arbeitsschutz und Arbeitsmedizin (BAuA)', 'Bussgeld bis zu 50.000 EUR, Produktrueckruf', ARRAY['Lieferantenerklaerungen fuer alle Bauteile einholen', 'XRF-Screening fuer Stichproben durchfuehren', 'DEHP, BBP, DBP und DIBP seit 2019 beachten', 'Ausnahmen in Anhang III der Richtlinie pruefen', 'Dokumentation 10 Jahre aufbewahren'], '[{"title": "RoHS-Richtlinie", "url": "https://eur-lex.europa.eu/eli/dir/2011/65"}, {"title": "ECHA RoHS", "url": "https://echa.europa.eu/"}, {"title": "BAuA RoHS", "url": "https://www.baua.de/DE/Themen/Anwendungssichere-Chemikalien-und-Produkte/Produktsicherheit"}]'::jsonb, ARRAY['Smartphone', 'Tablet', 'Laptop', 'Desktop-PC', 'Monitor', 'Fernseher', 'Kopfhoerer', 'Lautsprecher', 'Drucker', 'Router'], ARRAY['RoHS-Konformitaetserklaerung', 'Materialanalysen', 'Lieferantenerklaerungen']),

('DE', 'electronics', 'Energieverbrauchskennzeichnung', 'EU-Energielabel korrekt anbringen fuer energieverbrauchsrelevante Produkte.', 'Fuer bestimmte Elektronikprodukte wie Fernseher, Monitore und Displays ist die Energieverbrauchskennzeichnung verpflichtend. Seit 2021 gilt das neue Energielabel mit der Skala A bis G. Das Label muss im stationaeren Handel gut sichtbar angebracht und im Online-Handel als Produktdatenblatt bereitgestellt werden. Die Registrierung in der EPREL-Datenbank ist vor dem Inverkehrbringen erforderlich.', true, 'Kennzeichnung & Verpackung', 'high', true, 5, 'Verordnung (EU) 2017/1369, Delegierte Verordnungen je Produktgruppe', 'Bundesanstalt fuer Arbeitsschutz und Arbeitsmedizin (BAuA)', 'Bussgeld bis zu 50.000 EUR', ARRAY['EPREL-Registrierung vor dem Inverkehrbringen durchfuehren', 'Neues Label A-G verwenden, nicht mehr A+++ bis D', 'QR-Code auf dem Label muss zur EPREL-Datenbank fuehren', 'Produktdatenblatt im Online-Shop bereitstellen'], '[{"title": "Energielabel-Verordnung", "url": "https://eur-lex.europa.eu/eli/reg/2017/1369"}, {"title": "EPREL-Datenbank", "url": "https://eprel.ec.europa.eu/"}]'::jsonb, ARRAY['Fernseher', 'Monitor', 'Laptop', 'Beamer', 'Digital Signage Display'], ARRAY['Energielabel', 'Produktdatenblatt', 'EPREL-Registrierungsnachweis']),

('DE', 'electronics', 'REACH-SVHC-Pruefung', 'Pruefung auf besonders besorgniserregende Stoffe (SVHC) gemaess REACH.', 'Die REACH-Verordnung verpflichtet Lieferanten von Erzeugnissen, ihre Kunden zu informieren, wenn ein SVHC-Stoff in einer Konzentration von mehr als 0,1 Massenprozent enthalten ist. Die SVHC-Kandidatenliste wird zweimal jaehrlich aktualisiert. Fuer Elektronikprodukte sind besonders Weichmacher, Flammschutzmittel und bestimmte Loetlegierungen relevant. Ab 2021 besteht zusaetzlich eine Meldepflicht an die SCIP-Datenbank der ECHA.', true, 'Chemikalien & Inhaltsstoffe', 'high', true, 6, 'REACH (EG) Nr. 1907/2006, Art. 33 und Art. 7', 'Bundesanstalt fuer Arbeitsschutz und Arbeitsmedizin (BAuA), ECHA', 'Bussgeld bis zu 50.000 EUR', ARRAY['SVHC-Kandidatenliste regelmaessig pruefen (halbjahrl. Update)', 'SCIP-Meldung an ECHA-Datenbank durchfuehren', 'Lieferantenbefragungen dokumentieren', 'Schwellenwert 0,1% bezieht sich auf Einzelerzeugnisse'], '[{"title": "REACH-Verordnung", "url": "https://eur-lex.europa.eu/eli/reg/2006/1907"}, {"title": "ECHA SVHC-Liste", "url": "https://echa.europa.eu/candidate-list-table"}, {"title": "SCIP-Datenbank", "url": "https://echa.europa.eu/scip"}]'::jsonb, ARRAY['Smartphone', 'Tablet', 'Laptop', 'Kopfhoerer', 'Lautsprecher', 'Kabel', 'Adapter', 'Ladegeraet'], ARRAY['SVHC-Pruefbericht', 'SCIP-Meldenachweis', 'Lieferantenerklaerungen']),

('DE', 'electronics', 'Verpackungsregistrierung LUCID', 'Registrierung bei LUCID und Beteiligung an einem dualen System.', 'Wer verpackte Elektronikprodukte in Deutschland erstmalig in Verkehr bringt, muss sich im Verpackungsregister LUCID registrieren und an einem dualen System (z.B. Der Gruene Punkt, Interseroh) beteiligen. Die Registrierungsnummer muss oeffentlich einsehbar sein. Jahresmengenmeldungen muessen sowohl an LUCID als auch an das duale System erfolgen. Bei Nichtbeachtung gilt ein sofortiges Vertriebsverbot.', true, 'Kennzeichnung & Verpackung', 'high', true, 7, 'Verpackungsgesetz (VerpackG) §9', 'Zentrale Stelle Verpackungsregister (ZSVR)', 'Bussgeld bis zu 200.000 EUR, sofortiges Vertriebsverbot', ARRAY['Registrierung vor dem ersten Inverkehrbringen abschliessen', 'Systembeteiligungsvertrag mit dualem System abschliessen', 'Jahresmengenmeldungen fristgerecht abgeben', 'Registrierungsnummer im Online-Shop anzeigen'], '[{"title": "LUCID Verpackungsregister", "url": "https://lucid.verpackungsregister.org/"}, {"title": "Verpackungsgesetz", "url": "https://www.gesetze-im-internet.de/verpackg/"}]'::jsonb, ARRAY['Smartphone', 'Tablet', 'Laptop', 'Kopfhoerer', 'Drucker', 'Router', 'Externe Festplatte'], ARRAY['LUCID-Registrierungsnachweis', 'Systembeteiligungsvertrag', 'Mengenmeldungen']),

('DE', 'electronics', 'Digitaler Produktpass (DPP) Vorbereitung', 'Vorbereitung auf den digitalen Produktpass gemaess ESPR.', 'Die EU-Verordnung fuer oekologisches Produktdesign (ESPR) fuehrt den digitalen Produktpass fuer Elektronikprodukte ein. Fuer Elektronik wird der DPP voraussichtlich ab 2028 verpflichtend. Der DPP muss ueber einen QR-Code oder Datentraeger abrufbar sein und Informationen zu Materialzusammensetzung, Reparierbarkeit, Recyclingfaehigkeit und CO2-Fussabdruck enthalten. Fruehzeitige Vorbereitung verschafft Wettbewerbsvorteile.', false, 'Digitaler Produktpass', 'medium', false, 8, 'ESPR (EU) 2024/1781', 'Europaeische Kommission, Bundesministerium fuer Wirtschaft und Klimaschutz', 'Noch keine direkten Strafen, Marktzugang ab Deadline gefaehrdet', ARRAY['Produktdaten strukturiert digital erfassen', 'Materialzusammensetzung lueckenlos dokumentieren', 'CO2-Fussabdruck berechnen und dokumentieren', 'QR-Code-Infrastruktur fruehzeitig aufbauen', 'Lieferkettendaten digital verfuegbar machen'], '[{"title": "ESPR-Verordnung", "url": "https://eur-lex.europa.eu/eli/reg/2024/1781"}, {"title": "EU DPP Info", "url": "https://hadea.ec.europa.eu/programmes/single-market-programme/digital-product-passport_en"}]'::jsonb, ARRAY['Smartphone', 'Tablet', 'Laptop', 'Desktop-PC', 'Monitor', 'Fernseher', 'Drucker', 'Router'], ARRAY['DPP-Datentemplate', 'Materialdeklaration', 'CO2-Bilanz']),

('DE', 'electronics', 'Elektromagnetische Vertraeglichkeit (EMV)', 'Nachweis der elektromagnetischen Vertraeglichkeit gemaess EMV-Richtlinie.', 'Alle elektronischen Geraete muessen die Anforderungen der EMV-Richtlinie erfuellen. Dies umfasst Stoerfestigkeit und Stoeremission. Die Pruefung erfolgt nach harmonisierten Normen wie EN 55032 fuer Multimedia-Geraete. Der Nachweis kann durch Herstellererklaerung mit internen Tests oder durch akkreditierte Pruefstellen erfolgen. Die Bundesnetzagentur fuehrt Marktueberwachung durch und kann Geraete vom Markt nehmen.', true, 'Produktsicherheit & CE-Konformitaet', 'high', true, 9, 'EMV-Richtlinie 2014/30/EU', 'Bundesnetzagentur (BNetzA)', 'Bussgeld bis zu 50.000 EUR, Vertriebsverbot', ARRAY['EMV-Tests nach relevanten EN-Normen durchfuehren', 'EN 55032 fuer Multimediageraete beachten', 'Pruefberichte in der technischen Dokumentation ablegen', 'Bei Aenderungen am Design erneut pruefen'], '[{"title": "EMV-Richtlinie", "url": "https://eur-lex.europa.eu/eli/dir/2014/30"}, {"title": "Bundesnetzagentur EMV", "url": "https://www.bundesnetzagentur.de/DE/Fachthemen/Telekommunikation/Technik/EMV/emv-node.html"}]'::jsonb, ARRAY['Smartphone', 'Laptop', 'Desktop-PC', 'Monitor', 'Router', 'Smart-Home-Hub', 'Drucker', 'Spielkonsole'], ARRAY['EMV-Pruefbericht', 'Technische Dokumentation']),

('DE', 'electronics', 'Niederspannungsrichtlinie einhalten', 'Sicherheitsanforderungen fuer elektrische Betriebsmittel mit Nennspannung 50-1000V AC.', 'Die Niederspannungsrichtlinie gilt fuer alle elektrischen Betriebsmittel mit einer Nennspannung zwischen 50 und 1000 V Wechselstrom bzw. 75 und 1500 V Gleichstrom. Sie stellt Sicherheitsanforderungen zum Schutz vor elektrischem Schlag, thermischen Gefahren, mechanischen Gefahren und Strahlung. Die Konformitaet wird durch Anwendung harmonisierter Normen wie der EN 62368-1 vermutet.', true, 'Produktsicherheit & CE-Konformitaet', 'high', true, 10, 'Richtlinie 2014/35/EU (Niederspannungsrichtlinie)', 'Bundesanstalt fuer Arbeitsschutz und Arbeitsmedizin (BAuA)', 'Bussgeld bis zu 100.000 EUR, Produktrueckruf', ARRAY['EN 62368-1 als zentrale Norm anwenden', 'Sicherheitspruefung durch akkreditiertes Labor', 'Schutzklasse und Schutzart (IP) korrekt bestimmen', 'Bedienungsanleitung mit Sicherheitshinweisen in Deutsch'], '[{"title": "Niederspannungsrichtlinie", "url": "https://eur-lex.europa.eu/eli/dir/2014/35"}]'::jsonb, ARRAY['Laptop', 'Desktop-PC', 'Monitor', 'Fernseher', 'Drucker', 'Ladegeraet', 'Netzteil', 'Klimaanlage'], ARRAY['Sicherheitspruefbericht', 'Technische Dokumentation', 'EU-Konformitaetserklaerung']),

('DE', 'electronics', 'Funkgeraeterichtlinie (RED) einhalten', 'Konformitaet mit der Funkanlagenrichtlinie fuer drahtlose Geraete.', 'Alle Geraete mit Funktechnologie (WLAN, Bluetooth, NFC, Mobilfunk) muessen die Radio Equipment Directive erfuellen. Dies umfasst Anforderungen an Gesundheitsschutz, elektromagnetische Vertraeglichkeit und effiziente Spektrumnutzung. Ab August 2025 gelten zusaetzliche Cybersicherheitsanforderungen nach delegierter Verordnung. Die Bundesnetzagentur erteilt die Markterlaubnis und ueberwacht die Einhaltung.', true, 'Produktsicherheit & CE-Konformitaet', 'critical', true, 11, 'Richtlinie 2014/53/EU (RED), Delegierte Verordnung (EU) 2022/30', 'Bundesnetzagentur (BNetzA)', 'Bussgeld bis zu 50.000 EUR, Verkaufsverbot, Frequenzzuteilungsentzug', ARRAY['RED-Konformitaetsbewertung fuer jedes Funkmodul', 'SAR-Wert bei koerpernahen Geraeten bestimmen', 'Cybersicherheitsanforderungen ab 08/2025 beachten', 'Frequenzbereiche fuer den EU-Markt pruefen'], '[{"title": "RED-Richtlinie", "url": "https://eur-lex.europa.eu/eli/dir/2014/53"}, {"title": "Bundesnetzagentur RED", "url": "https://www.bundesnetzagentur.de/DE/Fachthemen/Telekommunikation/Technik/RED/red-node.html"}]'::jsonb, ARRAY['Smartphone', 'Tablet', 'Laptop', 'Smartwatch', 'Kopfhoerer', 'Bluetooth-Lautsprecher', 'Smart-Home-Hub', 'Router', 'WLAN-Repeater', 'Drohne'], ARRAY['RED-Pruefbericht', 'SAR-Messbericht', 'Frequenznutzungsnachweis']),

('DE', 'electronics', 'Oekodesign-Anforderungen pruefen', 'Einhaltung der Oekodesign-Anforderungen fuer energieverbrauchsrelevante Produkte.', 'Die Oekodesign-Richtlinie und ihre delegierten Verordnungen stellen Mindestanforderungen an die Energieeffizienz und Ressourceneffizienz. Fuer Elektronik sind insbesondere Standby-Verbräuche, Reparierbarkeit und Verfuegbarkeit von Ersatzteilen relevant. Die ESPR wird diese Anforderungen ab 2025 schrittweise erweitern. Bereits jetzt gelten Anforderungen fuer Server, Displays, Netzteile und andere Produktgruppen.', true, 'Umwelt & Nachhaltigkeit', 'medium', true, 12, 'Richtlinie 2009/125/EG (Oekodesign), ESPR (EU) 2024/1781', 'Bundesanstalt fuer Arbeitsschutz und Arbeitsmedizin (BAuA)', 'Bussgeld bis zu 50.000 EUR', ARRAY['Produktspezifische Oekodesign-Verordnung identifizieren', 'Standby-Verbrauch unter den Grenzwerten halten', 'Ersatzteilverfuegbarkeit fuer mindestens 7-10 Jahre sicherstellen', 'Reparaturanleitungen bereitstellen'], '[{"title": "Oekodesign-Richtlinie", "url": "https://eur-lex.europa.eu/eli/dir/2009/125"}, {"title": "ESPR", "url": "https://eur-lex.europa.eu/eli/reg/2024/1781"}]'::jsonb, ARRAY['Fernseher', 'Monitor', 'Server', 'Netzteil', 'Ladegeraet', 'Desktop-PC', 'Laptop'], ARRAY['Oekodesign-Konformitaetsnachweis', 'Energieeffizienz-Datenblatt']),

('DE', 'electronics', 'Bedienungsanleitung in deutscher Sprache', 'Vollstaendige Bedienungsanleitung mit Sicherheitshinweisen auf Deutsch bereitstellen.', 'Jedes Elektronikprodukt muss eine Bedienungsanleitung in der Amtssprache des Ziellandes enthalten. Fuer Deutschland ist Deutsch erforderlich. Die Anleitung muss Sicherheitshinweise, bestimmungsgemaessen Gebrauch, Entsorgungshinweise und Kontaktdaten des Herstellers oder Importeurs enthalten. Digitale Bereitstellung ist zulaessig, wenn auch eine gedruckte Version auf Anfrage erhaeltlich ist.', true, 'Dokumentation & Rueckverfolgbarkeit', 'medium', false, 13, 'Verordnung (EU) 2023/988 (GPSR) Art. 9, ProdSG', 'Bundesanstalt fuer Arbeitsschutz und Arbeitsmedizin (BAuA)', 'Bussgeld bis zu 25.000 EUR', ARRAY['Anleitung in klarer, verstaendlicher Sprache verfassen', 'Sicherheitshinweise deutlich hervorheben', 'Entsorgungshinweise und WEEE-Symbol aufnehmen', 'Kontaktdaten des verantwortlichen Wirtschaftsakteurs angeben'], '[{"title": "GPSR", "url": "https://eur-lex.europa.eu/eli/reg/2023/988"}, {"title": "Produktsicherheitsgesetz", "url": "https://www.gesetze-im-internet.de/prodsg_2021/"}]'::jsonb, ARRAY['Smartphone', 'Tablet', 'Laptop', 'Fernseher', 'Kopfhoerer', 'Drucker', 'Router', 'Smart-Home-Hub'], ARRAY['Bedienungsanleitung (DE)', 'Sicherheitsdatenblatt']),

('DE', 'electronics', 'Ruecknahme- und Entsorgungspflichten', 'Einrichtung eines Ruecknahmesystems fuer Elektro-Altgeraete.', 'Hersteller und Vertreiber von Elektronikgeraeten sind zur Ruecknahme von Altgeraeten verpflichtet. Vertreiber mit mehr als 400 m2 Verkaufsflaeche muessen kleine Altgeraete kostenlos zuruecknehmen. Hersteller muessen zudem die Entsorgungskosten tragen und die Sammelquote von 65% erfuellen. Auch Online-Haendler sind seit 2022 zur Ruecknahme verpflichtet.', true, 'Umwelt & Nachhaltigkeit', 'medium', true, 14, 'ElektroG §§ 16-17, WEEE 2012/19/EU', 'Stiftung EAR, Umweltbundesamt (UBA)', 'Bussgeld bis zu 100.000 EUR', ARRAY['Ruecknahmesystem einrichten oder beauftragen', 'Kunden ueber Rueckgabemoeglichkeiten informieren', 'Online-Handel: Ruecknahme bei Auslieferung anbieten', 'Entsorgungskosten kalkulieren und einplanen'], '[{"title": "ElektroG", "url": "https://www.gesetze-im-internet.de/elektrog_2015/"}, {"title": "Stiftung EAR", "url": "https://www.stiftung-ear.de"}, {"title": "UBA Elektro-Altgeraete", "url": "https://www.umweltbundesamt.de/themen/abfall-ressourcen/produktverantwortung-in-der-abfallwirtschaft/elektro-elektronikaltgeraete"}]'::jsonb, ARRAY['Smartphone', 'Tablet', 'Laptop', 'Desktop-PC', 'Monitor', 'Fernseher', 'Drucker', 'Kopfhoerer'], ARRAY['Ruecknahmekonzept', 'Entsorgungsnachweis', 'Sammelquotennachweis']),

('DE', 'electronics', 'CSRD Nachhaltigkeitsberichterstattung', 'Nachhaltigkeitsberichterstattung gemaess Corporate Sustainability Reporting Directive.', 'Die CSRD verpflichtet grosse Unternehmen und boersennotierte KMU zur erweiterten Nachhaltigkeitsberichterstattung. Fuer Elektronik-Hersteller und -Importeure sind insbesondere Angaben zu CO2-Emissionen, Ressourcenverbrauch, Kreislaufwirtschaft und Lieferkette relevant. Die Berichtspflicht wird stufenweise eingefuehrt: ab 2025 fuer grosse Unternehmen, ab 2026 fuer boersennotierte KMU. Die Berichte muessen nach den European Sustainability Reporting Standards (ESRS) erstellt werden.', false, 'Soziale Verantwortung & Lieferkette', 'medium', true, 15, 'CSRD (EU) 2022/2464, ESRS', 'Bundesanstalt fuer Finanzdienstleistungsaufsicht (BaFin)', 'Bussgeld bei Verletzung der Berichtspflicht, Reputationsschaeden', ARRAY['Wesentlichkeitsanalyse durchfuehren', 'Datenerhebungsprozesse fuer ESG-Kennzahlen einrichten', 'CO2-Bilanz nach GHG Protocol erstellen', 'Lieferketten-Sorgfaltspflichten dokumentieren'], '[{"title": "CSRD-Richtlinie", "url": "https://eur-lex.europa.eu/eli/dir/2022/2464"}, {"title": "EFRAG ESRS", "url": "https://www.efrag.org/lab6"}]'::jsonb, ARRAY['Smartphone', 'Laptop', 'Desktop-PC', 'Monitor', 'Fernseher', 'Server'], ARRAY['Nachhaltigkeitsbericht', 'CO2-Bilanz', 'ESG-Daten']),

('DE', 'electronics', 'Lieferkettensorgfaltspflichten (LkSG)', 'Einhaltung der Sorgfaltspflichten in der Lieferkette nach LkSG.', 'Das Lieferkettensorgfaltspflichtengesetz verpflichtet Unternehmen ab 1.000 Mitarbeitern zur Einrichtung eines Risikomanagements in der Lieferkette. Dies umfasst Menschenrechte und Umweltbelange. Fuer Elektronik sind insbesondere Konfliktmineralien, Arbeitsbedingungen in der Fertigung und Umweltauswirkungen des Rohstoffabbaus relevant. Ab 2026 wird das LkSG durch die EU-Lieferkettenrichtlinie CSDDD ergaenzt.', true, 'Soziale Verantwortung & Lieferkette', 'high', true, 16, 'LkSG (Lieferkettensorgfaltspflichtengesetz), CSDDD (EU) 2024/1760', 'Bundesamt fuer Wirtschaft und Ausfuhrkontrolle (BAFA)', 'Bussgeld bis zu 2% des weltweiten Jahresumsatzes', ARRAY['Risikoanalyse fuer alle direkten Zulieferer durchfuehren', 'Grundsatzerklaerung zur Menschenrechtsstrategie veroeffentlichen', 'Beschwerdemechanismus einrichten', 'Konfliktmineralien (3TG) in der Lieferkette nachverfolgen', 'Jahresbericht ueber Sorgfaltspflichten erstellen'], '[{"title": "LkSG", "url": "https://www.gesetze-im-internet.de/lksg/"}, {"title": "BAFA LkSG", "url": "https://www.bafa.de/DE/Lieferketten/lieferketten_node.html"}, {"title": "CSDDD", "url": "https://eur-lex.europa.eu/eli/dir/2024/1760"}]'::jsonb, ARRAY['Smartphone', 'Tablet', 'Laptop', 'Desktop-PC', 'Monitor', 'Fernseher', 'Kopfhoerer', 'Smartwatch'], ARRAY['Risikoanalyse', 'Grundsatzerklaerung', 'LkSG-Jahresbericht']),

('DE', 'electronics', 'Batterie-Kennzeichnung bei integrierten Akkus', 'Korrekte Kennzeichnung von Geraetebatterien gemaess EU-Batterieverordnung.', 'Elektronikgeraete mit integrierten Akkus unterliegen der EU-Batterieverordnung. Die Batterie muss mit dem Batteriesymbol (durchgestrichene Muelltonne), Kapazitaetsangabe und chemischem System gekennzeichnet sein. Ab 2027 wird ein QR-Code zum digitalen Batteriepass erforderlich. Zudem muss der Endnutzer ueber die sichere Entnahme informiert werden, sofern die Batterie vom Benutzer entfernbar ist.', true, 'Kennzeichnung & Verpackung', 'medium', true, 17, 'EU-Batterieverordnung (EU) 2023/1542', 'Umweltbundesamt (UBA)', 'Bussgeld bis zu 100.000 EUR', ARRAY['Batteriesymbol gut sichtbar auf dem Geraet anbringen', 'Kapazitaet in Wattstunden (Wh) angeben', 'Chemisches System kennzeichnen (z.B. Li-ion)', 'Hinweise zur sicheren Entnahme beifuegen'], '[{"title": "EU-Batterieverordnung", "url": "https://eur-lex.europa.eu/eli/reg/2023/1542"}, {"title": "UBA Batterien", "url": "https://www.umweltbundesamt.de/themen/abfall-ressourcen/produktverantwortung-in-der-abfallwirtschaft/batterien"}]'::jsonb, ARRAY['Smartphone', 'Tablet', 'Laptop', 'Smartwatch', 'Kopfhoerer', 'Bluetooth-Lautsprecher', 'Powerbank', 'E-Reader'], ARRAY['Batteriekennzeichnung', 'Batterie-Datenblatt']),

('DE', 'electronics', 'Datenschutz und Cybersicherheit', 'Einhaltung von Datenschutz- und Cybersicherheitsanforderungen fuer vernetzte Geraete.', 'Vernetzte Elektronikgeraete muessen Datenschutzanforderungen der DSGVO und kuenftige Cybersicherheitsanforderungen des Cyber Resilience Act erfuellen. Dies umfasst Privacy by Design, sichere Standard-Einstellungen und die Verpflichtung zu Sicherheitsupdates. Der CRA wird voraussichtlich ab 2027 verbindlich. Bereits jetzt verlangen Verbraucher und B2B-Kunden nachweisbare IT-Sicherheit.', false, 'Dokumentation & Rueckverfolgbarkeit', 'medium', false, 18, 'DSGVO (EU) 2016/679, Cyber Resilience Act (EU) 2024/2847', 'Bundesamt fuer Sicherheit in der Informationstechnik (BSI)', 'DSGVO: bis zu 20 Mio EUR oder 4% des Jahresumsatzes', ARRAY['Privacy by Design von Anfang an umsetzen', 'Sichere Standardeinstellungen konfigurieren', 'Prozess fuer Sicherheitsupdates etablieren', 'Datenschutzerklaerung fuer das Geraet bereitstellen'], '[{"title": "DSGVO", "url": "https://eur-lex.europa.eu/eli/reg/2016/679"}, {"title": "Cyber Resilience Act", "url": "https://eur-lex.europa.eu/eli/reg/2024/2847"}, {"title": "BSI", "url": "https://www.bsi.bund.de"}]'::jsonb, ARRAY['Smartphone', 'Tablet', 'Laptop', 'Smart-Home-Hub', 'Ueberwachungskamera', 'Smartwatch', 'Router', 'Smart Speaker'], ARRAY['Datenschutz-Folgenabschaetzung', 'IT-Sicherheitskonzept']),

('DE', 'electronics', 'Produkthaftung und Wirtschaftsakteur-Pflichten', 'Pflichten als Hersteller, Importeur oder Haendler gemaess GPSR und ProdSG.', 'Die neue Produktsicherheitsverordnung GPSR und das deutsche Produktsicherheitsgesetz definieren klare Pflichten fuer jeden Wirtschaftsakteur. Hersteller muessen eine Risikoanalyse durchfuehren und technische Dokumentation erstellen. Importeure muessen die Konformitaet des Herstellers pruefen. Haendler muessen die Einhaltung der Kennzeichnungspflichten ueberpruefen. Jeder Wirtschaftsakteur muss auf dem Produkt identifizierbar sein.', true, 'Dokumentation & Rueckverfolgbarkeit', 'high', true, 19, 'Verordnung (EU) 2023/988 (GPSR), ProdSG', 'Bundesanstalt fuer Arbeitsschutz und Arbeitsmedizin (BAuA)', 'Bussgeld bis zu 100.000 EUR, Produktrueckruf', ARRAY['Eigene Rolle als Wirtschaftsakteur bestimmen', 'Kontaktdaten auf dem Produkt oder der Verpackung anbringen', 'Risikoanalyse dokumentieren und aktuell halten', 'Rueckrufprozesse definieren und testen'], '[{"title": "GPSR", "url": "https://eur-lex.europa.eu/eli/reg/2023/988"}, {"title": "ProdSG", "url": "https://www.gesetze-im-internet.de/prodsg_2021/"}]'::jsonb, ARRAY['Smartphone', 'Tablet', 'Laptop', 'Desktop-PC', 'Monitor', 'Fernseher', 'Drucker', 'Router', 'Ladegeraet'], ARRAY['Risikoanalyse', 'Technische Dokumentation', 'Identifikation des Wirtschaftsakteurs']),

('DE', 'electronics', 'ErP-Standby-Verbrauch begrenzen', 'Begrenzung des Standby- und Aus-Zustands-Verbrauchs gemaess ErP.', 'Die Oekodesign-Verordnung (EU) Nr. 1275/2008 begrenzt den Energieverbrauch elektronischer Geraete im Standby- und Aus-Zustand. Der Standby-Verbrauch darf maximal 0,50 Watt betragen, im Aus-Zustand maximal 0,50 Watt. Geraete mit Netzwerk-Standby duerfen maximal 2-8 Watt verbrauchen. Diese Anforderungen gelten fuer nahezu alle Elektronikprodukte mit Netzanschluss.', true, 'Umwelt & Nachhaltigkeit', 'medium', true, 20, 'Verordnung (EU) Nr. 1275/2008, (EU) Nr. 801/2013', 'Bundesanstalt fuer Arbeitsschutz und Arbeitsmedizin (BAuA)', 'Bussgeld bis zu 50.000 EUR', ARRAY['Standby-Verbrauch messen und dokumentieren', 'Netzwerk-Standby-Funktion optimieren', 'Automatische Abschaltfunktion implementieren', 'Produktdatenblatt mit Energieverbrauchsdaten erstellen'], '[{"title": "Standby-Verordnung", "url": "https://eur-lex.europa.eu/eli/reg/2008/1275"}, {"title": "Netzwerk-Standby", "url": "https://eur-lex.europa.eu/eli/reg/2013/801"}]'::jsonb, ARRAY['Fernseher', 'Monitor', 'Desktop-PC', 'Drucker', 'Set-Top-Box', 'Spielkonsole', 'Router', 'Smart-Home-Hub'], ARRAY['Energieverbrauchsmessung', 'Produktdatenblatt']);

-- ============================================
-- DEUTSCHLAND (DE) - TEXTILIEN
-- ============================================
INSERT INTO checklist_templates (country_code, category_key, title, description, detailed_description, mandatory, category, priority, document_required, sort_order, legal_basis, authority, penalties, tips, links, applicable_products, document_types) VALUES

('DE', 'textiles', 'Textilkennzeichnung Faserzusammensetzung', 'Korrekte Angabe der Faserzusammensetzung gemaess EU-Textilkennzeichnungsverordnung.', 'Die EU-Textilkennzeichnungsverordnung schreibt die vollstaendige Angabe der Faserzusammensetzung in absteigender Reihenfolge des Gewichtsanteils vor. Die Bezeichnungen muessen den vorgegebenen Faserbezeichnungen entsprechen. Bei Mischungen muessen alle Fasern ab 5% Anteil einzeln aufgefuehrt werden. Das Etikett muss dauerhaft, leicht lesbar und in deutscher Sprache angebracht sein.', true, 'Kennzeichnung & Verpackung', 'critical', false, 1, 'Textilkennzeichnungsverordnung (EU) Nr. 1007/2011', 'Bundesanstalt fuer Arbeitsschutz und Arbeitsmedizin (BAuA)', 'Bussgeld bis zu 50.000 EUR', ARRAY['Fasern in absteigender Reihenfolge des Gewichtsanteils angeben', 'Vorgeschriebene Faserbezeichnungen verwenden', 'Toleranzen beachten: +/- 3% bei Analysen', 'Etikett muss waschbestaendig sein', 'Bei Mehrkomponenten-Produkten jede Komponente einzeln kennzeichnen'], '[{"title": "Textilkennzeichnungsverordnung", "url": "https://eur-lex.europa.eu/eli/reg/2011/1007"}, {"title": "BAuA Textilkennzeichnung", "url": "https://www.baua.de/DE/Themen/Anwendungssichere-Chemikalien-und-Produkte/Produktsicherheit"}]'::jsonb, ARRAY['T-Shirt', 'Hemd', 'Bluse', 'Pullover', 'Jeans', 'Kleid', 'Jacke', 'Unterwaesche', 'Socken', 'Bettwaesche'], ARRAY['Textilkennzeichnungsetikett', 'Faseranlyse-Zertifikat']),

('DE', 'textiles', 'REACH-Konformitaet fuer Textilien', 'Einhaltung der REACH-Beschraenkungen fuer Textilchemikalien.', 'Textilprodukte muessen die Beschraenkungen des REACH-Anhangs XVII einhalten. Besonders relevant sind Grenzwerte fuer AZO-Farbstoffe (Anhang XVII, Eintrag 43), Nickel bei Hautkontakt, Formaldehyd, Chrom VI in Leder und bestimmte Flammschutzmittel. SVHC-Stoffe ueber 0,1% Massenanteil muessen kommuniziert werden. Regelmaessige Pruefungen durch akkreditierte Labore sind empfohlen.', true, 'Chemikalien & Inhaltsstoffe', 'critical', true, 2, 'REACH (EG) Nr. 1907/2006, Anhang XVII', 'Bundesanstalt fuer Arbeitsschutz und Arbeitsmedizin (BAuA), ECHA', 'Bussgeld bis zu 50.000 EUR, Produktrueckruf', ARRAY['AZO-Farbstoffe-Test durchfuehren (max. 30 mg/kg)', 'Formaldehyd-Grenzwerte beachten', 'Nickel-Release bei Metallteilen pruefen', 'SVHC-Kandidatenliste regelmaessig pruefen', 'OEKO-TEX oder GOTS Zertifizierung erwaegen'], '[{"title": "REACH-Verordnung", "url": "https://eur-lex.europa.eu/eli/reg/2006/1907"}, {"title": "ECHA Textilien", "url": "https://echa.europa.eu/de/regulations/reach/registration"}, {"title": "SCIP-Datenbank", "url": "https://echa.europa.eu/scip"}]'::jsonb, ARRAY['T-Shirt', 'Hemd', 'Pullover', 'Jeans', 'Kleid', 'Kinderbekleidung', 'Unterwaesche', 'Socken', 'Bettwaesche', 'Handtuch'], ARRAY['REACH-Pruefbericht', 'AZO-Farbstoff-Analyse', 'Lieferantenerklaerung']),

('DE', 'textiles', 'Pflegekennzeichnung anbringen', 'Korrekte Pflegesymbole nach ISO 3758 anbringen.', 'Pflegesymbole sind in Deutschland zwar nicht gesetzlich vorgeschrieben, aber branchenueblich und werden von Verbrauchern erwartet. Die Symbole muessen der ISO 3758 / GINETEX-Norm entsprechen und umfassen Waschen, Bleichen, Trocknen, Buegeln und professionelle Textilpflege. Falsche Pflegehinweise koennen zu Gewaehrleistungsanspruechen fuehren.', false, 'Kennzeichnung & Verpackung', 'medium', false, 3, 'ISO 3758, GINETEX-Lizenzsystem', 'Keine spezifische Behoerde (Branchenstandard)', 'Keine direkten Bussgelder, aber Gewaehrleistungsrisiko', ARRAY['GINETEX-Lizenz fuer kommerzielle Nutzung der Symbole erwerben', 'Pflegeanleitung am tatsaechlichen Textil testen', 'Alle fuenf Symbolkategorien angeben', 'Symbole in der richtigen Reihenfolge anordnen'], '[{"title": "GINETEX", "url": "https://www.ginetex.net"}, {"title": "ISO 3758", "url": "https://www.iso.org/standard/75404.html"}]'::jsonb, ARRAY['T-Shirt', 'Hemd', 'Bluse', 'Pullover', 'Jeans', 'Kleid', 'Jacke', 'Bettwaesche', 'Handtuch'], ARRAY['Pflegeanleitung', 'GINETEX-Lizenz']),

('DE', 'textiles', 'Verpackungsregistrierung LUCID fuer Textilverpackungen', 'Registrierung der Textilverpackungen bei LUCID und Systembeteiligung.', 'Alle Verpackungen, in denen Textilien an Endverbraucher verkauft werden, unterliegen dem Verpackungsgesetz. Dies schliesst Plastiktueten, Kartons, Kleiderbuegelverpackungen und Versandverpackungen ein. Eine Registrierung bei LUCID und die Beteiligung an einem dualen System sind Pflicht. Die Meldepflichten umfassen Jahresmengen nach Materialfraktion.', true, 'Kennzeichnung & Verpackung', 'high', true, 4, 'Verpackungsgesetz (VerpackG)', 'Zentrale Stelle Verpackungsregister (ZSVR)', 'Bussgeld bis zu 200.000 EUR, Vertriebsverbot', ARRAY['Alle Verpackungskomponenten erfassen (Primaer, Sekundaer, Transport)', 'Registrierung vor dem ersten Inverkehrbringen', 'Versandverpackungen bei Online-Handel nicht vergessen', 'Kleiderbuegelverpackungen einbeziehen'], '[{"title": "LUCID", "url": "https://lucid.verpackungsregister.org/"}, {"title": "VerpackG", "url": "https://www.gesetze-im-internet.de/verpackg/"}]'::jsonb, ARRAY['T-Shirt', 'Hemd', 'Pullover', 'Jeans', 'Kleid', 'Jacke', 'Unterwaesche', 'Schuhe'], ARRAY['LUCID-Registrierungsnachweis', 'Systembeteiligungsvertrag']),

('DE', 'textiles', 'Herkunftsland-Kennzeichnung', 'Angabe des Herkunftslandes bei Textilimporten (Made in).', 'Die Angabe des Herkunftslandes ist fuer Textilien in der EU nicht generell verpflichtend, aber wenn eine Herkunftsangabe gemacht wird, muss sie wahrheitsgemaess sein. Fuer Importe aus Drittlaendern gelten die Ursprungsregeln des Zollkodex. Irrefuehrende Herkunftsangaben sind nach dem UWG verboten und koennen zu Abmahnungen fuehren. Viele Haendler und Verbraucher erwarten die Made-in-Angabe.', false, 'Kennzeichnung & Verpackung', 'low', false, 5, 'UWG §5, Zollkodex (EU) Nr. 952/2013', 'Zollbehoerden, Wettbewerbszentrale', 'Abmahnrisiko, Bussgeld wegen irrefuehrender Werbung', ARRAY['Herkunftsland nur angeben wenn gesichert nachweisbar', 'Ursprungsregeln des Zollkodex beachten', 'Irrefuehrende Angaben wie EU-Flagge bei Nicht-EU-Ware vermeiden', 'Bei Halbfertigwaren das Land der wesentlichen Verarbeitung angeben'], '[{"title": "UWG", "url": "https://www.gesetze-im-internet.de/uwg_2004/"}, {"title": "Zollkodex", "url": "https://eur-lex.europa.eu/eli/reg/2013/952"}]'::jsonb, ARRAY['T-Shirt', 'Hemd', 'Jeans', 'Kleid', 'Schuhe', 'Jacke', 'Pullover'], ARRAY['Ursprungszeugnis', 'Lieferkettennachweis']),

('DE', 'textiles', 'DPP-Vorbereitung fuer Textilien', 'Vorbereitung auf den digitalen Produktpass fuer Textilprodukte.', 'Der digitale Produktpass wird fuer Textilien voraussichtlich ab 2027 verpflichtend. Er muss Informationen zur Faserzusammensetzung, Herkunft der Rohstoffe, Umweltauswirkungen, Recyclingfaehigkeit und Pflegehinweisen enthalten. Die fruehzeitige digitale Erfassung aller relevanten Produktdaten und der Aufbau einer lueckenlosen Lieferkettendokumentation sind entscheidend.', false, 'Digitaler Produktpass', 'medium', false, 6, 'ESPR (EU) 2024/1781', 'Europaeische Kommission', 'Ab Deadline Marktzugangsbeschraenkung', ARRAY['Faserzusammensetzung digital dokumentieren', 'Lieferkette vom Rohstoff bis zum Fertigprodukt erfassen', 'CO2-Fussabdruck pro Produkt berechnen', 'Recyclingfaehigkeit bewerten und dokumentieren', 'QR-Code-Infrastruktur vorbereiten'], '[{"title": "ESPR", "url": "https://eur-lex.europa.eu/eli/reg/2024/1781"}]'::jsonb, ARRAY['T-Shirt', 'Hemd', 'Jeans', 'Kleid', 'Jacke', 'Pullover', 'Schuhe', 'Bettwaesche'], ARRAY['DPP-Datentemplate', 'Lieferkettendokumentation', 'CO2-Bilanz']),

('DE', 'textiles', 'Sicherheit von Kindertextilien', 'Besondere Sicherheitsanforderungen fuer Kinderbekleidung einhalten.', 'Kinderbekleidung unterliegt strengeren Sicherheitsanforderungen. Kordelzuege an Kapuzen sind bei Kleinkindern verboten, fuer aeltere Kinder stark eingeschraenkt (EN 14682). Verschluckbare Kleinteile wie Knoepfe und Applikationen muessen die Zuganforderungen der EN 71-1 erfuellen. Flammhemmende Ausruestungen duerfen keine schädlichen Chemikalien enthalten. Die Nickelfreisetzung bei metallischen Verschluessen muss unter dem Grenzwert liegen.', true, 'Produktsicherheit & CE-Konformitaet', 'critical', true, 7, 'EN 14682 (Kordeln), EN 71-1, GPSR (EU) 2023/988', 'Bundesanstalt fuer Arbeitsschutz und Arbeitsmedizin (BAuA)', 'Bussgeld bis zu 100.000 EUR, Produktrueckruf', ARRAY['EN 14682 fuer Kordeln und Zugbaender anwenden', 'Zugkraft von Knoepfen und Applikationen pruefen', 'Flammhemmende Chemikalien auf Unbedenklichkeit pruefen', 'Altersgruppe klar kennzeichnen'], '[{"title": "GPSR", "url": "https://eur-lex.europa.eu/eli/reg/2023/988"}, {"title": "BAuA Kinderbekleidung", "url": "https://www.baua.de/DE/Themen/Anwendungssichere-Chemikalien-und-Produkte/Produktsicherheit"}]'::jsonb, ARRAY['Kinderbekleidung', 'Babybekleidung', 'Schlafanzug', 'Jacke', 'Muetze', 'Handschuhe'], ARRAY['EN 14682 Pruefbericht', 'Zugkraftpruefung', 'Schadstoffpruefung']),

('DE', 'textiles', 'CSRD/Nachhaltigkeitsberichterstattung Textil', 'Nachhaltigkeitsberichtspflicht fuer Textilunternehmen einhalten.', 'Die CSRD und die EU-Textilstrategie fordern umfassende Transparenz in der Textillieferkette. Grosse Unternehmen muessen ueber Umweltauswirkungen, Wasserverbrauch, Chemikalieneinsatz und soziale Aspekte berichten. Die European Sustainability Reporting Standards (ESRS) geben den Rahmen vor. Fuer Textilien sind insbesondere die sektorspezifischen Standards zu Wasserverbrauch und Mikroplastik relevant.', false, 'Soziale Verantwortung & Lieferkette', 'medium', true, 8, 'CSRD (EU) 2022/2464, EU-Textilstrategie', 'BaFin, Europaeische Kommission', 'Bussgeld bei Verletzung der Berichtspflicht', ARRAY['Wasserverbrauch in der Lieferkette erfassen', 'Chemikalieneinsatz dokumentieren', 'Sozialaudits bei Zulieferern durchfuehren', 'Mikroplastik-Emissionen bei Synthetik bewerten'], '[{"title": "CSRD", "url": "https://eur-lex.europa.eu/eli/dir/2022/2464"}, {"title": "EU-Textilstrategie", "url": "https://ec.europa.eu/environment/strategy/textiles-strategy_de"}]'::jsonb, ARRAY['T-Shirt', 'Hemd', 'Jeans', 'Kleid', 'Jacke', 'Pullover', 'Schuhe'], ARRAY['Nachhaltigkeitsbericht', 'Sozialaudit-Berichte', 'Wasserverbrauchsdaten']),

('DE', 'textiles', 'Lieferkettensorgfaltspflichten (LkSG) fuer Textilien', 'Menschenrechts- und Umwelt-Sorgfaltspflichten in der Textillieferkette.', 'Die Textilindustrie steht besonders im Fokus des LkSG aufgrund bekannter Risiken wie Kinderarbeit, ungerechte Loehne und Umweltverschmutzung bei der Faserproduktion und Faerberei. Unternehmen muessen Risiken in ihren Lieferketten identifizieren, priorisieren und Praeventionsmassnahmen ergreifen. Die Sorgfaltspflichten erstrecken sich auf unmittelbare Zulieferer und bei substantiierten Hinweisen auch auf mittelbare Zulieferer.', true, 'Soziale Verantwortung & Lieferkette', 'high', true, 9, 'LkSG, CSDDD (EU) 2024/1760', 'Bundesamt fuer Wirtschaft und Ausfuhrkontrolle (BAFA)', 'Bussgeld bis zu 2% des weltweiten Jahresumsatzes', ARRAY['Risikoanalyse fuer Baumwoll- und Faserlieferanten', 'Sozialaudits bei Konfektionsbetrieben durchfuehren', 'Existenzsichernde Loehne bei Zulieferern einfordern', 'Umweltstandards fuer Faerbereien pruefen', 'Beschwerdemechanismus einrichten'], '[{"title": "LkSG", "url": "https://www.gesetze-im-internet.de/lksg/"}, {"title": "BAFA Textil", "url": "https://www.bafa.de/DE/Lieferketten/lieferketten_node.html"}]'::jsonb, ARRAY['T-Shirt', 'Hemd', 'Jeans', 'Kleid', 'Jacke', 'Pullover', 'Schuhe', 'Bettwaesche'], ARRAY['Risikoanalyse', 'Sozialaudit-Berichte', 'LkSG-Jahresbericht']),

('DE', 'textiles', 'Groessenkennzeichnung', 'Korrekte Groessenangaben nach Norm anbringen.', 'Groessenangaben sind in Deutschland nicht gesetzlich vorgeschrieben, aber ueblich und kundenrelevant. Es wird empfohlen, europaeische Konfektionsgroessen oder koerpermassbezogene Angaben nach EN 13402 zu verwenden. Falsche oder irrefuehrende Groessenangaben koennen zu erhoehten Retourenquoten fuehren und stellen ein Wettbewerbsrecht-Risiko dar.', false, 'Kennzeichnung & Verpackung', 'low', false, 10, 'EN 13402 (Groessenbezeichnung), UWG', 'Keine spezifische Behoerde', 'Keine direkten Bussgelder, Abmahnrisiko', ARRAY['EN 13402 Koerpermasse-System verwenden', 'Groessentabelle mit Koerpermassen bereitstellen', 'Internationale Groessenumrechnung anbieten', 'Konsistente Groessensysteme ueber alle Produkte'], '[{"title": "EN 13402", "url": "https://www.iso.org/standard/60408.html"}]'::jsonb, ARRAY['T-Shirt', 'Hemd', 'Bluse', 'Jeans', 'Kleid', 'Jacke', 'Schuhe'], ARRAY['Groessentabelle']),

('DE', 'textiles', 'Schadstoffpruefung nach OEKO-TEX oder vergleichbar', 'Schadstoffpruefung der Textilien durch unabhaengige Pruefstelle.', 'Obwohl nicht gesetzlich vorgeschrieben, ist eine Schadstoffpruefung nach OEKO-TEX Standard 100 oder vergleichbaren Standards wie GOTS, bluesign oder EU Ecolabel branchenueblich und wird von vielen Haendlern verlangt. Die Pruefung umfasst pH-Wert, Farbechtheit, Schwermetalle, Pestizide, Formaldehyd und weitere Parameter. Fuer Babytextilien gelten die strengsten Grenzwerte.', false, 'Chemikalien & Inhaltsstoffe', 'medium', true, 11, 'REACH (EG) Nr. 1907/2006, OEKO-TEX Standard 100', 'Keine gesetzliche Pflicht (Marktanforderung)', 'Keine direkten Bussgelder, aber Marktzugangsprobleme', ARRAY['OEKO-TEX Standard 100 Zertifizierung erwaegen', 'Pruefung durch Hohenstein oder andere akkreditierte Institute', 'Fuer Babytextilien Produktklasse I waehlen', 'Zertifikat jaehrlich erneuern lassen'], '[{"title": "OEKO-TEX", "url": "https://www.oeko-tex.com/de/"}, {"title": "GOTS", "url": "https://global-standard.org/"}]'::jsonb, ARRAY['T-Shirt', 'Hemd', 'Babybekleidung', 'Unterwaesche', 'Bettwaesche', 'Handtuch', 'Socken'], ARRAY['OEKO-TEX Zertifikat', 'Schadstoffpruefbericht']),

('DE', 'textiles', 'Produktsicherheit und GPSR fuer Textilien', 'Allgemeine Produktsicherheit gemaess neuer EU-Produktsicherheitsverordnung.', 'Die neue GPSR ersetzt seit Dezember 2024 die alte Produktsicherheitsrichtlinie und stellt erweiterte Anforderungen an alle Konsumprodukte, einschliesslich Textilien. Hersteller muessen eine interne Risikoanalyse durchfuehren, technische Dokumentation erstellen und einen verantwortlichen Wirtschaftsakteur in der EU benennen. Bei Online-Verkauf gelten zusaetzliche Informationspflichten.', true, 'Produktsicherheit & CE-Konformitaet', 'high', true, 12, 'Verordnung (EU) 2023/988 (GPSR)', 'Bundesanstalt fuer Arbeitsschutz und Arbeitsmedizin (BAuA)', 'Bussgeld bis zu 100.000 EUR', ARRAY['Interne Risikoanalyse dokumentieren', 'Verantwortlichen Wirtschaftsakteur in der EU benennen', 'Bei Online-Verkauf erweiterte Produktinformationen bereitstellen', 'Safety Gate / RAPEX-Meldungen verfolgen'], '[{"title": "GPSR", "url": "https://eur-lex.europa.eu/eli/reg/2023/988"}]'::jsonb, ARRAY['T-Shirt', 'Hemd', 'Kleid', 'Jacke', 'Schuhe', 'Babybekleidung', 'Sportbekleidung'], ARRAY['Risikoanalyse', 'Technische Dokumentation']);

-- ============================================
-- DEUTSCHLAND (DE) - BATTERIEN
-- ============================================
INSERT INTO checklist_templates (country_code, category_key, title, description, detailed_description, mandatory, category, priority, document_required, sort_order, legal_basis, authority, penalties, tips, links, applicable_products, document_types) VALUES

('DE', 'batteries', 'EU-Batterieverordnung Konformitaet', 'Vollstaendige Einhaltung der neuen EU-Batterieverordnung sicherstellen.', 'Die EU-Batterieverordnung 2023/1542 ersetzt die alte Batterierichtlinie und stellt umfassende Anforderungen an alle Batterietypen. Sie umfasst Leistungs- und Haltbarkeitsanforderungen, Kennzeichnungspflichten, Sorgfaltspflichten in der Lieferkette und den digitalen Batteriepass. Die Anforderungen werden stufenweise eingefuehrt, beginnend ab Februar 2024 mit Kennzeichnungspflichten.', true, 'Produktsicherheit & CE-Konformitaet', 'critical', true, 1, 'EU-Batterieverordnung (EU) 2023/1542', 'Umweltbundesamt (UBA)', 'Bussgeld bis zu 100.000 EUR', ARRAY['Stufenplan fuer die verschiedenen Deadlines erstellen', 'CO2-Fussabdruck-Berechnung nach delegierten Rechtsakten vorbereiten', 'Recyclatanteil-Nachweise aufbauen', 'Digitalen Batteriepass-Infrastruktur vorbereiten'], '[{"title": "EU-Batterieverordnung", "url": "https://eur-lex.europa.eu/eli/reg/2023/1542"}, {"title": "UBA Batterien", "url": "https://www.umweltbundesamt.de/themen/abfall-ressourcen/produktverantwortung-in-der-abfallwirtschaft/batterien"}]'::jsonb, ARRAY['Li-Ion 18650', 'Li-Ion 21700', 'EV-Batteriepack', 'E-Bike-Akku', 'Smartphone-Akku', 'Laptop-Akku', 'Starterbatterie 12V', 'Solarspeicher'], ARRAY['EU-Konformitaetserklaerung', 'Technische Dokumentation', 'CO2-Fussabdruck-Bericht']),

('DE', 'batteries', 'Digitaler Batteriepass', 'QR-Code mit digitalem Batteriepass ab Februar 2027 bereitstellen.', 'Ab dem 18. Februar 2027 muessen Industriebatterien und EV-Batterien ueber einen digitalen Batteriepass verfuegen. Dieser muss ueber einen QR-Code und Datentraeger abrufbar sein und Informationen zu Kapazitaet, Leistung, Lebensdauer, Materialzusammensetzung, CO2-Fussabdruck und Recyclingfaehigkeit enthalten. Der Batteriepass muss ueber den gesamten Lebenszyklus aktuell gehalten werden.', true, 'Digitaler Produktpass', 'critical', true, 2, 'EU-Batterieverordnung (EU) 2023/1542, Art. 77', 'Europaeische Kommission, Umweltbundesamt (UBA)', 'Vertriebsverbot ab Deadline, Bussgeld bis zu 100.000 EUR', ARRAY['QR-Code-System fruehzeitig implementieren', 'Datenerhebung fuer alle Pflichtfelder aufbauen', 'Schnittstellen zu Batterie-Management-System einrichten', 'Lebenszyklus-Tracking implementieren', 'Datenaustauschformat nach EU-Standards verwenden'], '[{"title": "EU-Batterieverordnung Art. 77", "url": "https://eur-lex.europa.eu/eli/reg/2023/1542"}, {"title": "Battery Pass Konsortium", "url": "https://thebatterypass.eu/"}]'::jsonb, ARRAY['EV-Batteriepack', 'E-Bike-Akku', 'E-Scooter-Akku', 'Solarspeicher', 'Heimspeicher', 'USV-Batterie', 'Traktionsbatterie'], ARRAY['Digitaler Batteriepass', 'QR-Code-Nachweis', 'Datenformat-Spezifikation']),

('DE', 'batteries', 'Batterie-Kennzeichnung und Etikettierung', 'Korrekte Kennzeichnung aller Batterien mit vorgeschriebenen Symbolen.', 'Alle Batterien muessen mit dem Symbol der durchgestrichenen Muelltonne, der Kapazitaetsangabe, dem chemischen System und dem CE-Zeichen gekennzeichnet sein. Ab 2025 wird ein QR-Code fuer den Zugang zum digitalen Batteriepass erforderlich. Die Symbole muessen dauerhaft, lesbar und unverwischbar auf der Batterie oder ihrer Verpackung angebracht sein.', true, 'Kennzeichnung & Verpackung', 'critical', false, 3, 'EU-Batterieverordnung (EU) 2023/1542, Art. 13', 'Umweltbundesamt (UBA)', 'Bussgeld bis zu 100.000 EUR, Vertriebsverbot', ARRAY['Durchgestrichene Muelltonne mindestens 3% der groessten Flaeche', 'Kapazitaet in Ah oder Wh angeben', 'Chemisches System (z.B. Li-ion, NiMH) kennzeichnen', 'QR-Code ab 2025 vorbereiten'], '[{"title": "EU-Batterieverordnung Kennzeichnung", "url": "https://eur-lex.europa.eu/eli/reg/2023/1542"}]'::jsonb, ARRAY['AA-Batterie (Mignon)', 'AAA-Batterie (Micro)', 'CR2032', 'Li-Ion 18650', 'EV-Batteriepack', 'E-Bike-Akku', 'Starterbatterie 12V', 'NiMH AA'], ARRAY['Kennzeichnungsmuster', 'Etikettenspezifikation']),

('DE', 'batteries', 'CO2-Fussabdruck-Deklaration', 'CO2-Fussabdruck der Batterie berechnen und deklarieren.', 'Ab Juli 2025 muessen EV-Batterien und Industriebatterien eine CO2-Fussabdruck-Deklaration tragen. Die Berechnung muss den gesamten Lebenszyklus umfassen: Rohstoffgewinnung, Produktion, Transport und End-of-Life. Die Methodik wird durch delegierte Rechtsakte der EU festgelegt. Ab 2028 gelten maximale CO2-Fussabdruck-Schwellenwerte.', true, 'Umwelt & Nachhaltigkeit', 'high', true, 4, 'EU-Batterieverordnung (EU) 2023/1542, Art. 7', 'Umweltbundesamt (UBA), Europaeische Kommission', 'Vertriebsverbot bei Nichteinhaltung der Schwellenwerte', ARRAY['Lebenszyklusanalyse (LCA) nach ISO 14040/44 durchfuehren', 'Lieferantendaten fuer CO2-Berechnung erheben', 'Delegierte Rechtsakte zur Berechnungsmethodik verfolgen', 'CO2-Klasse A-E vorbereiten'], '[{"title": "EU-Batterieverordnung CO2", "url": "https://eur-lex.europa.eu/eli/reg/2023/1542"}]'::jsonb, ARRAY['EV-Batteriepack', 'E-Bike-Akku', 'Traktionsbatterie', 'Solarspeicher', 'Heimspeicher', 'Li-Ion 18650', 'Li-Ion 21700'], ARRAY['CO2-Fussabdruck-Deklaration', 'LCA-Bericht', 'Lieferantendaten']),

('DE', 'batteries', 'Recyclatanteil-Nachweis', 'Mindestrecyclatanteil in Batterien nachweisen.', 'Die EU-Batterieverordnung schreibt ab 2031 Mindestrecyclatanteile vor: 16% Kobalt, 6% Lithium und 6% Nickel. Ab 2036 steigen die Werte auf 26% Kobalt, 12% Lithium und 15% Nickel. Hersteller muessen den Recyclatanteil nachweisen und dokumentieren. Die Nachweiskette muss lueckenlos sein und von Dritten ueberpruefbar.', true, 'Umwelt & Nachhaltigkeit', 'medium', true, 5, 'EU-Batterieverordnung (EU) 2023/1542, Art. 8', 'Umweltbundesamt (UBA)', 'Vertriebsverbot ab Deadline', ARRAY['Recyclatanteil-Tracking in der Lieferkette aufbauen', 'Zertifizierte Recycling-Lieferanten identifizieren', 'Nachweissystem fuer Sekundaerrohstoffe implementieren', 'Deadlines 2031 und 2036 im Blick behalten'], '[{"title": "EU-Batterieverordnung Recyclatanteil", "url": "https://eur-lex.europa.eu/eli/reg/2023/1542"}]'::jsonb, ARRAY['EV-Batteriepack', 'E-Bike-Akku', 'Traktionsbatterie', 'Li-Ion 18650', 'Li-Ion 21700', 'Solarspeicher'], ARRAY['Recyclatanteil-Nachweis', 'Lieferkettennachweis']),

('DE', 'batteries', 'Ruecknahme- und Sammelpflichten', 'Ruecknahmesystem fuer Altbatterien einrichten.', 'Hersteller und Importeure von Batterien sind verpflichtet, ein Ruecknahmesystem fuer Altbatterien zu betreiben oder sich an einem zugelassenen System zu beteiligen. Die Sammelquoten steigen auf 63% bis 2027 und 73% bis 2030 fuer Geraetebatterien. Fuer Batterien aus Elektrofahrzeugen gilt eine Sammelquote von 100%. Das Batterie-Gesetz regelt die nationale Umsetzung.', true, 'Umwelt & Nachhaltigkeit', 'high', true, 6, 'EU-Batterieverordnung (EU) 2023/1542, BattG', 'Umweltbundesamt (UBA), Stiftung GRS', 'Bussgeld bis zu 100.000 EUR', ARRAY['An zugelassenem Ruecknahmesystem (GRS, REBAT) teilnehmen', 'Sammelstellen fuer Endkunden bereitstellen', 'Sammelquoten dokumentieren und melden', 'Kunden ueber Rueckgabemoeglichkeiten informieren'], '[{"title": "BattG", "url": "https://www.gesetze-im-internet.de/battg/"}, {"title": "GRS Batterien", "url": "https://www.grs-batterien.de"}]'::jsonb, ARRAY['AA-Batterie (Mignon)', 'AAA-Batterie (Micro)', 'CR2032', 'Li-Ion 18650', 'Smartphone-Akku', 'Laptop-Akku', 'E-Bike-Akku'], ARRAY['Ruecknahme-Systemvertrag', 'Sammelquoten-Nachweis']),

('DE', 'batteries', 'Sorgfaltspflichten Lieferkette Rohstoffe', 'Due-Diligence-Pflichten fuer Batterierohstoffe einhalten.', 'Die EU-Batterieverordnung fuehrt Sorgfaltspflichten fuer die Beschaffung von Kobalt, Lithium, Nickel, Naturgraphit und Mangan ein. Wirtschaftsakteure muessen Risiken in ihrer Lieferkette identifizieren und minimieren. Dies umfasst Menschenrechtsrisiken, Umweltauswirkungen und Governance-Aspekte im Rohstoffabbau.', true, 'Soziale Verantwortung & Lieferkette', 'high', true, 7, 'EU-Batterieverordnung (EU) 2023/1542, Art. 48-52, CSDDD (EU) 2024/1760', 'Bundesamt fuer Wirtschaft und Ausfuhrkontrolle (BAFA)', 'Bussgeld bis zu 2% des Jahresumsatzes', ARRAY['Due-Diligence-System nach OECD-Leitlinien aufbauen', 'Kobalt-Lieferkette besonders gruendlich pruefen', 'Unabhaengige Audits bei Rohstofflieferanten durchfuehren', 'Risikobewertung jaehrlich aktualisieren'], '[{"title": "EU-Batterieverordnung Due Diligence", "url": "https://eur-lex.europa.eu/eli/reg/2023/1542"}, {"title": "OECD Due Diligence", "url": "https://www.oecd.org/corporate/mne/mining.htm"}]'::jsonb, ARRAY['EV-Batteriepack', 'E-Bike-Akku', 'Li-Ion 18650', 'Li-Ion 21700', 'Traktionsbatterie', 'Solarspeicher'], ARRAY['Due-Diligence-Bericht', 'Lieferkettenaudits', 'Risikoanalyse']),

('DE', 'batteries', 'Gefahrgut-Transport (ADR)', 'Einhaltung der Gefahrgutvorschriften fuer den Batterietransport.', 'Lithiumbatterien gelten als Gefahrgut der Klasse 9 und unterliegen den ADR-Vorschriften fuer den Strassentransport sowie den IATA-DGR fuer den Lufttransport. Die Anforderungen umfassen spezielle Verpackungen, Kennzeichnung, Dokumentation und Schulung des Personals. UN 3480 (Li-Ion), UN 3481 (Li-Ion mit Geraet), UN 3090 (Li-Metall) und UN 3091 (Li-Metall mit Geraet) sind die relevanten UN-Nummern.', true, 'Dokumentation & Rueckverfolgbarkeit', 'high', true, 8, 'ADR (Accord europeen relatif au transport international des marchandises dangereuses)', 'Bundesanstalt fuer Materialforschung und -pruefung (BAM)', 'Bussgeld bis zu 50.000 EUR, Transportverbot', ARRAY['UN-Nummer korrekt zuordnen (3480, 3481, 3090, 3091)', 'Lithiumgehalt und Nennenergie fuer Einstufung bestimmen', 'Zugelassene Verpackungen verwenden', 'UN 38.3 Tests nachweisen', 'Personal in Gefahrguthandling schulen'], '[{"title": "ADR Gefahrgut", "url": "https://unece.org/transportdangerous-goods/adr-2023"}, {"title": "BAM Gefahrgut", "url": "https://www.bam.de/Navigation/DE/Themen/Gefahrgut/gefahrgut.html"}]'::jsonb, ARRAY['Li-Ion 18650', 'Li-Ion 21700', 'LiPo-Akkus', 'EV-Batteriepack', 'E-Bike-Akku', 'Powerbank', 'Smartphone-Akku', 'Laptop-Akku'], ARRAY['UN 38.3 Pruefbericht', 'Gefahrgutbegleitpapiere', 'Verpackungszulassung']),

('DE', 'batteries', 'Leistungs- und Haltbarkeitsanforderungen', 'Mindestanforderungen an Leistung und Haltbarkeit einhalten.', 'Die EU-Batterieverordnung definiert Mindestanforderungen an die elektrochemische Leistung und Haltbarkeit von Batterien. Fuer tragbare Batterien gelten Anforderungen an die Mindestlebensdauer, fuer EV-Batterien an die Kapazitaetserhaltung ueber definierte Zyklen. Die spezifischen Werte werden durch delegierte Rechtsakte festgelegt.', true, 'Produktsicherheit & CE-Konformitaet', 'medium', true, 9, 'EU-Batterieverordnung (EU) 2023/1542, Art. 9-10', 'Umweltbundesamt (UBA)', 'Bussgeld bis zu 100.000 EUR', ARRAY['Leistungstests nach harmonisierten Normen durchfuehren', 'Kapazitaetserhaltung ueber Zyklen dokumentieren', 'Delegierte Rechtsakte zu Mindeststandards verfolgen', 'Qualitaetsmanagementsystem implementieren'], '[{"title": "EU-Batterieverordnung Leistung", "url": "https://eur-lex.europa.eu/eli/reg/2023/1542"}]'::jsonb, ARRAY['Li-Ion 18650', 'Li-Ion 21700', 'EV-Batteriepack', 'E-Bike-Akku', 'Smartphone-Akku', 'Laptop-Akku'], ARRAY['Leistungspruefbericht', 'Zyklentest-Dokumentation']),

('DE', 'batteries', 'REACH-Konformitaet fuer Batteriechemikalien', 'REACH-konforme Chemikalien in der Batterieproduktion verwenden.', 'Batterien enthalten zahlreiche Chemikalien, die unter REACH reguliert sind. Insbesondere Kobaltverbindungen, Nickelverbindungen und bestimmte Elektrolyte stehen auf der SVHC-Kandidatenliste. Hersteller muessen sicherstellen, dass alle verwendeten Chemikalien registriert sind und die Beschraenkungen des Anhangs XVII eingehalten werden.', true, 'Chemikalien & Inhaltsstoffe', 'high', true, 10, 'REACH (EG) Nr. 1907/2006', 'ECHA, BAuA', 'Bussgeld bis zu 50.000 EUR', ARRAY['SVHC-Screening fuer alle Batteriekomponenten', 'Sicherheitsdatenblaetter fuer Elektrolyte bereithalten', 'SCIP-Meldung fuer SVHC-haltige Batterien', 'Substitution von SVHC-Stoffen pruefen'], '[{"title": "REACH", "url": "https://eur-lex.europa.eu/eli/reg/2006/1907"}, {"title": "ECHA Batterien", "url": "https://echa.europa.eu/"}]'::jsonb, ARRAY['Li-Ion 18650', 'Li-Ion 21700', 'EV-Batteriepack', 'NiMH AA', 'Starterbatterie 12V'], ARRAY['REACH-Konformitaetsnachweis', 'SVHC-Pruefbericht', 'Sicherheitsdatenblaetter']);

-- ============================================
-- DEUTSCHLAND (DE) - VERPACKUNGEN
-- ============================================
INSERT INTO checklist_templates (country_code, category_key, title, description, detailed_description, mandatory, category, priority, document_required, sort_order, legal_basis, authority, penalties, tips, links, applicable_products, document_types) VALUES

('DE', 'packaging', 'LUCID-Registrierung Verpackungsregister', 'Pflichtregistrierung im Verpackungsregister LUCID vor dem Inverkehrbringen.', 'Jeder Erstinverkehrbringer von Verpackungen muss sich im Verpackungsregister LUCID registrieren. Die Registrierung ist Voraussetzung fuer den Vertrieb verpackter Waren in Deutschland. Sie umfasst die Angabe aller Verpackungsmaterialien und -mengen. Die Registrierungsnummer muss oeffentlich einsehbar sein und wird von der Zentralen Stelle Verpackungsregister ueberwacht.', true, 'Dokumentation & Rueckverfolgbarkeit', 'critical', true, 1, 'Verpackungsgesetz (VerpackG) §9', 'Zentrale Stelle Verpackungsregister (ZSVR)', 'Bussgeld bis zu 200.000 EUR, sofortiges Vertriebsverbot', ARRAY['Registrierung vor dem ersten Inverkehrbringen abschliessen', 'Alle Verpackungsmaterialien korrekt klassifizieren', 'Registrierungsnummer im Online-Shop angeben', 'Jaehrliche Datenmeldung fristgerecht einreichen'], '[{"title": "LUCID Verpackungsregister", "url": "https://lucid.verpackungsregister.org/"}, {"title": "VerpackG", "url": "https://www.gesetze-im-internet.de/verpackg/"}]'::jsonb, ARRAY['Wellpappkarton', 'Faltschachtel', 'PET-Flasche', 'Kunststofftüte', 'Glasflasche', 'Getränkedose', 'Getränkekarton (Tetra Pak)', 'Standbodenbeutel'], ARRAY['LUCID-Registrierungsnachweis', 'Mengenmeldungen']),

('DE', 'packaging', 'Systembeteiligung duales System', 'Beteiligung an einem dualen System (z.B. Der Gruene Punkt, Interseroh).', 'Parallel zur LUCID-Registrierung muss eine Systembeteiligung bei einem zugelassenen dualen System abgeschlossen werden. Die Lizenzierung erfolgt nach Materialart und Menge. Verpackungsmengen muessen bei LUCID und dem dualen System uebereinstimmen. Die Vollstaendigkeitserklaerung ist jaehrlich durch einen Wirtschaftspruefer zu pruefen ab bestimmten Mengenschwellen.', true, 'Umwelt & Nachhaltigkeit', 'critical', true, 2, 'Verpackungsgesetz (VerpackG) §7', 'Zentrale Stelle Verpackungsregister (ZSVR)', 'Bussgeld bis zu 200.000 EUR, Vertriebsverbot', ARRAY['Vertrag mit dualem System vor dem Inverkehrbringen abschliessen', 'Mengenmeldungen mit LUCID abstimmen', 'Vollstaendigkeitserklaerung ab 80.000 kg Glas oder 50.000 kg andere Materialien', 'Verpackungsmengen genau erfassen und melden'], '[{"title": "LUCID", "url": "https://lucid.verpackungsregister.org/"}, {"title": "VerpackG", "url": "https://www.gesetze-im-internet.de/verpackg/"}]'::jsonb, ARRAY['Wellpappkarton', 'Faltschachtel', 'PET-Flasche', 'Kunststofftüte', 'Glasflasche', 'Getränkedose'], ARRAY['Systembeteiligungsvertrag', 'Mengenmeldungen', 'Vollstaendigkeitserklaerung']),

('DE', 'packaging', 'Recyclingfaehigkeit sicherstellen', 'Design-for-Recycling Anforderungen fuer Verpackungen umsetzen.', 'Die kommende PPWR der EU wird verbindliche Anforderungen an die Recyclingfaehigkeit von Verpackungen stellen. Bereits jetzt sollten Verpackungen moeglichst monomaterial gestaltet, auf schwer recycelbare Verbundmaterialien verzichtet und die Trennbarkeit der Komponenten sichergestellt werden. Der Mindeststandard der ZSVR definiert bereits heute die Recyclingfaehigkeit fuer die Systembeteiligung.', true, 'Umwelt & Nachhaltigkeit', 'high', true, 3, 'PPWR (EU), VerpackG, ZSVR-Mindeststandard', 'Zentrale Stelle Verpackungsregister (ZSVR)', 'Kuenftig Vertriebsverbot bei mangelnder Recyclingfaehigkeit', ARRAY['Monomaterial-Verpackungen bevorzugen', 'Auf Carbon-Black-Pigmente verzichten (nicht detektierbar)', 'Etiketten leicht abloesbar gestalten', 'Mindeststandard der ZSVR beachten', 'Recyclingfaehigkeit nach Mindeststandard bewerten lassen'], '[{"title": "ZSVR Mindeststandard", "url": "https://www.verpackungsregister.org/informationen-orientierung/mindeststandard"}, {"title": "PPWR", "url": "https://eur-lex.europa.eu/legal-content/DE/TXT/?uri=CELEX:52022PC0677"}]'::jsonb, ARRAY['Wellpappkarton', 'PET-Flasche', 'HDPE-Flasche', 'Standbodenbeutel', 'Kunststofftüte', 'Faltschachtel'], ARRAY['Recyclingfaehigkeits-Gutachten', 'Materialanalyse']),

('DE', 'packaging', 'Verpackungskennzeichnung und Entsorgungshinweise', 'Korrekte Materialcodierung und Entsorgungshinweise auf Verpackungen.', 'Verpackungen sollten mit den Materialidentifikationscodes gemaess Entscheidung 97/129/EG gekennzeichnet werden. Die Recycling-Codes (z.B. PAP 21, PP 05) helfen Verbrauchern bei der korrekten Trennung. Entsorgungshinweise in Text oder Piktogramm sind empfohlen. Die kuenftige PPWR wird einheitliche EU-weite Kennzeichnungen einfuehren.', true, 'Kennzeichnung & Verpackung', 'medium', false, 4, 'Entscheidung 97/129/EG, PPWR (EU)', 'Zentrale Stelle Verpackungsregister (ZSVR)', 'Keine direkten Bussgelder fuer fehlende Codes, aber PPWR wird dies aendern', ARRAY['Materialidentifikationscodes nach 97/129/EG anbringen', 'Entsorgungshinweise fuer Verbraucher aufdrucken', 'Trennhinweise bei Verbundverpackungen', 'PPWR-konforme Kennzeichnung fruehzeitig planen'], '[{"title": "Entscheidung 97/129/EG", "url": "https://eur-lex.europa.eu/eli/dec/1997/129"}, {"title": "PPWR", "url": "https://eur-lex.europa.eu/legal-content/DE/TXT/?uri=CELEX:52022PC0677"}]'::jsonb, ARRAY['Wellpappkarton', 'PET-Flasche', 'Glasflasche', 'Getränkedose', 'Standbodenbeutel', 'Kunststofftüte', 'Faltschachtel'], ARRAY['Kennzeichnungsvorlage']),

('DE', 'packaging', 'Einweg-Kunststoffverpackungen Reduktion', 'Anforderungen der Einwegkunststoffrichtlinie umsetzen.', 'Die Einwegkunststoffrichtlinie verbietet bestimmte Einwegkunststoffprodukte und stellt Anforderungen an andere. Fuer Getraenkeverpackungen gilt ab 2025 die Pflicht zu befestigten Deckeln (Tethered Caps). Die Sammelquote fuer Einweg-Getraenkeflaschen muss bis 2029 auf 90% steigen. Zudem gelten Mindestrecyclatanteile fuer PET-Flaschen.', true, 'Umwelt & Nachhaltigkeit', 'high', true, 5, 'Richtlinie (EU) 2019/904 (Einwegkunststoffe), VerpackG', 'Umweltbundesamt (UBA)', 'Bussgeld bis zu 100.000 EUR', ARRAY['Verbotene Einwegkunststoffprodukte identifizieren', 'Tethered Caps ab 2025 implementieren', 'Recyclatanteil fuer PET-Flaschen: 25% ab 2025, 30% ab 2030', 'Alternativen zu Einwegkunststoff evaluieren'], '[{"title": "Einwegkunststoff-Richtlinie", "url": "https://eur-lex.europa.eu/eli/dir/2019/904"}, {"title": "UBA Einwegkunststoffe", "url": "https://www.umweltbundesamt.de/themen/abfall-ressourcen/produktverantwortung-in-der-abfallwirtschaft/einwegkunststoffe"}]'::jsonb, ARRAY['PET-Flasche', 'PP-Becher', 'PS-Becher', 'Kunststofftüte', 'Stretchfolie', 'Blisterverpackung'], ARRAY['Recyclatanteil-Nachweis', 'Materialanalyse']),

('DE', 'packaging', 'Lebensmittelkontaktmaterialien', 'Anforderungen an Verpackungen mit Lebensmittelkontakt einhalten.', 'Verpackungen, die mit Lebensmitteln in Kontakt kommen, muessen die EU-Rahmenverordnung und nationale Regelungen einhalten. Sie duerfen keine Bestandteile in gesundheitsschaedlichen Mengen an das Lebensmittel abgeben. Fuer Kunststoffe gilt die EU-Verordnung 10/2011 mit spezifischen Migrationslimits. Fuer Druckfarben auf der Aussenseite gilt die Schweizer Druckfarbenverordnung als Referenz.', true, 'Chemikalien & Inhaltsstoffe', 'high', true, 6, 'Verordnung (EG) Nr. 1935/2004, Verordnung (EU) Nr. 10/2011', 'Bundesamt fuer Verbraucherschutz und Lebensmittelsicherheit (BVL)', 'Bussgeld bis zu 50.000 EUR, Produktrueckruf', ARRAY['Migrationspruefungen nach EU 10/2011 durchfuehren', 'Konformitaetserklaerung fuer Lebensmittelkontaktmaterialien erstellen', 'Druckfarben auf der Innenseite vermeiden', 'BfR-Empfehlungen fuer nicht harmonisierte Materialien beachten'], '[{"title": "FCM-Rahmenverordnung", "url": "https://eur-lex.europa.eu/eli/reg/2004/1935"}, {"title": "Kunststoff-Verordnung", "url": "https://eur-lex.europa.eu/eli/reg/2011/10"}]'::jsonb, ARRAY['PET-Flasche', 'PP-Becher', 'PET-Schale', 'Tiefziehschale', 'Konservenglas', 'Getränkekarton (Tetra Pak)', 'Alufolie'], ARRAY['Migrationstest-Bericht', 'FCM-Konformitaetserklaerung']),

('DE', 'packaging', 'REACH und Schwermetalle in Verpackungen', 'Einhaltung der Schwermetall-Grenzwerte in Verpackungsmaterialien.', 'Die Verpackungsrichtlinie 94/62/EG begrenzt die Gesamtkonzentration von Blei, Cadmium, Quecksilber und sechswertigem Chrom in Verpackungen auf maximal 100 ppm. Zusaetzlich gelten die REACH-Beschraenkungen. Insbesondere bei Druckfarben, Pigmenten und Stabilisatoren in Kunststoffverpackungen ist Vorsicht geboten.', true, 'Chemikalien & Inhaltsstoffe', 'medium', true, 7, 'Verpackungsrichtlinie 94/62/EG Art. 11, REACH (EG) Nr. 1907/2006', 'Zentrale Stelle Verpackungsregister (ZSVR)', 'Bussgeld bis zu 50.000 EUR', ARRAY['Schwermetallanalyse durchfuehren (Summe < 100 ppm)', 'Lieferantenerklaerungen einholen', 'Druckfarben auf Schwermetallfreiheit pruefen', 'Regelmäßige Stichprobenanalysen einplanen'], '[{"title": "Verpackungsrichtlinie", "url": "https://eur-lex.europa.eu/eli/dir/1994/62"}, {"title": "REACH", "url": "https://eur-lex.europa.eu/eli/reg/2006/1907"}]'::jsonb, ARRAY['PET-Flasche', 'Glasflasche', 'Getränkedose', 'Faltschachtel', 'Kunststofftüte', 'Blisterverpackung'], ARRAY['Schwermetallanalyse', 'Lieferantenerklaerung']),

('DE', 'packaging', 'DPP-Vorbereitung Verpackungen', 'Vorbereitung auf den digitalen Produktpass fuer Verpackungen.', 'Die ESPR und die PPWR werden auch fuer Verpackungen einen digitalen Produktpass erfordern. Dieser soll Materialzusammensetzung, Recyclingfaehigkeit, Recyclatanteil und Entsorgungshinweise digital zugaenglich machen. Die fruehzeitige digitale Erfassung aller Verpackungsdaten erleichtert die spaetere Umsetzung.', false, 'Digitaler Produktpass', 'medium', false, 8, 'ESPR (EU) 2024/1781, PPWR (EU)', 'Europaeische Kommission', 'Ab Deadline Marktzugangsbeschraenkung', ARRAY['Materialzusammensetzung aller Verpackungen digital erfassen', 'Recyclingfaehigkeit bewerten', 'Recyclatanteil dokumentieren', 'QR-Code auf Verpackung vorbereiten'], '[{"title": "ESPR", "url": "https://eur-lex.europa.eu/eli/reg/2024/1781"}, {"title": "PPWR", "url": "https://eur-lex.europa.eu/legal-content/DE/TXT/?uri=CELEX:52022PC0677"}]'::jsonb, ARRAY['Wellpappkarton', 'PET-Flasche', 'Glasflasche', 'Standbodenbeutel', 'Faltschachtel'], ARRAY['DPP-Datentemplate', 'Materialdeklaration']),

('DE', 'packaging', 'Pfandpflicht Einweggetraenkeverpackungen', 'Einhaltung der Pfandpflicht fuer Einweggetraenkeverpackungen.', 'In Deutschland gilt seit 2022 eine erweiterte Pfandpflicht fuer nahezu alle Einweggetraenkeverpackungen aus Kunststoff und Metall, einschliesslich Milchgetraenke. Der Pfandbetrag betraegt 25 Cent. Erstinverkehrbringer muessen sich bei einem Pfandclearing-System registrieren und die Ruecknahme sicherstellen.', true, 'Umwelt & Nachhaltigkeit', 'high', true, 9, 'VerpackG §31', 'Zentrale Stelle Verpackungsregister (ZSVR)', 'Bussgeld bis zu 200.000 EUR', ARRAY['Pfandpflichtigkeit aller Getraenkeverpackungen pruefen', 'Registrierung bei DPG (Deutsche Pfandsystem GmbH)', 'DPG-Logo auf Verpackung anbringen', 'EAN-Code bei DPG registrieren'], '[{"title": "DPG Pfandsystem", "url": "https://www.dpg-pfandsystem.de"}, {"title": "VerpackG", "url": "https://www.gesetze-im-internet.de/verpackg/"}]'::jsonb, ARRAY['PET-Flasche', 'Getränkedose', 'HDPE-Flasche'], ARRAY['DPG-Registrierung', 'Pfandlogo-Nachweis']),

('DE', 'packaging', 'Transportverpackungen und Produktsicherheit', 'Transportverpackungen muessen Produkt ausreichend schuetzen.', 'Transportverpackungen muessen sicherstellen, dass das verpackte Produkt waehrend Transport und Lagerung vor Beschaedigung geschuetzt ist. Fuer Gefahrgut gelten besondere Verpackungsanforderungen. Die ISTA-Standards bieten anerkannte Testverfahren fuer Transportverpackungen. Bei Maengelruegen wegen Transportschaeden drohen Gewaehrleistungsansprueche.', false, 'Produktsicherheit & CE-Konformitaet', 'low', false, 10, 'Handelsgesetzbuch (HGB), ADR, ISTA-Standards', 'Keine spezifische Behoerde', 'Keine Bussgelder, aber Gewaehrleistungsrisiken', ARRAY['ISTA-Tests fuer Transportverpackung durchfuehren', 'Verpackungsdesign an Logistikkette anpassen', 'Falltest und Vibrationstest durchfuehren', 'Gefahrgutverpackung separat pruefen'], '[{"title": "ISTA Standards", "url": "https://www.ista.org/"}]'::jsonb, ARRAY['Wellpappkarton', 'Versandkarton', 'Holzkiste', 'Holzpalette', 'Europalette', 'Schaumstoffverpackung'], ARRAY['ISTA-Testbericht', 'Transportschadenprotokoll']);

-- ============================================
-- DEUTSCHLAND (DE) - MOEBEL
-- ============================================
INSERT INTO checklist_templates (country_code, category_key, title, description, detailed_description, mandatory, category, priority, document_required, sort_order, legal_basis, authority, penalties, tips, links, applicable_products, document_types) VALUES

('DE', 'furniture', 'Formaldehyd-Emissionsgrenzwerte', 'Einhaltung der Formaldehyd-Emissionsgrenzwerte fuer Holzwerkstoffe.', 'In Deutschland gilt die Chemikalien-Verbotsverordnung mit strengen Grenzwerten fuer Formaldehyd-Emissionen aus Holzwerkstoffen. Holzbasierte Moebel duerfen maximal 0,1 ppm Formaldehyd emittieren (E1-Standard). Spanplatten, MDF und Sperrholz muessen geprueft werden. Der Grenzwert bezieht sich auf die Ausgleichskonzentration in der Pruefkammer nach EN 717-1.', true, 'Chemikalien & Inhaltsstoffe', 'critical', true, 1, 'ChemVerbotsV, REACH (EG) Nr. 1907/2006 Anhang XVII', 'Bundesanstalt fuer Arbeitsschutz und Arbeitsmedizin (BAuA)', 'Bussgeld bis zu 50.000 EUR, Verkaufsverbot', ARRAY['E1-Klassifizierung fuer alle Holzwerkstoffe sicherstellen', 'Pruefberichte nach EN 717-1 von Lieferanten anfordern', 'Bei CARB-2 Export auch US-Anforderungen beachten', 'Lacke und Klebstoffe auf Formaldehyd-Abgabe pruefen'], '[{"title": "ChemVerbotsV", "url": "https://www.gesetze-im-internet.de/chemverbotsv_2017/"}, {"title": "REACH Anhang XVII", "url": "https://eur-lex.europa.eu/eli/reg/2006/1907"}]'::jsonb, ARRAY['Kleiderschrank', 'Kommode', 'Bücherregal', 'Esstisch', 'Schreibtisch', 'Kinderbett', 'Kuechenschrank', 'Spanplatte', 'MDF-Platte'], ARRAY['Formaldehyd-Emissionspruefung', 'E1-Zertifikat', 'Materialanalyse']),

('DE', 'furniture', 'Standsicherheit und mechanische Sicherheit', 'Standsicherheits- und Kipptests fuer Moebel durchfuehren.', 'Moebel muessen mechanisch sicher sein und duerfen keine Kippgefahr darstellen, insbesondere Regale, Kommoden und Schraenke. Die EN 14749 definiert Anforderungen an Aufbewahrungsmoebel, die EN 12520 an Sitzmoebel. Kindermoebelmuessen zusaetzlich die EN 716 (Kinderbetten) und EN 14988 (Hochstuehle) erfuellen. Eine Wandbefestigung muss mitgeliefert werden, wenn Kippgefahr besteht.', true, 'Produktsicherheit & CE-Konformitaet', 'critical', true, 2, 'Verordnung (EU) 2023/988 (GPSR), EN 14749, EN 12520', 'Bundesanstalt fuer Arbeitsschutz und Arbeitsmedizin (BAuA)', 'Bussgeld bis zu 100.000 EUR, Produktrueckruf', ARRAY['Kipptest nach EN 14749 durchfuehren', 'Wandbefestigungsset beilegen und in Anleitung beschreiben', 'Belastungstests fuer Regalboeden und Schubladen', 'Scharfe Kanten und Quetschstellen eliminieren'], '[{"title": "GPSR", "url": "https://eur-lex.europa.eu/eli/reg/2023/988"}, {"title": "BAuA Moebelsicherheit", "url": "https://www.baua.de"}]'::jsonb, ARRAY['Kleiderschrank', 'Bücherregal', 'Kommode', 'Esstisch', 'Kinderbett', 'Hochstuhl', 'TV-Board', 'Standregal'], ARRAY['Standsicherheitspruefung', 'Belastungstest', 'Risikoanalyse']),

('DE', 'furniture', 'REACH-Konformitaet Moebeloberflaechen', 'REACH-konforme Lacke, Beizen und Oberflaechen fuer Moebel.', 'Moebeloberflaechen koennen SVHC-Stoffe enthalten, insbesondere in Lacken, Beizen, Klebstoffen und Flammschutzmitteln in Polstermaterialien. Die REACH-Verordnung schreibt die Kommunikation von SVHC-Stoffen ueber 0,1% vor. Fuer Kindermobel gelten zusaetzlich die strengeren Grenzwerte der Spielzeugrichtlinie fuer migrierbare Elemente.', true, 'Chemikalien & Inhaltsstoffe', 'high', true, 3, 'REACH (EG) Nr. 1907/2006', 'BAuA, ECHA', 'Bussgeld bis zu 50.000 EUR', ARRAY['SVHC-Screening fuer Lacke und Klebstoffe', 'Flammschutzmittel in Polsterungen pruefen', 'SCIP-Datenbank-Meldung durchfuehren', 'Loesemittelarme Lacke bevorzugen'], '[{"title": "REACH", "url": "https://eur-lex.europa.eu/eli/reg/2006/1907"}, {"title": "ECHA SCIP", "url": "https://echa.europa.eu/scip"}]'::jsonb, ARRAY['Esstisch', 'Kleiderschrank', 'Kommode', 'Bücherregal', 'Kinderbett', 'Kinderschreibtisch', 'Sofa', 'Sessel'], ARRAY['SVHC-Pruefbericht', 'SCIP-Meldung', 'Materialsicherheitsdatenblaetter']),

('DE', 'furniture', 'Holz aus nachhaltiger Forstwirtschaft', 'Nachweis der legalen und nachhaltigen Holzherkunft.', 'Die EU-Holzhandelsverordnung (EUTR) verbietet das Inverkehrbringen von illegal geschlagenem Holz. Seit Juni 2023 gilt die erweiterte EU-Entwaldungsverordnung (EUDR), die auch Entwaldungsfreiheit verlangt. Moebelhersteller muessen die Herkunft ihrer Hoelzer lueckenlos nachweisen. FSC- oder PEFC-Zertifizierung erleichtert den Nachweis, ist aber nicht ausreichend.', true, 'Umwelt & Nachhaltigkeit', 'high', true, 4, 'EUDR (EU) 2023/1115, EUTR (EU) 995/2010', 'Bundesanstalt fuer Landwirtschaft und Ernaehrung (BLE)', 'Bussgeld bis zu 50.000 EUR', ARRAY['Sorgfaltspflicht-System nach EUDR aufbauen', 'Geolokalisierung der Holzherkunft dokumentieren', 'FSC- oder PEFC-Zertifizierung erwaegen', 'Lieferantenaudits durchfuehren', 'Due-Diligence-Erklaerung vor Import abgeben'], '[{"title": "EUDR", "url": "https://eur-lex.europa.eu/eli/reg/2023/1115"}, {"title": "BLE Holzhandel", "url": "https://www.ble.de/DE/Themen/Wald-Holz/Holzhandel/holzhandel_node.html"}]'::jsonb, ARRAY['Esstisch', 'Kleiderschrank', 'Bücherregal', 'Kommode', 'Kinderbett', 'Schreibtisch', 'Gartenbank'], ARRAY['EUDR-Sorgfaltserklaerung', 'FSC/PEFC-Zertifikat', 'Herkunftsnachweis']),

('DE', 'furniture', 'Brandschutz Polstermoebel', 'Brandschutzanforderungen fuer Polstermoebel und Matratzen beachten.', 'In Deutschland gibt es keine spezifische Verordnung fuer den Brandschutz von Wohnmoebeln im privaten Bereich, anders als in Grossbritannien. Fuer Moebel im oeffentlichen Bereich (Hotels, Bueros) gelten jedoch Brandschutzanforderungen. Hersteller sollten dennoch die EN 1021-1 und EN 1021-2 (Zigaretten- und Streichholztest) als Mindeststandard beachten.', false, 'Produktsicherheit & CE-Konformitaet', 'medium', true, 5, 'Bauordnungsrecht, EN 1021, GPSR (EU) 2023/988', 'Landesbehoerden (Bauordnungsaemter)', 'Abhaengig von Landesrecht und Verwendungszweck', ARRAY['EN 1021-1/2 Brandtest fuer Polstermoebel durchfuehren', 'Flammschutzmittel ohne SVHC-Stoffe verwenden', 'Bei Objektmoebeln hoeheren Brandschutz beachten', 'Matratzen nach EN 597 pruefen'], '[{"title": "GPSR", "url": "https://eur-lex.europa.eu/eli/reg/2023/988"}]'::jsonb, ARRAY['Sofa', 'Sessel', 'Federkernmatratze', 'Kaltschaummatratze', 'Buerstuhl', 'Polsterbett'], ARRAY['Brandpruefbericht', 'Materialzertifikat']),

('DE', 'furniture', 'Aufbauanleitung und Sicherheitshinweise', 'Vollstaendige Aufbauanleitung mit Sicherheitshinweisen bereitstellen.', 'Jedes Moebelstueck muss mit einer verstaendlichen Aufbauanleitung und Sicherheitshinweisen in deutscher Sprache geliefert werden. Die Anleitung muss Warnungen vor Kippgefahr, Hinweise zur Wandbefestigung und maximal zulaessige Belastungen enthalten. Bei Kindermoebeln muessen zusaetzliche Sicherheitshinweise gegeben werden.', true, 'Dokumentation & Rueckverfolgbarkeit', 'medium', false, 6, 'GPSR (EU) 2023/988, ProdSG', 'BAuA', 'Bussgeld bis zu 25.000 EUR', ARRAY['Anleitung in klarem Deutsch mit Bildern', 'Warnhinweise zu Kippgefahr hervorheben', 'Maximale Belastungsangaben machen', 'Kontaktdaten des Herstellers angeben'], '[{"title": "GPSR", "url": "https://eur-lex.europa.eu/eli/reg/2023/988"}]'::jsonb, ARRAY['Kleiderschrank', 'Bücherregal', 'Esstisch', 'Schreibtisch', 'Kinderbett', 'Kommode'], ARRAY['Aufbauanleitung (DE)', 'Sicherheitshinweise']),

('DE', 'furniture', 'DPP-Vorbereitung Moebel', 'Vorbereitung auf den digitalen Produktpass fuer Moebel.', 'Moebel gehoeren zu den Produktgruppen, fuer die ein DPP unter der ESPR eingefuehrt wird. Der DPP muss Materialzusammensetzung, Holzherkunft, Recyclingfaehigkeit, Reparierbarkeit und Umweltauswirkungen enthalten. Die fruehzeitige digitale Dokumentation verschafft Marktvorteile.', false, 'Digitaler Produktpass', 'medium', false, 7, 'ESPR (EU) 2024/1781', 'Europaeische Kommission', 'Ab Deadline Marktzugangsbeschraenkung', ARRAY['Materialzusammensetzung digital erfassen', 'Holzherkunft dokumentieren', 'Reparierbarkeit bewerten', 'Recyclingfaehigkeit der Materialien analysieren'], '[{"title": "ESPR", "url": "https://eur-lex.europa.eu/eli/reg/2024/1781"}]'::jsonb, ARRAY['Esstisch', 'Kleiderschrank', 'Sofa', 'Bücherregal', 'Schreibtisch', 'Kinderbett'], ARRAY['DPP-Datentemplate', 'Materialdeklaration']),

('DE', 'furniture', 'Verpackungsentsorgung Moebel', 'Verpackungspflichten fuer Moebel-Verpackungen einhalten.', 'Moebelverpackungen aus Karton, Styropor, Folien und Holz unterliegen dem Verpackungsgesetz. Die Registrierung bei LUCID und die Systembeteiligung sind Pflicht. Grossformatige Verpackungen erfordern besondere Aufmerksamkeit bei der Mengenmeldung.', true, 'Kennzeichnung & Verpackung', 'high', true, 8, 'Verpackungsgesetz (VerpackG)', 'Zentrale Stelle Verpackungsregister (ZSVR)', 'Bussgeld bis zu 200.000 EUR', ARRAY['Alle Verpackungskomponenten erfassen', 'Styropor und EPS separat melden', 'LUCID-Registrierung aktuell halten', 'Verpackungsmenge reduzieren'], '[{"title": "LUCID", "url": "https://lucid.verpackungsregister.org/"}]'::jsonb, ARRAY['Kleiderschrank', 'Esstisch', 'Sofa', 'Bücherregal', 'Kinderbett', 'Matratze'], ARRAY['LUCID-Registrierung', 'Mengenmeldungen']),

('DE', 'furniture', 'Lieferkettensorgfaltspflichten Moebel', 'LkSG-Pflichten fuer die Moebellieferkette einhalten.', 'Die Moebelindustrie hat komplexe Lieferketten mit Risiken bei der Holzgewinnung, Textilproduktion fuer Polsterstoffe und Metallverarbeitung. Das LkSG verpflichtet zur Risikoanalyse und Praevention. Besonders relevant sind Entwaldung, Kinderarbeit in der Forstwirtschaft und Arbeitsbedingungen in der Lederverarbeitung.', true, 'Soziale Verantwortung & Lieferkette', 'medium', true, 9, 'LkSG, CSDDD (EU) 2024/1760', 'BAFA', 'Bussgeld bis zu 2% des Jahresumsatzes', ARRAY['Risikoanalyse fuer Holz- und Textillieferanten', 'Entwaldungsrisiken bewerten', 'Sozialaudits durchfuehren', 'Beschwerdemechanismus einrichten'], '[{"title": "LkSG", "url": "https://www.gesetze-im-internet.de/lksg/"}, {"title": "BAFA", "url": "https://www.bafa.de/DE/Lieferketten/lieferketten_node.html"}]'::jsonb, ARRAY['Esstisch', 'Sofa', 'Kleiderschrank', 'Bücherregal', 'Kinderbett'], ARRAY['Risikoanalyse', 'Sozialaudit-Berichte', 'LkSG-Bericht']),

('DE', 'furniture', 'GPSR Produktsicherheit Moebel', 'Allgemeine Produktsicherheit nach GPSR fuer Moebel einhalten.', 'Die neue GPSR stellt erweiterte Anforderungen an alle Konsumprodukte einschliesslich Moebel. Hersteller muessen eine Risikoanalyse durchfuehren, technische Unterlagen erstellen und einen verantwortlichen Wirtschaftsakteur in der EU benennen. Bei Unfaellen oder Maengeln besteht eine Meldepflicht ueber das Safety Gate.', true, 'Produktsicherheit & CE-Konformitaet', 'high', true, 10, 'GPSR (EU) 2023/988', 'BAuA', 'Bussgeld bis zu 100.000 EUR', ARRAY['Risikoanalyse dokumentieren', 'Safety-Gate-Meldepflicht beachten', 'Verantwortlichen in EU benennen', 'Rueckrufprozesse definieren'], '[{"title": "GPSR", "url": "https://eur-lex.europa.eu/eli/reg/2023/988"}]'::jsonb, ARRAY['Sofa', 'Esstisch', 'Kleiderschrank', 'Kinderbett', 'Hochstuhl', 'Bürostuhl'], ARRAY['Risikoanalyse', 'Technische Dokumentation']);

-- ============================================
-- DEUTSCHLAND (DE) - KOSMETIK
-- ============================================
INSERT INTO checklist_templates (country_code, category_key, title, description, detailed_description, mandatory, category, priority, document_required, sort_order, legal_basis, authority, penalties, tips, links, applicable_products, document_types) VALUES

('DE', 'cosmetics', 'CPNP-Notifizierung', 'Notifizierung im Cosmetic Products Notification Portal vor dem Inverkehrbringen.', 'Jedes kosmetische Produkt muss vor dem Inverkehrbringen im CPNP der Europaeischen Kommission notifiziert werden. Die Notifizierung umfasst Produktkategorie, Zusammensetzung (Rahmenrezeptur), Kennzeichnung und die verantwortliche Person. Die Notifizierung muss bei Formulierungsaenderungen aktualisiert werden.', true, 'Dokumentation & Rueckverfolgbarkeit', 'critical', true, 1, 'Kosmetikverordnung (EG) Nr. 1223/2009, Art. 13', 'Bundesamt fuer Verbraucherschutz und Lebensmittelsicherheit (BVL)', 'Bussgeld bis zu 50.000 EUR, Vertriebsverbot', ARRAY['CPNP-Zugang beantragen und verantwortliche Person benennen', 'Rahmenrezeptur korrekt und vollstaendig eingeben', 'Kennzeichnungsbild (Label) hochladen', 'Bei jeder Formulierungsaenderung aktualisieren', 'Notifizierung mindestens 24h vor Inverkehrbringen'], '[{"title": "CPNP Portal", "url": "https://ec.europa.eu/growth/tools-databases/cosing/cpnp/"}, {"title": "Kosmetikverordnung", "url": "https://eur-lex.europa.eu/eli/reg/2009/1223"}]'::jsonb, ARRAY['Tagescreme', 'Shampoo', 'Duschgel', 'Lippenstift', 'Foundation', 'Sonnencreme', 'Zahnpasta', 'Eau de Parfum', 'Deodorant', 'Nagellack'], ARRAY['CPNP-Notifizierung', 'Rahmenrezeptur']),

('DE', 'cosmetics', 'Sicherheitsbewertung durch Sicherheitsbewerter', 'Sicherheitsbewertung durch qualifizierten Sicherheitsbewerter erstellen lassen.', 'Jedes kosmetische Produkt muss einer Sicherheitsbewertung durch eine qualifizierte Person unterzogen werden. Der Sicherheitsbewerter muss ein Diplom in Pharmazie, Medizin, Toxikologie oder vergleichbar haben. Die Bewertung umfasst alle Inhaltsstoffe, deren Konzentration, die vorhersehbare Exposition und die Gesamtsicherheit des Produkts. Der Sicherheitsbericht (Teil A und B) muss vor dem Inverkehrbringen vorliegen.', true, 'Produktsicherheit & CE-Konformitaet', 'critical', true, 2, 'Kosmetikverordnung (EG) Nr. 1223/2009, Art. 10, Anhang I', 'Bundesamt fuer Verbraucherschutz und Lebensmittelsicherheit (BVL)', 'Bussgeld bis zu 50.000 EUR, Vertriebsverbot', ARRAY['Qualifizierten Sicherheitsbewerter beauftragen', 'Alle Rohstoff-Spezifikationen bereithalten', 'Stabilitaetsdaten und mikrobiologische Tests vorlegen', 'PAO (Period After Opening) festlegen lassen', 'Bewertung bei Formulierungsaenderungen aktualisieren'], '[{"title": "Kosmetikverordnung Anhang I", "url": "https://eur-lex.europa.eu/eli/reg/2009/1223"}]'::jsonb, ARRAY['Tagescreme', 'Shampoo', 'Duschgel', 'Sonnencreme', 'Foundation', 'Lippenstift', 'Zahnpasta', 'Haarfarbe', 'Nagellack'], ARRAY['Sicherheitsbericht (Teil A und B)', 'Stabilitaetsdaten', 'Mikrobiologische Tests']),

('DE', 'cosmetics', 'INCI-Deklaration und Kennzeichnung', 'Vollstaendige Kennzeichnung nach Kosmetikverordnung anbringen.', 'Die Kennzeichnung muss den Namen der verantwortlichen Person, das Nennvolumen, das Mindesthaltbarkeitsdatum oder PAO, Verwendungszweck, INCI-Liste, Chargennummer und Herkunftsland (bei Import aus Drittlaendern) enthalten. Die INCI-Deklaration muss alle Inhaltsstoffe in absteigender Reihenfolge der Konzentration auflisten, wobei Stoffe unter 1% in beliebiger Reihenfolge stehen duerfen.', true, 'Kennzeichnung & Verpackung', 'critical', false, 3, 'Kosmetikverordnung (EG) Nr. 1223/2009, Art. 19', 'BVL', 'Bussgeld bis zu 50.000 EUR', ARRAY['INCI-Namen nach CosIng-Datenbank verwenden', 'Absteigend nach Konzentration sortieren', 'Nanomaterialien mit [nano] kennzeichnen', 'Allergene Duftstoffe einzeln auflisten', 'Mindesthaltbarkeit oder PAO-Symbol anbringen'], '[{"title": "CosIng Datenbank", "url": "https://ec.europa.eu/growth/tools-databases/cosing/"}, {"title": "Kosmetikverordnung", "url": "https://eur-lex.europa.eu/eli/reg/2009/1223"}]'::jsonb, ARRAY['Tagescreme', 'Shampoo', 'Duschgel', 'Sonnencreme', 'Lippenstift', 'Foundation', 'Parfum', 'Zahnpasta'], ARRAY['INCI-Deklaration', 'Kennzeichnungsentwurf']),

('DE', 'cosmetics', 'Verbotene und beschraenkte Inhaltsstoffe', 'Einhaltung der Verbots- und Beschraenkungslisten der Kosmetikverordnung.', 'Die Kosmetikverordnung enthaelt Listen verbotener Stoffe (Anhang II, ueber 1.600 Eintraege), beschraenkter Stoffe (Anhang III), zugelassener Farbstoffe (Anhang IV), Konservierungsstoffe (Anhang V) und UV-Filter (Anhang VI). Die Listen werden regelmaessig aktualisiert. Besondere Aufmerksamkeit gilt CMR-Stoffen, Nanomaterialien und endokrinen Disruptoren.', true, 'Chemikalien & Inhaltsstoffe', 'critical', true, 4, 'Kosmetikverordnung (EG) Nr. 1223/2009, Anhaenge II-VI', 'BVL, Bundesinstitut fuer Risikobewertung (BfR)', 'Bussgeld bis zu 50.000 EUR, Produktrueckruf', ARRAY['Alle Inhaltsstoffe gegen Anhaenge II-VI pruefen', 'Beschraenkte Stoffe nur in zulaessiger Konzentration verwenden', 'Updates der Anhaenge verfolgen', 'Nanomaterialien melden und kennzeichnen'], '[{"title": "Kosmetikverordnung Anhaenge", "url": "https://eur-lex.europa.eu/eli/reg/2009/1223"}, {"title": "BfR Kosmetik", "url": "https://www.bfr.bund.de/de/kosmetische_mittel-54773.html"}]'::jsonb, ARRAY['Tagescreme', 'Shampoo', 'Haarfarbe', 'Sonnencreme', 'Nagellack', 'Parfum', 'Zahnpasta', 'Deodorant'], ARRAY['Pruefbericht Inhaltsstoffe', 'Spezifikationen Rohstoffe']),

('DE', 'cosmetics', 'Produktinformationsdossier (PID)', 'Vollstaendiges Produktinformationsdossier zusammenstellen und bereithalten.', 'Fuer jedes kosmetische Produkt muss ein Produktinformationsdossier (PID) erstellt und 10 Jahre nach dem letzten Inverkehrbringen aufbewahrt werden. Das PID muss die Produktbeschreibung, den Sicherheitsbericht, die Herstellungsmethode, Nachweise der Wirksamkeit, Tierversuchsdaten und den GMP-Nachweis enthalten.', true, 'Dokumentation & Rueckverfolgbarkeit', 'high', true, 5, 'Kosmetikverordnung (EG) Nr. 1223/2009, Art. 11', 'BVL', 'Bussgeld bis zu 50.000 EUR', ARRAY['PID-Ordner systematisch aufbauen', 'GMP-Nachweis nach ISO 22716 einschliessen', 'Wirksamkeitsnachweise fuer Claims', '10 Jahre Aufbewahrungspflicht beachten'], '[{"title": "Kosmetikverordnung Art. 11", "url": "https://eur-lex.europa.eu/eli/reg/2009/1223"}, {"title": "ISO 22716 GMP", "url": "https://www.iso.org/standard/36437.html"}]'::jsonb, ARRAY['Tagescreme', 'Shampoo', 'Sonnencreme', 'Foundation', 'Parfum', 'Zahnpasta', 'Haarfarbe'], ARRAY['Produktinformationsdossier', 'GMP-Zertifikat', 'Sicherheitsbericht']),

('DE', 'cosmetics', 'GMP-Herstellung ISO 22716', 'Herstellung nach Good Manufacturing Practice ISO 22716.', 'Die Kosmetikverordnung schreibt die Herstellung nach GMP vor. Die ISO 22716 definiert die Leitlinien fuer die Herstellung, Kontrolle, Lagerung und den Versand kosmetischer Produkte. Sie umfasst Personalqualifikation, Raeumlichkeiten, Ausruestung, Rohstoffe, Produktion, Qualitaetskontrolle und Dokumentation.', true, 'Produktsicherheit & CE-Konformitaet', 'high', true, 6, 'Kosmetikverordnung (EG) Nr. 1223/2009, Art. 8', 'BVL', 'Bussgeld bis zu 50.000 EUR', ARRAY['ISO 22716 Zertifizierung anstreben', 'Regelmaessige interne Audits durchfuehren', 'Chargenrueckverfolgbarkeit sicherstellen', 'Reinigungs- und Hygieneplaene erstellen'], '[{"title": "ISO 22716", "url": "https://www.iso.org/standard/36437.html"}, {"title": "Kosmetikverordnung Art. 8", "url": "https://eur-lex.europa.eu/eli/reg/2009/1223"}]'::jsonb, ARRAY['Tagescreme', 'Shampoo', 'Duschgel', 'Sonnencreme', 'Lippenstift', 'Foundation', 'Parfum'], ARRAY['ISO 22716 Zertifikat', 'GMP-Auditbericht']),

('DE', 'cosmetics', 'Sonnenschutz-Claims und LSF-Pruefung', 'Korrekte Auslobung des Lichtschutzfaktors und UV-Schutz-Claims.', 'Sonnenschutzprodukte muessen den LSF nach ISO 24444 (in-vivo) oder gleichwertigen Methoden bestimmen. UV-A-Schutz muss nach ISO 24443 nachgewiesen werden. Die EU-Empfehlung 2006/647/EG legt fest, dass die UV-A-Schutzleistung mindestens ein Drittel des LSF betragen muss. Claims wie wasserfest muessen durch standardisierte Tests belegt sein.', true, 'Produktsicherheit & CE-Konformitaet', 'high', true, 7, 'Kosmetikverordnung (EG) Nr. 1223/2009, EU-Empfehlung 2006/647/EG', 'BVL, BfR', 'Bussgeld wegen irrefuehrender Claims, Vertriebsverbot', ARRAY['LSF nach ISO 24444 bestimmen lassen', 'UV-A-Schutz nach ISO 24443 nachweisen', 'UV-A-Logo nur bei Einhaltung des 1/3-Kriteriums verwenden', 'Wasserfest-Claim durch Standardtest belegen'], '[{"title": "EU-Empfehlung Sonnenschutz", "url": "https://eur-lex.europa.eu/eli/reco/2006/647"}]'::jsonb, ARRAY['Sonnencreme (LSF 30)', 'Sonnencreme (LSF 50)', 'Sonnenspray', 'Sonnenmilch', 'After-Sun-Lotion', 'Tagescreme'], ARRAY['LSF-Pruefbericht', 'UV-A-Testbericht', 'Wasserfest-Test']),

('DE', 'cosmetics', 'Tierversuchsverbot beachten', 'Keine an Tieren getesteten Produkte oder Inhaltsstoffe verwenden.', 'Die EU verbietet seit 2013 Tierversuche fuer kosmetische Produkte und deren Inhaltsstoffe vollstaendig. Dies gilt sowohl fuer in der EU hergestellte als auch importierte Produkte. Der Nachweis der Tierversuchsfreiheit ist Teil des PID. Auch Inhaltsstoffe, die unter REACH getestet wurden, koennen problematisch sein.', true, 'Chemikalien & Inhaltsstoffe', 'high', false, 8, 'Kosmetikverordnung (EG) Nr. 1223/2009, Art. 18', 'BVL', 'Bussgeld bis zu 50.000 EUR, Vertriebsverbot', ARRAY['Lieferantenbestaetigungen zur Tierversuchsfreiheit einholen', 'Alternative Testmethoden verwenden', 'Drittlandsrecht bei Import beachten', 'REACH-Testdaten separat betrachten'], '[{"title": "Kosmetikverordnung Art. 18", "url": "https://eur-lex.europa.eu/eli/reg/2009/1223"}]'::jsonb, ARRAY['Tagescreme', 'Shampoo', 'Duschgel', 'Sonnencreme', 'Lippenstift', 'Foundation', 'Parfum', 'Zahnpasta'], ARRAY['Tierversuchsfreiheits-Erklaerung']),

('DE', 'cosmetics', 'Verpackung und LUCID fuer Kosmetik', 'Verpackungspflichten fuer Kosmetikverpackungen einhalten.', 'Kosmetikverpackungen unterliegen dem Verpackungsgesetz. Besonders bei Kosmetik sind haeufig Mehrkomponentenverpackungen (Glas, Kunststoffpumpe, Karton) zu beachten. Jede Materialfraktion muss separat gemeldet werden. Die Verpackung muss zudem die gesetzlichen Kennzeichnungsanforderungen der Kosmetikverordnung erfuellen.', true, 'Kennzeichnung & Verpackung', 'high', true, 9, 'VerpackG, Kosmetikverordnung (EG) Nr. 1223/2009', 'ZSVR, BVL', 'Bussgeld bis zu 200.000 EUR', ARRAY['Alle Verpackungskomponenten separat erfassen', 'Materialfraktionen korrekt zuordnen', 'LUCID-Registrierung aktuell halten', 'Glasverpackungen und Pumpen getrennt melden'], '[{"title": "LUCID", "url": "https://lucid.verpackungsregister.org/"}]'::jsonb, ARRAY['Tagescreme', 'Shampoo', 'Duschgel', 'Parfum', 'Sonnencreme', 'Lippenstift'], ARRAY['LUCID-Registrierung', 'Mengenmeldungen']),

('DE', 'cosmetics', 'DPP-Vorbereitung Kosmetik', 'Vorbereitung auf den digitalen Produktpass fuer Kosmetikprodukte.', 'Obwohl Kosmetik nicht zu den ersten Produktgruppen fuer den DPP gehoert, wird erwartet, dass auch diese Kategorie kuenftig einbezogen wird. Die fruehzeitige digitale Dokumentation von Inhaltsstoffen, Herkunft, Umweltauswirkungen und Verpackungsdaten bereitet auf kommende Anforderungen vor.', false, 'Digitaler Produktpass', 'low', false, 10, 'ESPR (EU) 2024/1781', 'Europaeische Kommission', 'Noch keine direkten Strafen', ARRAY['INCI-Liste digital verfuegbar machen', 'Inhaltsstoff-Herkunft dokumentieren', 'Verpackungsrecyclingfaehigkeit erfassen', 'Carbon-Footprint berechnen'], '[{"title": "ESPR", "url": "https://eur-lex.europa.eu/eli/reg/2024/1781"}]'::jsonb, ARRAY['Tagescreme', 'Shampoo', 'Duschgel', 'Sonnencreme', 'Parfum'], ARRAY['DPP-Datentemplate', 'Inhaltsstoff-Deklaration']);

-- ============================================
-- DEUTSCHLAND (DE) - SPIELZEUG
-- ============================================
INSERT INTO checklist_templates (country_code, category_key, title, description, detailed_description, mandatory, category, priority, document_required, sort_order, legal_basis, authority, penalties, tips, links, applicable_products, document_types) VALUES

('DE', 'toys', 'CE-Kennzeichnung und Spielzeugrichtlinie', 'CE-Kennzeichnung gemaess Spielzeugrichtlinie 2009/48/EG anbringen.', 'Spielzeug muss die CE-Kennzeichnung tragen, die die Konformitaet mit der Spielzeugrichtlinie bestaetigt. Die Konformitaetsbewertung umfasst die Pruefung nach EN 71 (Teile 1-13) und EN 62115 fuer elektrisches Spielzeug. Die CE-Kennzeichnung muss sichtbar, leserlich und dauerhaft auf dem Spielzeug, der Verpackung oder einem Beipackzettel angebracht sein.', true, 'Produktsicherheit & CE-Konformitaet', 'critical', true, 1, 'Spielzeugrichtlinie 2009/48/EG, GPSR (EU) 2023/988', 'Bundesanstalt fuer Arbeitsschutz und Arbeitsmedizin (BAuA)', 'Bussgeld bis zu 100.000 EUR, Produktrueckruf', ARRAY['EN 71 Pruefungen durchfuehren (mechanisch, entflammbar, chemisch)', 'EN 62115 fuer elektrisches Spielzeug beachten', 'CE-Kennzeichnung mindestens 5 mm hoch', 'Konformitaetserklaerung vor Inverkehrbringen erstellen'], '[{"title": "Spielzeugrichtlinie", "url": "https://eur-lex.europa.eu/eli/dir/2009/48"}, {"title": "BAuA Spielzeug", "url": "https://www.baua.de/DE/Themen/Anwendungssichere-Chemikalien-und-Produkte/Produktsicherheit"}]'::jsonb, ARRAY['LEGO-Set', 'Puppe', 'Plüschtier', 'Spielzeugauto', 'Brettspiel', 'Puzzle', 'Baukasten', 'Lerncomputer', 'Experimentierkasten', 'Kinderküche'], ARRAY['EU-Konformitaetserklaerung', 'EN 71 Pruefberichte', 'Technische Dokumentation']),

('DE', 'toys', 'Mechanische und physikalische Sicherheit EN 71-1', 'Mechanische und physikalische Sicherheitsanforderungen nach EN 71-1 pruefen.', 'EN 71-1 definiert Anforderungen an die mechanische und physikalische Sicherheit von Spielzeug. Dies umfasst die Pruefung auf verschluckbare Kleinteile, scharfe Kanten, Spitzen, Zugfestigkeit von Naehten bei Plueschtieren, Sicherheit von Verpackungen und Pruefung beweglicher Teile. Fuer Kinder unter 3 Jahren gelten besonders strenge Anforderungen.', true, 'Produktsicherheit & CE-Konformitaet', 'critical', true, 2, 'EN 71-1, Spielzeugrichtlinie 2009/48/EG', 'BAuA', 'Bussgeld bis zu 100.000 EUR, Produktrueckruf', ARRAY['Kleinteil-Test mit Pruefzylinder durchfuehren', 'Zugfestigkeit von Naehten und Befestigungen testen', 'Scharfe Kanten und Spitzen pruefen', 'Verpackungsfolie auf Erstickungsgefahr testen', 'Altersempfehlung korrekt angeben'], '[{"title": "EN 71-1 Sicherheit", "url": "https://eur-lex.europa.eu/eli/dir/2009/48"}]'::jsonb, ARRAY['Puppe', 'Plüschtier', 'Spielzeugauto', 'LEGO-Set', 'Puzzle', 'Greifling', 'Beissring', 'Spielfigur', 'Rassel'], ARRAY['EN 71-1 Pruefbericht']),

('DE', 'toys', 'Chemische Sicherheit EN 71-3', 'Migration bestimmter Elemente nach EN 71-3 pruefen.', 'EN 71-3 legt Grenzwerte fuer die Migration bestimmter Elemente aus Spielzeugmaterialien fest. Die Grenzwerte unterscheiden nach drei Materialkategorien: trocken/bruechig/pulverfoermig, fluessig/klebrig und abgeschabtes Material. Geprueft werden 19 Elemente, darunter Blei, Cadmium, Chrom, Barium und organische Zinnverbindungen.', true, 'Chemikalien & Inhaltsstoffe', 'critical', true, 3, 'EN 71-3, Spielzeugrichtlinie 2009/48/EG, REACH (EG) Nr. 1907/2006', 'BAuA, BfR', 'Bussgeld bis zu 100.000 EUR, Produktrueckruf', ARRAY['Migration aller 19 Elemente pruefen lassen', 'Alle drei Materialkategorien beruecksichtigen', 'Besonders bei lackiertem Spielzeug testen', 'Grenzwerte fuer Kinder unter 3 besonders streng'], '[{"title": "EN 71-3", "url": "https://eur-lex.europa.eu/eli/dir/2009/48"}, {"title": "BfR Spielzeug", "url": "https://www.bfr.bund.de"}]'::jsonb, ARRAY['Puppe', 'Spielzeugauto', 'Holzbausteine', 'Fingerfarben', 'Knetmasse', 'Spielfigur', 'Malset', 'Babypuppe'], ARRAY['EN 71-3 Pruefbericht', 'Materialanalyse']),

('DE', 'toys', 'Altersempfehlung und Warnhinweise', 'Korrekte Altersempfehlung und Warnhinweise anbringen.', 'Spielzeug muss mit einer geeigneten Altersempfehlung versehen sein. Spielzeug, das fuer Kinder unter 3 Jahren nicht geeignet ist, muss den Warnhinweis mit dem Symbol und dem Text tragen. Zusaetzlich sind nutzungsspezifische Warnhinweise erforderlich, z.B. fuer Magnete, Wasser-Spielzeug oder chemische Experimentierkästen.', true, 'Kennzeichnung & Verpackung', 'high', false, 4, 'Spielzeugrichtlinie 2009/48/EG, Anhang V', 'BAuA', 'Bussgeld bis zu 50.000 EUR', ARRAY['Altersempfehlung realistisch festlegen', 'Nicht-geeignet-unter-3 Symbol verwenden', 'Warnhinweise in deutscher Sprache', 'Nutzungsspezifische Warnungen beachten'], '[{"title": "Spielzeugrichtlinie Anhang V", "url": "https://eur-lex.europa.eu/eli/dir/2009/48"}]'::jsonb, ARRAY['LEGO-Set', 'Puppe', 'Plüschtier', 'Spielzeugauto', 'Experimentierkasten', 'Magnetbausteine', 'Wassersprinkler', 'Drohne (Kinder)'], ARRAY['Kennzeichnungsmuster', 'Warnhinweistexte']),

('DE', 'toys', 'Entflammbarkeit EN 71-2', 'Entflammbarkeitsanforderungen fuer Spielzeug nach EN 71-2 pruefen.', 'EN 71-2 stellt Anforderungen an die Entflammbarkeit von Spielzeugmaterialien. Besonders relevant fuer Verkleidungskostueme, Plueschtiere, Spielzelte und Spielzeug aus Textilien. Die Flammenausbreitungsgeschwindigkeit darf bestimmte Grenzwerte nicht ueberschreiten. Celluloidaehnliche Materialien sind bis auf wenige Ausnahmen verboten.', true, 'Produktsicherheit & CE-Konformitaet', 'high', true, 5, 'EN 71-2, Spielzeugrichtlinie 2009/48/EG', 'BAuA', 'Bussgeld bis zu 100.000 EUR, Produktrueckruf', ARRAY['Flammenausbreitungstest durchfuehren', 'Textilspielzeug besonders pruefen', 'Kein Celluloid verwenden', 'Verkleidungskostueme nach EN 14878 testen'], '[{"title": "EN 71-2", "url": "https://eur-lex.europa.eu/eli/dir/2009/48"}]'::jsonb, ARRAY['Plüschtier', 'Spielzelt', 'Puppenkleidung', 'Verkleidungskostueme', 'Schmusetuch'], ARRAY['EN 71-2 Pruefbericht']),

('DE', 'toys', 'WEEE-Registrierung fuer elektronisches Spielzeug', 'Elektronisches Spielzeug bei der Stiftung EAR registrieren.', 'Elektronisches Spielzeug faellt unter das ElektroG und muss bei der Stiftung EAR registriert werden. Dies betrifft alle batterie- oder netzbetriebenen Spielzeuge. Die WEEE-Registrierungsnummer muss auf Rechnungen angegeben werden. Zusaetzlich gelten die RoHS-Beschraenkungen.', true, 'Umwelt & Nachhaltigkeit', 'high', true, 6, 'ElektroG, WEEE 2012/19/EU, RoHS 2011/65/EU', 'Stiftung EAR', 'Bussgeld bis zu 100.000 EUR, Vertriebsverbot', ARRAY['Registrierung bei Stiftung EAR vor Inverkehrbringen', 'Geraetekategorie korrekt zuordnen', 'WEEE-Nummer auf Rechnungen angeben', 'RoHS-Konformitaet sicherstellen'], '[{"title": "Stiftung EAR", "url": "https://www.stiftung-ear.de"}, {"title": "ElektroG", "url": "https://www.gesetze-im-internet.de/elektrog_2015/"}]'::jsonb, ARRAY['Lerncomputer', 'Kinder-Tablet', 'Roboter (Spielzeug)', 'Elektronisches Haustier', 'Walkie-Talkie (Kinder)', 'Kinderkamera', 'Karaoke-Maschine', 'Tanzmatten'], ARRAY['WEEE-Registrierung', 'RoHS-Erklaerung']),

('DE', 'toys', 'REACH und SVHC fuer Spielzeug', 'Einhaltung der REACH-Anforderungen und SVHC-Kommunikation.', 'Neben den spielzeugspezifischen Grenzwerten gelten auch die REACH-Anforderungen. Besonders relevant sind Phthalat-Beschraenkungen (DEHP, DBP, BBP verboten; DINP, DIDP, DNOP in mundfaehigem Spielzeug verboten). Die SVHC-Kandidatenliste muss beachtet und die SCIP-Meldepflicht erfuellt werden.', true, 'Chemikalien & Inhaltsstoffe', 'high', true, 7, 'REACH (EG) Nr. 1907/2006, Anhang XVII Eintrag 51/52', 'BAuA, ECHA', 'Bussgeld bis zu 50.000 EUR, Produktrueckruf', ARRAY['Phthalat-Pruefung fuer alle Kunststoffteile', 'SVHC-Kandidatenliste regelmaessig pruefen', 'SCIP-Meldung durchfuehren', 'Besonders strenge Grenzwerte fuer mundgaengiges Spielzeug'], '[{"title": "REACH Eintrag 51", "url": "https://eur-lex.europa.eu/eli/reg/2006/1907"}, {"title": "ECHA SVHC", "url": "https://echa.europa.eu/candidate-list-table"}]'::jsonb, ARRAY['Puppe', 'Babypuppe', 'Beissring', 'Greifling', 'Spielfigur', 'Badespielzeug', 'Knetmasse', 'Plüschtier'], ARRAY['Phthalat-Pruefbericht', 'SVHC-Screening', 'SCIP-Meldung']),

('DE', 'toys', 'Verpackungssicherheit und LUCID', 'Sichere Verpackung und Verpackungsregistrierung fuer Spielzeug.', 'Spielzeugverpackungen muessen sicher sein (keine Erstickungsgefahr durch Folie) und dem Verpackungsgesetz entsprechen. LUCID-Registrierung und Systembeteiligung sind Pflicht. Bei Spielzeugverpackungen ist besonders darauf zu achten, dass sie kindersicher sind und keine verschluckbaren Teile enthalten.', true, 'Kennzeichnung & Verpackung', 'medium', true, 8, 'VerpackG, EN 71-1 (Verpackungssicherheit)', 'ZSVR, BAuA', 'Bussgeld bis zu 200.000 EUR', ARRAY['Folie ueber 100x100mm mit Warnhinweis versehen', 'LUCID-Registrierung aktuell halten', 'Keine verschluckbaren Verpackungsteile', 'Blisterverpackungen ohne scharfe Kanten'], '[{"title": "LUCID", "url": "https://lucid.verpackungsregister.org/"}]'::jsonb, ARRAY['LEGO-Set', 'Puppe', 'Plüschtier', 'Spielzeugauto', 'Brettspiel', 'Puzzle'], ARRAY['LUCID-Registrierung', 'Verpackungssicherheitstest']),

('DE', 'toys', 'Magnetspielzeug Sicherheitsanforderungen', 'Besondere Sicherheitsanforderungen fuer Spielzeug mit Magneten.', 'Spielzeug mit starken Magneten (Flussdichte > 50 kJ/m3) muss besondere Warnhinweise tragen und darf nicht fuer Kinder unter 3 Jahren bestimmt sein. Die verschluckbaren Magnete stellen eine ernsthafte Gesundheitsgefahr dar. Die EN 71-1 definiert spezifische Anforderungen und Warnhinweise.', true, 'Produktsicherheit & CE-Konformitaet', 'high', true, 9, 'EN 71-1, Spielzeugrichtlinie 2009/48/EG', 'BAuA', 'Bussgeld bis zu 100.000 EUR, Produktrueckruf', ARRAY['Magnetstaerke messen und dokumentieren', 'Warnhinweis gut sichtbar anbringen', 'Altersempfehlung ab 3 Jahren', 'Verschluckbarkeitstest durchfuehren'], '[{"title": "EN 71-1 Magnete", "url": "https://eur-lex.europa.eu/eli/dir/2009/48"}]'::jsonb, ARRAY['Magnetbausteine', 'Magnetische Buchstaben', 'Experimentierkasten'], ARRAY['EN 71-1 Magnettest', 'Risikoanalyse']),

('DE', 'toys', 'DPP-Vorbereitung Spielzeug', 'Vorbereitung auf den digitalen Produktpass fuer Spielzeug.', 'Spielzeug wird voraussichtlich in einer spaeteren Phase unter die DPP-Pflicht fallen. Die Vorbereitung umfasst die digitale Erfassung von Materialzusammensetzung, Sicherheitszertifizierungen, Herstellungsinformationen und Recyclingfaehigkeit. Besonders die lueckenlose Dokumentation der Lieferkette ist fuer Spielzeug relevant.', false, 'Digitaler Produktpass', 'low', false, 10, 'ESPR (EU) 2024/1781', 'Europaeische Kommission', 'Noch keine direkten Strafen', ARRAY['Materialzusammensetzung digital erfassen', 'Sicherheitszertifikate digital verwalten', 'Lieferkettendokumentation aufbauen', 'QR-Code-Infrastruktur vorbereiten'], '[{"title": "ESPR", "url": "https://eur-lex.europa.eu/eli/reg/2024/1781"}]'::jsonb, ARRAY['LEGO-Set', 'Puppe', 'Plüschtier', 'Spielzeugauto', 'Brettspiel', 'Experimentierkasten'], ARRAY['DPP-Datentemplate']);

-- ============================================
-- DEUTSCHLAND (DE) - LEBENSMITTEL
-- ============================================
INSERT INTO checklist_templates (country_code, category_key, title, description, detailed_description, mandatory, category, priority, document_required, sort_order, legal_basis, authority, penalties, tips, links, applicable_products, document_types) VALUES

('DE', 'food', 'Lebensmittelinformations-Verordnung (LMIV) Kennzeichnung', 'Vollstaendige Lebensmittelkennzeichnung gemaess LMIV.', 'Die LMIV verpflichtet zur umfassenden Kennzeichnung aller vorverpackten Lebensmittel. Die Pflichtkennzeichnung umfasst: Bezeichnung des Lebensmittels, Zutatenverzeichnis, Allergenkennzeichnung, Nettofuellmenge, Mindesthaltbarkeitsdatum, Aufbewahrungshinweise, Name und Anschrift des Lebensmittelunternehmers, Herkunftsangabe, Naehrwertdeklaration und Alkoholgehalt.', true, 'Kennzeichnung & Verpackung', 'critical', true, 1, 'LMIV (EU) Nr. 1169/2011', 'Lebensmittelueberwachung der Bundeslaender', 'Bussgeld bis zu 50.000 EUR', ARRAY['Alle 14 Hauptallergene hervorheben', 'Naehrwerttabelle im vorgeschriebenen Format', 'Mindestschriftgroesse 1,2 mm beachten', 'Bezeichnung nach Leitsaetzen des Lebensmittelbuches', 'MHD oder Verbrauchsdatum korrekt angeben'], '[{"title": "LMIV", "url": "https://eur-lex.europa.eu/eli/reg/2011/1169"}, {"title": "BVL Kennzeichnung", "url": "https://www.bvl.bund.de/DE/Arbeitsbereiche/01_Lebensmittel/01_Aufgaben/02_AmtlicheLebensmittelueberwachung/lm_amtliche_lebensmittelueberwachung_node.html"}]'::jsonb, ARRAY['Frischmilch', 'Joghurt', 'Schokolade', 'Vollkornbrot', 'Tiefkuehlpizza', 'Olivenoel', 'Kaffee', 'Chips', 'Muesli', 'Ketchup'], ARRAY['Kennzeichnungsentwurf', 'Naehrwertanalyse']),

('DE', 'food', 'HACCP-Konzept implementieren', 'Gefahrenanalyse und kritische Kontrollpunkte-System einrichten.', 'Jeder Lebensmittelunternehmer muss ein HACCP-System implementieren. Dies umfasst die Gefahrenanalyse, Bestimmung kritischer Kontrollpunkte, Festlegung von Grenzwerten, Ueberwachungssysteme, Korrekturmassnahmen, Verifizierungsverfahren und Dokumentation. Das System muss regelmaessig ueberprüft und aktualisiert werden.', true, 'Produktsicherheit & CE-Konformitaet', 'critical', true, 2, 'Verordnung (EG) Nr. 852/2004, Art. 5', 'Lebensmittelueberwachung der Bundeslaender', 'Bussgeld bis zu 50.000 EUR, Betriebsschliessung', ARRAY['Sieben HACCP-Grundsaetze systematisch anwenden', 'CCP-Punkte identifizieren und ueberwachen', 'Korrekturmassnahmen dokumentieren', 'Jaehrliche Revision des HACCP-Plans', 'Personal regelmaessig schulen'], '[{"title": "Hygieneverordnung 852/2004", "url": "https://eur-lex.europa.eu/eli/reg/2004/852"}]'::jsonb, ARRAY['Frischmilch', 'Rindfleisch', 'Lachs', 'Vollkornbrot', 'Tiefkuehlpizza', 'Joghurt', 'Wurst', 'Kaese'], ARRAY['HACCP-Plan', 'CCP-Ueberwachungsprotokolle', 'Schulungsnachweise']),

('DE', 'food', 'Allergenkennzeichnung', 'Korrekte Kennzeichnung der 14 Hauptallergene.', 'Die 14 Hauptallergene muessen im Zutatenverzeichnis optisch hervorgehoben werden (z.B. Fettdruck, Grossbuchstaben). Bei unverpackten Lebensmitteln muss die Information muendlich oder schriftlich verfuegbar sein. Die Allergene umfassen Gluten, Krebstiere, Eier, Fisch, Erdnuesse, Soja, Milch, Schalenfrüchte, Sellerie, Senf, Sesam, Schwefeldioxid, Lupinen und Weichtiere.', true, 'Kennzeichnung & Verpackung', 'critical', false, 3, 'LMIV (EU) Nr. 1169/2011, Art. 21, Anhang II', 'Lebensmittelueberwachung', 'Bussgeld bis zu 50.000 EUR', ARRAY['Alle 14 Allergene im Zutatenverzeichnis hervorheben', 'Kreuzkontamination dokumentieren und kommunizieren', 'Bei loser Ware schriftliche Information bereithalten', 'Allergene auch bei Rezepturaenderungen aktualisieren'], '[{"title": "LMIV Anhang II", "url": "https://eur-lex.europa.eu/eli/reg/2011/1169"}]'::jsonb, ARRAY['Vollkornbrot', 'Muesli', 'Schokolade', 'Keks', 'Fertiggericht', 'Joghurt', 'Wurst', 'Pasta'], ARRAY['Allergenmatrix', 'Zutatenliste']),

('DE', 'food', 'Rueckverfolgbarkeit sicherstellen', 'Lueckenlose Rueckverfolgbarkeit aller Lebensmittel und Zutaten.', 'Die Verordnung (EG) Nr. 178/2002 verpflichtet alle Lebensmittelunternehmer zur Rueckverfolgbarkeit. Die Identifizierung von Lieferanten und Kunden (one step back, one step forward) muss jederzeit moeglich sein. Chargenrueckverfolgbarkeit ermoeglicht im Krisenfall gezielte Rueckrufe.', true, 'Dokumentation & Rueckverfolgbarkeit', 'high', true, 4, 'Verordnung (EG) Nr. 178/2002, Art. 18', 'Lebensmittelueberwachung', 'Bussgeld bis zu 50.000 EUR', ARRAY['One-step-back/one-step-forward System einrichten', 'Chargennummern konsequent vergeben', 'Lieferantendaten vollstaendig erfassen', 'Rueckrufuebung jaehrlich durchfuehren'], '[{"title": "Basisverordnung 178/2002", "url": "https://eur-lex.europa.eu/eli/reg/2002/178"}]'::jsonb, ARRAY['Frischmilch', 'Rindfleisch', 'Lachs', 'Vollkornbrot', 'Joghurt', 'Kaese', 'Obst', 'Gemuese'], ARRAY['Rueckverfolgbarkeits-Dokumentation', 'Chargenverfolgung']),

('DE', 'food', 'Naehrwertdeklaration', 'Naehrwertangaben im vorgeschriebenen Tabellenformat.', 'Die Naehrwertdeklaration muss Energiegehalt, Fett, gesaettigte Fettsaeuren, Kohlenhydrate, Zucker, Eiweiss und Salz je 100 g/ml enthalten. Die Angaben muessen in Tabellenform erfolgen. Freiwillig koennen zusaetzliche Naehrstoffe, Portionsangaben und Referenzmengen angegeben werden.', true, 'Kennzeichnung & Verpackung', 'high', true, 5, 'LMIV (EU) Nr. 1169/2011, Art. 29-35', 'Lebensmittelueberwachung', 'Bussgeld bis zu 50.000 EUR', ARRAY['Big-7 Naehrwerte im Tabellenformat angeben', 'Analytische oder berechnete Werte verwenden', 'Toleranzen beachten', 'Portionsangaben freiwillig aber empfohlen', 'Nutri-Score als freiwillige Ergaenzung erwaegen'], '[{"title": "LMIV Naehrwert", "url": "https://eur-lex.europa.eu/eli/reg/2011/1169"}]'::jsonb, ARRAY['Muesli', 'Schokolade', 'Chips', 'Joghurt', 'Tiefkuehlpizza', 'Ketchup', 'Olivenoel', 'Proteinriegel'], ARRAY['Naehrwertanalyse', 'Kennzeichnungsentwurf']),

('DE', 'food', 'Lebensmittelkontaktmaterialien pruefen', 'Verpackungsmaterialien fuer Lebensmittelkontakt konform.', 'Alle Materialien, die mit Lebensmitteln in Kontakt kommen, muessen die Anforderungen der FCM-Rahmenverordnung und der Kunststoff-Verordnung erfuellen. Migrationstests muessen durchgefuehrt werden. Die BfR-Empfehlungen gelten fuer nicht harmonisierte Materialien wie Papier und Pappe.', true, 'Chemikalien & Inhaltsstoffe', 'high', true, 6, 'Verordnung (EG) Nr. 1935/2004, (EU) Nr. 10/2011', 'BVL, BfR', 'Bussgeld bis zu 50.000 EUR', ARRAY['Migrationstests fuer alle Kontaktmaterialien', 'Konformitaetserklaerung vom Verpackungshersteller einholen', 'BfR-Empfehlungen fuer Papier/Pappe beachten', 'MOSH/MOAH-Belastung bei Recycling-Karton pruefen'], '[{"title": "FCM-Verordnung", "url": "https://eur-lex.europa.eu/eli/reg/2004/1935"}, {"title": "BfR Empfehlungen", "url": "https://www.bfr.bund.de/de/empfehlungen_fuer_materialien_fuer_den_lebensmittelkontakt-319.html"}]'::jsonb, ARRAY['PET-Flasche', 'Konservendose', 'Getränkekarton', 'Faltschachtel', 'Tiefziehschale', 'Glasflasche'], ARRAY['FCM-Konformitaetserklaerung', 'Migrationspruefung']),

('DE', 'food', 'Bio-Zertifizierung (falls zutreffend)', 'Korrekte Bio-Kennzeichnung und Zertifizierung.', 'Wer Lebensmittel als oekologisch/biologisch vermarkten will, muss nach der EU-Oeko-Verordnung zertifiziert sein. Die Kontrollnummer der Oeko-Kontrollstelle muss auf dem Produkt angegeben werden. Das EU-Bio-Logo ist fuer vorverpackte Bio-Lebensmittel Pflicht. Herkunftsangabe der landwirtschaftlichen Rohstoffe (EU/Nicht-EU) muss neben dem Logo stehen.', true, 'Kennzeichnung & Verpackung', 'medium', true, 7, 'Verordnung (EU) 2018/848 (Oeko-Verordnung)', 'Bundesanstalt fuer Landwirtschaft und Ernaehrung (BLE)', 'Bussgeld bis zu 50.000 EUR', ARRAY['Kontrollvertrag mit zugelassener Kontrollstelle', 'EU-Bio-Logo korrekt verwenden', 'Kontrollstellennummer auf Produkt angeben', 'Herkunftsangabe EU/Nicht-EU neben Logo'], '[{"title": "Oeko-Verordnung", "url": "https://eur-lex.europa.eu/eli/reg/2018/848"}, {"title": "BLE Bio", "url": "https://www.ble.de/DE/Themen/Landwirtschaft/Oekologischer-Landbau/oekologischer-landbau_node.html"}]'::jsonb, ARRAY['Bio-Milch', 'Bio-Eier', 'Bio-Gemuese', 'Bio-Kaffee', 'Bio-Tee'], ARRAY['Oeko-Zertifikat', 'Kontrollbericht']),

('DE', 'food', 'Verpackungsregistrierung LUCID Lebensmittel', 'LUCID-Registrierung fuer Lebensmittelverpackungen.', 'Alle Lebensmittelverpackungen unterliegen dem Verpackungsgesetz. Die Registrierung bei LUCID und Systembeteiligung sind Pflicht. Bei Lebensmitteln sind haeufig Mehrkomponentenverpackungen (Kunststoffschale, Kartonumverpackung, Folie) zu beachten.', true, 'Umwelt & Nachhaltigkeit', 'high', true, 8, 'VerpackG', 'ZSVR', 'Bussgeld bis zu 200.000 EUR', ARRAY['Alle Verpackungskomponenten erfassen', 'Materialfraktionen separat melden', 'LUCID aktuell halten', 'Serviceverpackungen beachten'], '[{"title": "LUCID", "url": "https://lucid.verpackungsregister.org/"}]'::jsonb, ARRAY['PET-Flasche', 'Konservendose', 'Faltschachtel', 'Tiefziehschale', 'Glasflasche', 'Standbodenbeutel'], ARRAY['LUCID-Registrierung', 'Mengenmeldungen']),

('DE', 'food', 'Health-Claims und nährwertbezogene Angaben', 'Gesundheitsbezogene Angaben nur gemaess Health-Claims-Verordnung.', 'Gesundheitsbezogene Angaben auf Lebensmitteln sind nur zulaessig, wenn sie in der EU-Positivliste enthalten sind. Naehrwertbezogene Angaben muessen den Bedingungen des Anhangs der Verordnung entsprechen. Krankheitsbezogene Angaben sind verboten. Die Angaben muessen durch wissenschaftliche Nachweise belegt sein.', true, 'Kennzeichnung & Verpackung', 'medium', false, 9, 'Verordnung (EG) Nr. 1924/2006 (Health-Claims-Verordnung)', 'BVL', 'Bussgeld bis zu 50.000 EUR, Abmahnung', ARRAY['Nur zugelassene Health Claims verwenden', 'EU-Register zugelassener Claims pruefen', 'Naehrwertbezogene Angaben an Bedingungen knuepfen', 'Keine krankheitsbezogenen Aussagen machen'], '[{"title": "Health-Claims-Verordnung", "url": "https://eur-lex.europa.eu/eli/reg/2006/1924"}, {"title": "EU Claims Register", "url": "https://ec.europa.eu/food/safety/labelling-and-nutrition/nutrition-and-health-claims/eu-register-nutrition-and-health-claims_en"}]'::jsonb, ARRAY['Joghurt', 'Muesli', 'Proteinriegel', 'Smoothie', 'Nahrungsergaenzungsmittel'], ARRAY['Health-Claims-Pruefung', 'Wissenschaftliche Nachweise']),

('DE', 'food', 'DPP-Vorbereitung Lebensmittel', 'Vorbereitung auf den digitalen Produktpass fuer Lebensmittel.', 'Obwohl Lebensmittel nicht zu den ersten DPP-Produktgruppen gehoeren, werden digitale Transparenzanforderungen zunehmen. Die Rueckverfolgbarkeit, Herkunftsangaben, Nachhaltigkeitsinformationen und Naehrwertdaten sollten digital strukturiert erfasst werden.', false, 'Digitaler Produktpass', 'low', false, 10, 'ESPR (EU) 2024/1781', 'Europaeische Kommission', 'Noch keine direkten Strafen', ARRAY['Lieferkettendaten digital erfassen', 'Herkunftsinformationen strukturieren', 'Naehrwertdaten maschinenlesbar bereitstellen', 'QR-Code fuer Produkttransparenz erwaegen'], '[{"title": "ESPR", "url": "https://eur-lex.europa.eu/eli/reg/2024/1781"}]'::jsonb, ARRAY['Frischmilch', 'Rindfleisch', 'Schokolade', 'Olivenoel', 'Kaffee'], ARRAY['DPP-Datentemplate']);

-- ============================================
-- DEUTSCHLAND (DE) - BAUMATERIALIEN
-- ============================================
INSERT INTO checklist_templates (country_code, category_key, title, description, detailed_description, mandatory, category, priority, document_required, sort_order, legal_basis, authority, penalties, tips, links, applicable_products, document_types) VALUES

('DE', 'construction', 'CE-Kennzeichnung Bauprodukte', 'CE-Kennzeichnung gemaess Bauproduktenverordnung fuer harmonisierte Bauprodukte.', 'Harmonisierte Bauprodukte muessen die CE-Kennzeichnung gemaess Bauproduktenverordnung tragen. Die CE-Kennzeichnung basiert auf der Leistungserklaerung (DoP) und verweist auf die harmonisierte europaeische Norm. Sie muss wesentliche Merkmale wie Feuerwiderstand, Waermeleitfaehigkeit und mechanische Festigkeit deklarieren.', true, 'Produktsicherheit & CE-Konformitaet', 'critical', true, 1, 'Bauproduktenverordnung (EU) Nr. 305/2011', 'Deutsches Institut fuer Bautechnik (DIBt)', 'Bussgeld bis zu 100.000 EUR, Vertriebsverbot', ARRAY['Harmonisierte Norm (hEN) fuer das Produkt identifizieren', 'Leistungserklaerung (DoP) erstellen', 'AVCP-System (Leistungsbestaendigkeitssystem) beachten', 'Notifizierte Stelle bei System 1+, 1, 2+ einschalten'], '[{"title": "Bauproduktenverordnung", "url": "https://eur-lex.europa.eu/eli/reg/2011/305"}, {"title": "DIBt", "url": "https://www.dibt.de"}]'::jsonb, ARRAY['Mauerziegel', 'Dachziegel', 'Daemmplatte', 'Bodenfliese', 'Laminat', 'Fenster', 'Tueren', 'Stahltraeger', 'Fertigbeton'], ARRAY['Leistungserklaerung (DoP)', 'Pruefberichte', 'Werkseigene Produktionskontrolle']),

('DE', 'construction', 'Leistungserklaerung (DoP) erstellen', 'Declaration of Performance fuer harmonisierte Bauprodukte.', 'Fuer jedes Bauprodukt, das unter eine harmonisierte europaeische Norm faellt, muss der Hersteller eine Leistungserklaerung (Declaration of Performance) erstellen. Die DoP muss die wesentlichen Merkmale des Produkts und deren deklarierte Leistungen enthalten. Sie muss dem Abnehmer zur Verfuegung gestellt werden und kann in elektronischer Form bereitgestellt werden.', true, 'Dokumentation & Rueckverfolgbarkeit', 'critical', true, 2, 'Bauproduktenverordnung (EU) Nr. 305/2011, Art. 4-6', 'DIBt', 'Bussgeld bis zu 50.000 EUR', ARRAY['Alle wesentlichen Merkmale deklarieren', 'DoP-Nummer eindeutig vergeben', 'In Landessprache bereitstellen', 'Elektronische Bereitstellung zulaessig', 'DoP 10 Jahre aufbewahren'], '[{"title": "Bauproduktenverordnung DoP", "url": "https://eur-lex.europa.eu/eli/reg/2011/305"}]'::jsonb, ARRAY['Mauerziegel', 'Dachziegel', 'Fenster', 'Bodenfliese', 'Fertigbeton', 'Daemmplatte', 'Laminat'], ARRAY['Leistungserklaerung (DoP)']),

('DE', 'construction', 'Gefahrstoff-Emissionen (VOC, Formaldehyd)', 'Emissionspruefung fuer Baustoffe im Innenraum.', 'Baumaterialien fuer den Innenraum muessen die Emissionsanforderungen des Ausschusses zur gesundheitlichen Bewertung von Bauprodukten (AgBB) einhalten. Das AgBB-Schema bewertet VOC-Emissionen nach 3 und 28 Tagen. Fuer den Einsatz in oeffentlichen Gebaeuden wird zunehmend der Blaue Engel oder das natureplus-Siegel verlangt.', true, 'Chemikalien & Inhaltsstoffe', 'high', true, 3, 'AgBB-Bewertungsschema, ABG (Ausschuss fuer die Gesundheitliche Bewertung von Bauprodukten)', 'Umweltbundesamt (UBA), DIBt', 'Bussgeld bis zu 50.000 EUR bei Grenzwertueberschreitung', ARRAY['AgBB-Pruefung nach 3 und 28 Tagen durchfuehren', 'VOC-Emissionen nach ISO 16000 messen', 'Formaldehyd-Emissionen unter E1 halten', 'Blauer Engel fuer oeffentliche Ausschreibungen erwaegen'], '[{"title": "UBA AgBB", "url": "https://www.umweltbundesamt.de/themen/gesundheit/kommissionen-arbeitsgruppen/ausschuss-zur-gesundheitlichen-bewertung-von"}, {"title": "Blauer Engel", "url": "https://www.blauer-engel.de"}]'::jsonb, ARRAY['Laminat', 'Parkett', 'Wandfarbe (Innen)', 'Klebstoff', 'Vinyl-Bodenbelag', 'Putz (Innen)', 'Silikon'], ARRAY['AgBB-Pruefbericht', 'VOC-Emissionsmessung', 'Blauer-Engel-Zertifikat']),

('DE', 'construction', 'Brandschutzklassifizierung', 'Brandverhalten von Baustoffen und Bauteilen klassifizieren.', 'Baustoffe muessen nach EN 13501-1 in Euroklassen des Brandverhaltens klassifiziert werden (A1, A2, B, C, D, E, F). Die Klassifizierung bestimmt die Verwendbarkeit in verschiedenen Gebaeudeklassen. Zusaetzlich werden Rauchentwicklung (s1, s2, s3) und brennendes Abtropfen (d0, d1, d2) bewertet.', true, 'Produktsicherheit & CE-Konformitaet', 'high', true, 4, 'EN 13501-1, Bauproduktenverordnung 305/2011, Landesbauordnungen', 'DIBt, Landesbehoerden', 'Bussgeld und baurechtliche Konsequenzen', ARRAY['Brandpruefung nach EN 13501-1 durchfuehren', 'Euroklasse auf CE-Kennzeichnung angeben', 'Rauchentwicklung und Abtropfverhalten deklarieren', 'Landesbauordnung fuer Mindestanforderungen pruefen'], '[{"title": "EN 13501-1", "url": "https://eur-lex.europa.eu/eli/reg/2011/305"}]'::jsonb, ARRAY['Daemmplatte', 'Wandfarbe', 'Bodenbelag', 'Fassadenverkleidung', 'Dachfolie', 'Holzwerkstoff', 'Akustikplatte'], ARRAY['Brandpruefbericht EN 13501-1', 'Klassifizierungsbericht']),

('DE', 'construction', 'REACH-Konformitaet Baustoffe', 'REACH-Anforderungen fuer chemische Stoffe in Bauprodukten.', 'Bauprodukte enthalten zahlreiche Chemikalien, die unter REACH reguliert sind. Besonders relevant sind SVHC in Klebstoffen, Dichtstoffen, Farben und Beschichtungen. Die SCIP-Meldepflicht gilt auch fuer Bauprodukte. Asbest und andere verbotene Stoffe muessen bei Renovierungen beachtet werden.', true, 'Chemikalien & Inhaltsstoffe', 'high', true, 5, 'REACH (EG) Nr. 1907/2006', 'BAuA, ECHA', 'Bussgeld bis zu 50.000 EUR', ARRAY['SVHC-Screening fuer Klebstoffe und Dichtstoffe', 'SCIP-Meldung durchfuehren', 'Sicherheitsdatenblaetter bereithalten', 'Isocyanate in PU-Produkten beachten'], '[{"title": "REACH", "url": "https://eur-lex.europa.eu/eli/reg/2006/1907"}, {"title": "ECHA", "url": "https://echa.europa.eu/"}]'::jsonb, ARRAY['Montagekleber', 'Silikon', 'Bauschaum (PU)', 'Wandfarbe', 'Holzschutzlasur', 'Fliesenkleber', 'Grundierung'], ARRAY['REACH-Konformitaet', 'SVHC-Pruefung', 'Sicherheitsdatenblaetter']),

('DE', 'construction', 'Nachhaltigkeitszertifizierung (DGNB/BNB)', 'Nachhaltigkeitsbewertung fuer Bauprodukte nach DGNB oder BNB.', 'Fuer nachhaltige Gebaeudezertifizierungen (DGNB, BNB, LEED, BREEAM) werden zunehmend Umweltproduktdeklarationen (EPD) gefordert. EPDs nach EN 15804 dokumentieren die Umweltauswirkungen von Bauprodukten ueber ihren Lebenszyklus. Das Deutsche Bewertungssystem Nachhaltiges Bauen (BNB) ist fuer Bundesbauten verpflichtend.', false, 'Umwelt & Nachhaltigkeit', 'medium', true, 6, 'EN 15804 (EPD), BNB, ESPR (EU) 2024/1781', 'Bundesministerium fuer Wohnen, Stadtentwicklung und Bauwesen', 'Kein Bussgeld, aber Marktvorteil bei oeffentlichen Ausschreibungen', ARRAY['EPD nach EN 15804 erstellen lassen', 'Oekobilanz (LCA) durchfuehren', 'IBU oder andere Programmhalter nutzen', 'CO2-Fussabdruck berechnen'], '[{"title": "IBU EPD", "url": "https://ibu-epd.com/"}, {"title": "DGNB", "url": "https://www.dgnb.de"}, {"title": "BNB", "url": "https://www.bnb-nachhaltigesbauen.de"}]'::jsonb, ARRAY['Daemmplatte', 'Mauerziegel', 'Fertigbeton', 'Fenster', 'Bodenfliese', 'Laminat', 'Dachziegel'], ARRAY['Umweltproduktdeklaration (EPD)', 'LCA-Bericht']),

('DE', 'construction', 'DPP-Vorbereitung Bauprodukte', 'Vorbereitung auf den digitalen Produktpass fuer Bauprodukte.', 'Bauprodukte gehoeren zu den priorisierten Produktgruppen fuer den DPP unter der ESPR. Der digitale Produktpass muss Informationen zu Materialzusammensetzung, Umweltauswirkungen, Recyclingfaehigkeit und Leistungseigenschaften enthalten. Die Verzahnung mit bestehenden EPDs und DoPs bietet Synergien.', false, 'Digitaler Produktpass', 'medium', false, 7, 'ESPR (EU) 2024/1781', 'Europaeische Kommission', 'Ab Deadline Marktzugangsbeschraenkung', ARRAY['Bestehende DoP-Daten digital strukturieren', 'EPD-Daten fuer DPP nutzen', 'Materialzusammensetzung lueckenlos dokumentieren', 'QR-Code auf Bauprodukt vorbereiten'], '[{"title": "ESPR", "url": "https://eur-lex.europa.eu/eli/reg/2024/1781"}]'::jsonb, ARRAY['Daemmplatte', 'Mauerziegel', 'Fenster', 'Bodenfliese', 'Fertigbeton', 'Dachziegel'], ARRAY['DPP-Datentemplate', 'EPD', 'DoP']),

('DE', 'construction', 'Verpackung und LUCID fuer Baustoffe', 'Verpackungspflichten fuer Baumaterialverpackungen.', 'Auch Baumaterialverpackungen unterliegen dem Verpackungsgesetz. Grossgebinde, Paletten und Transportverpackungen muessen beachtet werden. Die korrekte Zuordnung von Materialfraktionen bei oft schweren und gemischten Verpackungen ist wichtig.', true, 'Kennzeichnung & Verpackung', 'medium', true, 8, 'VerpackG', 'ZSVR', 'Bussgeld bis zu 200.000 EUR', ARRAY['Transport- und Verkaufsverpackungen unterscheiden', 'Paletten als Transportverpackung melden', 'Alle Materialfraktionen erfassen', 'LUCID aktuell halten'], '[{"title": "LUCID", "url": "https://lucid.verpackungsregister.org/"}]'::jsonb, ARRAY['Daemmplatte', 'Fliesen', 'Farben', 'Kleber', 'Fertigbeton'], ARRAY['LUCID-Registrierung', 'Mengenmeldungen']);

-- ============================================
-- DEUTSCHLAND (DE) - MASCHINEN
-- ============================================
INSERT INTO checklist_templates (country_code, category_key, title, description, detailed_description, mandatory, category, priority, document_required, sort_order, legal_basis, authority, penalties, tips, links, applicable_products, document_types) VALUES

('DE', 'machinery', 'Maschinenverordnung EU 2023/1230 Konformitaet', 'Konformitaet mit der neuen EU-Maschinenverordnung sicherstellen.', 'Die neue EU-Maschinenverordnung 2023/1230 ersetzt ab Januar 2027 die bisherige Maschinenrichtlinie 2006/42/EG. Sie fuehrt neue Anforderungen an Cybersicherheit, kuenstliche Intelligenz und digitale Betriebsanleitungen ein. Die Konformitaetsbewertung umfasst eine Risikobeurteilung, die Anwendung harmonisierter Normen und die technische Dokumentation. Fuer gefaehrliche Maschinen des Anhangs I ist die Einschaltung einer notifizierten Stelle erforderlich.', true, 'Produktsicherheit & CE-Konformitaet', 'critical', true, 1, 'Maschinenverordnung (EU) 2023/1230', 'Bundesanstalt fuer Arbeitsschutz und Arbeitsmedizin (BAuA)', 'Bussgeld bis zu 100.000 EUR, Vertriebsverbot', ARRAY['Risikobeurteilung nach EN ISO 12100 durchfuehren', 'Neue Cybersicherheitsanforderungen beachten', 'Digitale Betriebsanleitung ab 2027 zulaessig', 'Anhangs-I-Maschinen durch notifizierte Stelle pruefen', 'Uebergangsfristen bis 20.01.2027 nutzen'], '[{"title": "Maschinenverordnung", "url": "https://eur-lex.europa.eu/eli/reg/2023/1230"}, {"title": "BAuA Maschinen", "url": "https://www.baua.de/DE/Themen/Anwendungssichere-Chemikalien-und-Produkte/Produktsicherheit/Maschinen/maschinen_node.html"}]'::jsonb, ARRAY['CNC-Fraesmaschine', 'Bandsaege', 'Kreissaege', 'Abkantpresse', 'Spritzgussmaschine', 'Foerderband', 'Gabelstapler', 'Industrieroboter', 'Hydraulikpumpe', 'Kompressor'], ARRAY['EU-Konformitaetserklaerung', 'Risikobeurteilung', 'Technische Dokumentation']),

('DE', 'machinery', 'Risikobeurteilung EN ISO 12100', 'Systematische Risikobeurteilung fuer alle Maschinen durchfuehren.', 'Die Risikobeurteilung ist das zentrale Element der Maschinenkonformitaet. Nach EN ISO 12100 muessen alle Gefaehrdungen identifiziert, das Risiko bewertet und durch konstruktive Massnahmen, Schutzeinrichtungen und Benutzerinformation minimiert werden. Die Risikobeurteilung muss den gesamten Lebenszyklus umfassen und bei Aenderungen aktualisiert werden.', true, 'Produktsicherheit & CE-Konformitaet', 'critical', true, 2, 'Maschinenverordnung (EU) 2023/1230, EN ISO 12100', 'BAuA', 'Bussgeld bis zu 100.000 EUR', ARRAY['Dreistufenmethode anwenden: konstruktiv, Schutz, Information', 'Alle Lebenszyklusphasen beruecksichtigen', 'Restrisiken in der Betriebsanleitung dokumentieren', 'Risikobeurteilung bei Aenderungen aktualisieren', 'Performancelevel (PL) nach EN ISO 13849-1 bestimmen'], '[{"title": "EN ISO 12100", "url": "https://www.iso.org/standard/51528.html"}, {"title": "BAuA Risikobeurteilung", "url": "https://www.baua.de"}]'::jsonb, ARRAY['CNC-Fraesmaschine', 'Bandsaege', 'Kreissaege', 'Abkantpresse', 'Spritzgussmaschine', 'Industrieroboter', 'Foerderband'], ARRAY['Risikobeurteilung', 'Risikograph', 'PL-Bestimmung']),

('DE', 'machinery', 'Betriebsanleitung in deutscher Sprache', 'Vollstaendige Betriebsanleitung mit Sicherheitshinweisen bereitstellen.', 'Jede Maschine muss mit einer Betriebsanleitung in der Sprache des Verwenderlandes ausgeliefert werden. Die Anleitung muss Angaben zur bestimmungsgemaessen Verwendung, Sicherheitshinweise, Aufstellungsbedingungen, Inbetriebnahme, Bedienung, Wartung, Demontage und Entsorgung enthalten. Ab 2027 sind auch digitale Betriebsanleitungen zulaessig.', true, 'Dokumentation & Rueckverfolgbarkeit', 'high', true, 3, 'Maschinenverordnung (EU) 2023/1230, Anhang III', 'BAuA', 'Bussgeld bis zu 50.000 EUR', ARRAY['Alle in Anhang III geforderten Inhalte aufnehmen', 'Restrisiken aus der Risikobeurteilung uebernehmen', 'Bildliche Darstellungen fuer sicherheitskritische Schritte', 'Wartungsintervalle klar definieren', 'Digitale Version ab 2027 vorbereiten'], '[{"title": "Maschinenverordnung Anhang III", "url": "https://eur-lex.europa.eu/eli/reg/2023/1230"}]'::jsonb, ARRAY['CNC-Fraesmaschine', 'Bandsaege', 'Kreissaege', 'Abkantpresse', 'Spritzgussmaschine', 'Foerderband', 'Kompressor'], ARRAY['Betriebsanleitung (DE)', 'Sicherheitsdatenblatt']),

('DE', 'machinery', 'EMV und elektrische Sicherheit', 'Elektromagnetische Vertraeglichkeit und elektrische Sicherheit.', 'Maschinen mit elektrischen Komponenten muessen die EMV-Richtlinie und die Niederspannungsrichtlinie einhalten. Die EN 60204-1 definiert die Anforderungen an die elektrische Ausruestung von Maschinen. Die EMV-Pruefung stellt sicher, dass die Maschine andere Geraete nicht stoert und selbst stoerfest ist.', true, 'Produktsicherheit & CE-Konformitaet', 'high', true, 4, 'EMV-Richtlinie 2014/30/EU, Niederspannungsrichtlinie 2014/35/EU, EN 60204-1', 'Bundesnetzagentur, BAuA', 'Bussgeld bis zu 50.000 EUR', ARRAY['EN 60204-1 fuer elektrische Ausruestung anwenden', 'EMV-Pruefung durchfuehren oder Herstellererklaerung', 'Not-Halt-Einrichtungen nach EN ISO 13850', 'Schutzart (IP) korrekt bestimmen'], '[{"title": "EN 60204-1", "url": "https://www.iso.org/standard/82537.html"}, {"title": "EMV-Richtlinie", "url": "https://eur-lex.europa.eu/eli/dir/2014/30"}]'::jsonb, ARRAY['CNC-Fraesmaschine', 'Kreissaege', 'Abkantpresse', 'Spritzgussmaschine', 'Frequenzumrichter', 'Industrieroboter'], ARRAY['EMV-Pruefbericht', 'EN 60204-1 Pruefprotokoll']),

('DE', 'machinery', 'REACH und Gefahrstoffe in Maschinen', 'REACH-konforme Betriebsstoffe und Materialien.', 'Maschinen enthalten haeufig Schmierstoffe, Hydraulikfluessigkeiten und Kuehlmittel, die unter REACH reguliert sind. Sicherheitsdatenblaetter muessen fuer alle Betriebsstoffe bereitgestellt werden. Bei der Konstruktion sollten SVHC-freie Alternativen bevorzugt werden.', true, 'Chemikalien & Inhaltsstoffe', 'medium', true, 5, 'REACH (EG) Nr. 1907/2006', 'BAuA, ECHA', 'Bussgeld bis zu 50.000 EUR', ARRAY['Sicherheitsdatenblaetter fuer alle Betriebsstoffe bereithalten', 'SVHC in Schmierstoffen und Hydraulikoel pruefen', 'Substitutionspruefung fuer gefaehrliche Stoffe', 'SCIP-Meldung bei SVHC in Maschinenkomponenten'], '[{"title": "REACH", "url": "https://eur-lex.europa.eu/eli/reg/2006/1907"}]'::jsonb, ARRAY['CNC-Fraesmaschine', 'Hydraulikpumpe', 'Kompressor', 'Kreissaege', 'Drehmaschine'], ARRAY['Sicherheitsdatenblaetter', 'SVHC-Pruefung']),

('DE', 'machinery', 'Laermemissionen dokumentieren', 'Laermemissionswerte messen und in der Betriebsanleitung angeben.', 'Die Maschinenverordnung verlangt die Angabe des Schalleistungspegels und des Schalldruckpegels am Arbeitsplatz in der Betriebsanleitung. Die Messung muss nach harmonisierten Normen erfolgen. Bei einem Schalldruckpegel ueber 80 dB(A) muessen besondere Schutzmassnahmen ergriffen werden.', true, 'Umwelt & Nachhaltigkeit', 'medium', true, 6, 'Maschinenverordnung (EU) 2023/1230, Richtlinie 2000/14/EG (Outdoor)', 'BAuA', 'Bussgeld bis zu 50.000 EUR', ARRAY['Laermmessung nach harmonisierten Normen', 'Schallleistungspegel in Betriebsanleitung angeben', 'Outdoor-Maschinen: RL 2000/14/EG beachten', 'Laermminderungsmassnahmen dokumentieren'], '[{"title": "Maschinenverordnung", "url": "https://eur-lex.europa.eu/eli/reg/2023/1230"}, {"title": "Outdoor-Laerm RL", "url": "https://eur-lex.europa.eu/eli/dir/2000/14"}]'::jsonb, ARRAY['CNC-Fraesmaschine', 'Kreissaege', 'Kompressor', 'Hochdruckreiniger', 'Rasenmaeher', 'Haecksler'], ARRAY['Laermmessbericht', 'Schallleistungspegel-Nachweis']),

('DE', 'machinery', 'Funktionale Sicherheit SIL/PL', 'Sicherheitsrelevante Steuerungsteile nach EN ISO 13849-1 oder IEC 62061.', 'Sicherheitsrelevante Teile von Maschinensteuerungen muessen die Anforderungen an funktionale Sicherheit erfuellen. Der erforderliche Performance Level (PL) wird aus der Risikobeurteilung abgeleitet. Die Validierung des erreichten PL muss dokumentiert werden. Alternativ kann der SIL nach IEC 62061 verwendet werden.', true, 'Produktsicherheit & CE-Konformitaet', 'high', true, 7, 'EN ISO 13849-1, IEC 62061, Maschinenverordnung (EU) 2023/1230', 'BAuA', 'Bussgeld bis zu 100.000 EUR', ARRAY['Erforderlichen PL aus Risikobeurteilung ableiten', 'SISTEMA-Tool fuer PL-Berechnung verwenden', 'Validierung des erreichten PL dokumentieren', 'Sicherheitsfunktionen regelmaessig testen'], '[{"title": "EN ISO 13849-1", "url": "https://www.iso.org/standard/73481.html"}, {"title": "SISTEMA Tool", "url": "https://www.dguv.de/ifa/praxishilfen/praktische-loesungsbeispiele-maschinenschutz/software-sistema/index.jsp"}]'::jsonb, ARRAY['CNC-Fraesmaschine', 'Abkantpresse', 'Industrieroboter', 'Spritzgussmaschine', 'Foerderband', 'Hubarbeitsbuehne'], ARRAY['PL-Berechnung', 'SISTEMA-Nachweis', 'Validierungsbericht']),

('DE', 'machinery', 'DPP-Vorbereitung Maschinen', 'Vorbereitung auf den digitalen Produktpass fuer Maschinen.', 'Die ESPR wird auch fuer Maschinen digitale Produktpaesse erfordern. Die Verknuepfung mit der digitalen Betriebsanleitung nach der neuen Maschinenverordnung bietet Synergien. Der DPP soll Materialzusammensetzung, Energieeffizienz, Reparierbarkeit und Recyclingfaehigkeit dokumentieren.', false, 'Digitaler Produktpass', 'medium', false, 8, 'ESPR (EU) 2024/1781, Maschinenverordnung (EU) 2023/1230', 'Europaeische Kommission', 'Ab Deadline Marktzugangsbeschraenkung', ARRAY['Digitale Betriebsanleitung mit DPP verknuepfen', 'Materialzusammensetzung digital erfassen', 'Energieeffizienz dokumentieren', 'Reparierbarkeit und Ersatzteilversorgung planen'], '[{"title": "ESPR", "url": "https://eur-lex.europa.eu/eli/reg/2024/1781"}, {"title": "Maschinenverordnung", "url": "https://eur-lex.europa.eu/eli/reg/2023/1230"}]'::jsonb, ARRAY['CNC-Fraesmaschine', 'Industrieroboter', 'Spritzgussmaschine', 'Foerderband', 'Kompressor'], ARRAY['DPP-Datentemplate', 'Digitale Betriebsanleitung']),

('DE', 'machinery', 'Verpackung und Transport Maschinen', 'Verpackungs- und Transportanforderungen fuer Maschinen.', 'Maschinenverpackungen unterliegen dem Verpackungsgesetz. Bei der Maschinensicherung waehrend des Transports sind die Ladungssicherungsvorschriften der VDI 2700 zu beachten. Bei Maschinen mit Betriebsstoffen gelten zusaetzlich Gefahrgutvorschriften.', true, 'Kennzeichnung & Verpackung', 'low', true, 9, 'VerpackG, VDI 2700, ADR', 'ZSVR', 'Bussgeld bis zu 200.000 EUR (Verpackung)', ARRAY['Transportverpackung LUCID-konform melden', 'Ladungssicherung nach VDI 2700', 'Gefahrgutvorschriften bei Betriebsstoffen beachten', 'Schwertransport bei Grossmaschinen planen'], '[{"title": "LUCID", "url": "https://lucid.verpackungsregister.org/"}, {"title": "VDI 2700", "url": "https://www.vdi.de"}]'::jsonb, ARRAY['CNC-Fraesmaschine', 'Abkantpresse', 'Spritzgussmaschine', 'Kompressor', 'Gabelstapler'], ARRAY['LUCID-Registrierung', 'Ladungssicherungsnachweis']),

('DE', 'machinery', 'Lieferkettensorgfalt Maschinenbau', 'LkSG-Pflichten fuer den Maschinenbau einhalten.', 'Der Maschinenbau hat globale Lieferketten mit Risiken bei der Rohstoffgewinnung und Komponentenfertigung. Das LkSG verpflichtet zur Risikoanalyse bezueglich Menschenrechte und Umwelt. Seltene Erden fuer Elektromotoren und Konfliktmineralien fuer Elektronikkomponenten sind besonders relevant.', true, 'Soziale Verantwortung & Lieferkette', 'medium', true, 10, 'LkSG, CSDDD (EU) 2024/1760', 'BAFA', 'Bussgeld bis zu 2% des Jahresumsatzes', ARRAY['Risikoanalyse fuer Komponentenlieferanten', 'Seltene Erden-Lieferkette pruefen', 'Sozialstandards bei Zulieferern durchsetzen', 'Beschwerdemechanismus einrichten'], '[{"title": "LkSG", "url": "https://www.gesetze-im-internet.de/lksg/"}, {"title": "BAFA", "url": "https://www.bafa.de/DE/Lieferketten/lieferketten_node.html"}]'::jsonb, ARRAY['CNC-Fraesmaschine', 'Industrieroboter', 'Elektromotor', 'Spritzgussmaschine', 'Foerderband'], ARRAY['Risikoanalyse', 'LkSG-Bericht']);

-- ============================================
-- FRANKREICH (FR) - ALLE KATEGORIEN
-- ============================================

-- Laenderspezifische Anforderungen FR
INSERT INTO checklist_templates (country_code, category_key, title, description, detailed_description, mandatory, category, priority, document_required, sort_order, legal_basis, authority, penalties, tips, links, applicable_products, document_types) VALUES
('FR', 'electronics', 'Indice de reparabilite (Reparierbarkeitsindex)', 'Berechnung und Anzeige des franzoesischen Reparierbarkeitsindex fuer Elektronik.', 'Frankreich verpflichtet seit Januar 2021 zur Angabe eines Reparierbarkeitsindex fuer bestimmte Elektronikprodukte (Smartphones, Laptops, Fernseher, Waschmaschinen, Rasenmaeher). Der Index wird auf einer Skala von 0 bis 10 bewertet und muss am Verkaufspunkt gut sichtbar angezeigt werden. Die Bewertung basiert auf Dokumentation, Demontierbarkeit, Ersatzteilverfuegbarkeit, Preis der Ersatzteile und produktspezifischen Kriterien. Ab 2024 wird der Index zum Indice de durabilite erweitert.', true, 'Kennzeichnung & Verpackung', 'critical', true, 1, 'Loi AGEC (Loi anti-gaspillage pour une economie circulaire) Art. 16', 'DGCCRF, ADEME', 'Amende bis zu 15.000 EUR pro Verstoss', ARRAY['Reparierbarkeitsindex nach offizieller Berechnungsmethode ermitteln', 'Index am Verkaufspunkt neben dem Preis anzeigen', 'Auf der Produktverpackung und im Online-Shop anzeigen', 'Ersatzteilpreise und -verfuegbarkeit dokumentieren', 'Demontierbarkeit in der Konstruktion beruecksichtigen'], '[{"title":"Indice de reparabilite","url":"https://www.ecologie.gouv.fr/indice-reparabilite"},{"title":"AGEC Gesetz","url":"https://www.legifrance.gouv.fr/jorf/id/JORFTEXT000041553759"}]'::jsonb, ARRAY['Smartphone', 'Laptop', 'Fernseher', 'Tablet'], ARRAY['Reparierbarkeitsindex-Berechnung', 'Bewertungsprotokoll']),

('FR', 'electronics', 'Triman-Symbol und Info-tri', 'Triman-Recycling-Symbol und Sortierhinweise auf Verpackung und Produkt anbringen.', 'Das Triman-Symbol ist in Frankreich fuer alle recycelbaren Produkte und Verpackungen verpflichtend. Seit Januar 2022 muss zusaetzlich die Info-tri (Sortieranleitung) angebracht werden, die dem Verbraucher zeigt, in welche Tonne das Produkt gehoert. Das Symbol muss auf dem Produkt selbst oder der Verpackung angebracht sein und darf nicht nur digital bereitgestellt werden.', true, 'Kennzeichnung & Verpackung', 'high', false, 2, 'Loi AGEC, Decret n° 2021-835', 'ADEME', 'Amende bis zu 15.000 EUR', ARRAY['Triman-Symbol auf Verpackung und/oder Produkt anbringen', 'Info-tri Sortierhinweis hinzufuegen', 'Farbcodes fuer Sortierung beachten', 'Digitale Triman-Info nicht ausreichend'], '[{"title":"Triman & Info-tri","url":"https://www.ecologie.gouv.fr/signalitique-de-tri-triman-info-tri"}]'::jsonb, ARRAY['Smartphone', 'Laptop', 'Kopfhoerer', 'Router', 'Drucker', 'Verpackung'], ARRAY['Triman-Kennzeichnungsmuster']),

('FR', 'electronics', 'REP-Registrierung (Responsabilite elargie du producteur)', 'Registrierung im franzoesischen System der erweiterten Herstellerverantwortung.', 'Frankreich hat ein umfassendes REP-System fuer zahlreiche Produktkategorien. Fuer Elektronik muessen Hersteller sich bei der zustaendigen Oeko-Organisation (z.B. ecosystem) registrieren und Gebuehren zahlen. Die AGEC hat die Anzahl der REP-Filieren auf 24 erweitert. Die Registrierungsnummer muss auf Rechnungen und Verkaufsplattformen angegeben werden.', true, 'Umwelt & Nachhaltigkeit', 'critical', true, 3, 'Code de l''environnement, Loi AGEC', 'ADEME', 'Amende bis zu 30.000 EUR, Vertriebsverbot', ARRAY['Bei ecosystem oder Ecologic registrieren', 'Unique Identification Number (UIN) beantragen', 'UIN auf allen Marktplaetzen angeben', 'Oeko-Beitraege fristgerecht zahlen'], '[{"title":"ecosystem REP","url":"https://www.ecosystem.eco/"},{"title":"ADEME REP","url":"https://www.ademe.fr/expertises/dechets/elements-contexte/filieres-a-responsabilite-elargie-producteurs-rep"}]'::jsonb, ARRAY['Smartphone', 'Laptop', 'Fernseher', 'Drucker', 'Router', 'Kopfhoerer'], ARRAY['REP-Registrierungsnachweis', 'UIN-Bescheinigung']),

('FR', 'textiles', 'REP Textiles - Refashion Registrierung', 'Registrierung bei Refashion fuer die erweiterte Herstellerverantwortung Textilien.', 'Frankreich war eines der ersten Laender mit einer REP-Filiere fuer Textilien. Hersteller und Importeure muessen sich bei Refashion (ehemals Eco TLC) registrieren und Oeko-Beitraege zahlen. Die Beitraege finanzieren die Sammlung, Sortierung und das Recycling von Alttextilien. Ab 2025 werden die Beitraege nach Umweltkriterien (Eco-Modulation) differenziert.', true, 'Umwelt & Nachhaltigkeit', 'critical', true, 1, 'Code de l''environnement, Loi AGEC', 'ADEME, Refashion', 'Amende bis zu 30.000 EUR', ARRAY['Bei Refashion registrieren', 'Eco-Modulation-Kriterien beachten', 'Jahresmeldungen fristgerecht abgeben', 'Recyclingfaehige Materialien bevorzugen'], '[{"title":"Refashion","url":"https://refashion.fr/"}]'::jsonb, ARRAY['T-Shirt', 'Jeans', 'Kleid', 'Jacke', 'Schuhe', 'Bettwaesche'], ARRAY['Refashion-Registrierung', 'Mengenmeldung']),

('FR', 'textiles', 'Affichage environnemental (Umwelt-Anzeige)', 'Franzoesische Umweltkennzeichnung fuer Textilien vorbereiten.', 'Frankreich fuehrt eine verpflichtende Umweltkennzeichnung (Affichage environnemental) fuer Textilien ein. Der Eco-Score bewertet die Umweltauswirkungen ueber den gesamten Lebenszyklus. Die Methodik basiert auf PEF (Product Environmental Footprint) und beruecksichtigt CO2-Emissionen, Wasserverbrauch, Eutrophierung und Mikroplastik. Ab 2025 voraussichtlich verpflichtend.', false, 'Umwelt & Nachhaltigkeit', 'high', true, 2, 'Loi Climat et Resilience Art. 2, AGEC', 'ADEME', 'In Vorbereitung', ARRAY['PEF-Berechnung fuer Textilprodukte vorbereiten', 'Oekobilanz-Daten erheben', 'Microfibre-Emissionen bei Synthetik beruecksichtigen', 'ADEME-Methodik verfolgen'], '[{"title":"Affichage environnemental","url":"https://www.ecologie.gouv.fr/affichage-environnemental"}]'::jsonb, ARRAY['T-Shirt', 'Jeans', 'Kleid', 'Jacke', 'Pullover'], ARRAY['PEF-Studie', 'Oekobilanz']),

('FR', 'batteries', 'REP Batterien - Corepile/Screlec', 'Registrierung bei Corepile oder Screlec fuer die franzoesische Batterie-REP.', 'Hersteller und Importeure von Batterien muessen sich in Frankreich bei einem zugelassenen Oeko-Organismus (Corepile fuer Geraetebatterien oder Screlec) registrieren. Die Oeko-Beitraege finanzieren die Sammlung und das Recycling. Die Registrierung muss vor dem Inverkehrbringen erfolgen.', true, 'Umwelt & Nachhaltigkeit', 'critical', true, 1, 'Code de l''environnement, EU-Batterieverordnung (EU) 2023/1542', 'ADEME', 'Amende bis zu 30.000 EUR', ARRAY['Bei Corepile oder Screlec registrieren', 'Oeko-Beitraege nach Batterietyp kalkulieren', 'Jahresmeldungen fristgerecht', 'Sammelquoten beachten'], '[{"title":"Corepile","url":"https://www.corepile.fr/"},{"title":"Screlec","url":"https://www.screlec.fr/"}]'::jsonb, ARRAY['Li-Ion 18650', 'AA-Batterie', 'E-Bike-Akku', 'Smartphone-Akku', 'Laptop-Akku'], ARRAY['REP-Registrierung', 'Mengenmeldung']),

('FR', 'packaging', 'REP Emballages - Citeo Registrierung', 'Pflichtregistrierung bei Citeo fuer Verpackungen in Frankreich.', 'In Frankreich muessen alle Erstinverkehrbringer von verpackten Produkten bei Citeo (ehemals Eco-Emballages) registriert sein und Oeko-Beitraege zahlen. Die AGEC hat die Anforderungen verschaerft, einschliesslich Eco-Modulation der Beitraege, Triman-Symbol und Info-tri. Die Beitraege werden nach Materialart, Gewicht und Recyclingfaehigkeit berechnet.', true, 'Umwelt & Nachhaltigkeit', 'critical', true, 1, 'Code de l''environnement, Loi AGEC', 'ADEME, Citeo', 'Amende bis zu 30.000 EUR, Vertriebsverbot', ARRAY['Bei Citeo registrieren', 'Eco-Modulation: recyclingfaehige Verpackungen nutzen', 'Triman und Info-tri auf allen Verpackungen', 'Mengenmeldung nach Materialfraktion'], '[{"title":"Citeo","url":"https://www.citeo.com/"}]'::jsonb, ARRAY['Wellpappkarton', 'PET-Flasche', 'Glasflasche', 'Kunststofftüte', 'Getränkekarton'], ARRAY['Citeo-Registrierung', 'Mengenmeldung']),

('FR', 'furniture', 'REP Meubles - Ecomaison Registrierung', 'Registrierung bei Ecomaison (ehemals Eco-mobilier) fuer Moebel-REP.', 'Frankreich hat eine spezifische REP-Filiere fuer Moebel. Hersteller und Importeure muessen sich bei Ecomaison registrieren und Oeko-Beitraege zahlen. Die Beitraege finanzieren die Sammlung, Wiederverwendung und das Recycling von Altmoebeln. Die Registrierung ist vor dem Inverkehrbringen Pflicht.', true, 'Umwelt & Nachhaltigkeit', 'critical', true, 1, 'Code de l''environnement, Loi AGEC', 'ADEME, Ecomaison', 'Amende bis zu 30.000 EUR', ARRAY['Bei Ecomaison registrieren', 'Moebel nach Kategorien klassifizieren', 'Oeko-Modulation: langlebige Moebel bevorzugen', 'Jahresmeldungen einreichen'], '[{"title":"Ecomaison","url":"https://www.ecomaison.com/"}]'::jsonb, ARRAY['Sofa', 'Esstisch', 'Kleiderschrank', 'Matratze', 'Buerstuhl'], ARRAY['Ecomaison-Registrierung', 'Mengenmeldung']),

('FR', 'cosmetics', 'ANSM Kosmetik-Ueberwachung', 'Kosmetikovigilanz-Meldepflicht an die ANSM beachten.', 'In Frankreich ueberwacht die ANSM die Sicherheit kosmetischer Mittel. Schwerwiegende unerwuenschte Wirkungen muessen innerhalb von 20 Tagen an die ANSM gemeldet werden. Frankreich hat zusaetzlich eigene Anforderungen an die Deklaration von Nanomaterialien und endokrinen Disruptoren.', true, 'Produktsicherheit & CE-Konformitaet', 'high', true, 1, 'Code de la sante publique, Kosmetikverordnung (EG) Nr. 1223/2009', 'ANSM', 'Amende bis zu 150.000 EUR', ARRAY['Kosmetikovigilanz-System einrichten', 'Unerwuenschte Wirkungen innerhalb 20 Tagen melden', 'Franzoesische Verbraucherhotline einrichten', 'ANSM-Datenbank regelmaessig pruefen'], '[{"title":"ANSM Cosmetiques","url":"https://ansm.sante.fr/page/cosmetiques"}]'::jsonb, ARRAY['Tagescreme', 'Shampoo', 'Sonnencreme', 'Haarfarbe', 'Parfum'], ARRAY['Kosmetikovigilanz-Bericht']),

('FR', 'toys', 'NF-Kennzeichnung Spielzeug (freiwillig)', 'Franzoesische NF-Kennzeichnung als Qualitaetszeichen fuer Spielzeug.', 'Die NF-Kennzeichnung (Norme Francaise) ist in Frankreich ein freiwilliges Qualitaetszeichen, das ueber die CE-Anforderungen hinausgeht. Fuer Spielzeug existiert die NF Petite Enfance fuer Kleinkinder. Die Zertifizierung wird von AFNOR Certification durchgefuehrt und ist bei franzoesischen Verbrauchern und Einkaeufern anerkannt.', false, 'Produktsicherheit & CE-Konformitaet', 'low', true, 1, 'Freiwilliger Standard, Code de la consommation', 'AFNOR Certification', 'Keine Pflicht, Marktvorteil', ARRAY['NF-Zertifizierung als Marktvorteil erwaegen', 'NF Petite Enfance fuer Kleinkindprodukte', 'AFNOR Certification beauftragen', 'Zertifikat jaehrlich erneuern'], '[{"title":"AFNOR NF","url":"https://certification.afnor.org/"}]'::jsonb, ARRAY['Greifling', 'Beissring', 'Babypuppe', 'Stapelturm', 'Holzbausteine'], ARRAY['NF-Zertifikat']),

('FR', 'food', 'Nutri-Score Kennzeichnung', 'Nutri-Score Kennzeichnung fuer den franzoesischen Markt.', 'Frankreich war das erste Land, das den Nutri-Score eingefuehrt hat. Obwohl freiwillig, ist der Nutri-Score auf dem franzoesischen Markt weit verbreitet und wird von Verbrauchern und Haendlern erwartet. Die Berechnung basiert auf dem Naehrwertprofil pro 100 g/ml und bewertet guenstige (Obst, Gemuese, Ballaststoffe) und unguenstige (Energie, Zucker, Fett, Salz) Naehrstoffe. Ab 2024 gilt der ueberarbeitete Nutri-Score-Algorithmus.', false, 'Kennzeichnung & Verpackung', 'medium', false, 1, 'Freiwillig, Code de la sante publique', 'Sante publique France', 'Keine Pflicht, aber Marktnachteil ohne', ARRAY['Nutri-Score-Berechnung durchfuehren', 'Neuen Algorithmus ab 2024 verwenden', 'Lizenz bei Sante publique France beantragen', 'Rezepturen fuer besseren Score optimieren'], '[{"title":"Nutri-Score","url":"https://www.santepubliquefrance.fr/determinants-de-sante/nutrition-et-activite-physique/articles/nutri-score"}]'::jsonb, ARRAY['Joghurt', 'Muesli', 'Schokolade', 'Chips', 'Tiefkuehlpizza', 'Olivenoel'], ARRAY['Nutri-Score-Berechnung']),

('FR', 'construction', 'Avis Technique / DTA', 'Technische Zulassung (Avis Technique) fuer innovative Bauprodukte in Frankreich.', 'Fuer innovative Bauprodukte, die nicht durch harmonisierte Normen abgedeckt sind, ist in Frankreich ein Avis Technique (Technische Bewertung) des CSTB erforderlich. Die DTA (Document Technique d''Application) definiert die Anwendungsbedingungen. Fuer den franzoesischen Markt ist dieses Dokument oft Voraussetzung fuer die Verwendung in oeffentlichen Bauprojekten und fuer den Versicherungsschutz.', false, 'Produktsicherheit & CE-Konformitaet', 'high', true, 1, 'Code de la construction et de l''habitation', 'CSTB', 'Kein Vertriebsverbot, aber praktisch notwendig fuer den Markt', ARRAY['CSTB-Antrag fruehzeitig stellen (6-12 Monate Bearbeitungszeit)', 'DTA fuer innovative Produkte beantragen', 'Assurance-Anforderungen beachten', 'Regelmaessige Erneuerung alle 3-7 Jahre'], '[{"title":"CSTB Avis Technique","url":"https://www.cstb.fr/evaluations/avis-techniques/"}]'::jsonb, ARRAY['Daemmplatte', 'Fenster', 'Fassadenverkleidung', 'Bodensystem', 'Dachsystem'], ARRAY['Avis Technique', 'DTA']),

('FR', 'machinery', 'DIRECCTE Marktueberwachung Maschinen', 'Franzoesische Marktueberwachung fuer Maschinen durch DIRECCTE.', 'In Frankreich fuehrt die DREETS (ehemals DIRECCTE) die Marktueberwachung fuer Maschinen durch. Bei Kontrollen werden CE-Kennzeichnung, Konformitaetserklaerung, Betriebsanleitung in Franzoesisch und Sicherheitseinrichtungen geprueft. Die Betriebsanleitung muss vollstaendig auf Franzoesisch vorliegen.', true, 'Dokumentation & Rueckverfolgbarkeit', 'high', true, 1, 'Code du travail, Maschinenverordnung (EU) 2023/1230', 'DREETS (Direction regionale de l''economie, de l''emploi, du travail et des solidarites)', 'Amende bis zu 150.000 EUR', ARRAY['Betriebsanleitung vollstaendig auf Franzoesisch', 'CE-Konformitaetserklaerung auf Franzoesisch bereithalten', 'Sicherheitskennzeichnung in Franzoesisch', 'DREETS-Anforderungen proaktiv erfuellen'], '[{"title":"DREETS","url":"https://dreets.gouv.fr/"}]'::jsonb, ARRAY['CNC-Fraesmaschine', 'Kreissaege', 'Abkantpresse', 'Gabelstapler', 'Industrieroboter'], ARRAY['Betriebsanleitung (FR)', 'Konformitaetserklaerung (FR)']);

-- EU-weite Anforderungen fuer FR
INSERT INTO checklist_templates (country_code, category_key, title, description, detailed_description, mandatory, category, priority, document_required, sort_order, legal_basis, authority, penalties, tips, links, applicable_products, document_types) VALUES
('FR', 'electronics', 'CE-Kennzeichnung pruefen', 'CE-Zeichen korrekt und sichtbar anbringen.', 'Die CE-Kennzeichnung ist fuer alle Elektronikprodukte im EU-Binnenmarkt verpflichtend. Sie muss mindestens 5 mm hoch, sichtbar und dauerhaft angebracht sein. Der Hersteller uebernimmt die Verantwortung fuer die EU-Konformitaet. Die Kennzeichnung muss vor dem Inverkehrbringen angebracht werden.', true, 'Produktsicherheit & CE-Konformitaet', 'critical', true, 1, 'Verordnung (EU) 2023/988 (GPSR)', 'DGCCRF (Direction generale de la concurrence, de la consommation et de la repression des fraudes)', 'Amende jusqu''a 300.000 EUR et 2 ans d''emprisonnement', ARRAY['CE-Zeichen mindestens 5 mm hoch', 'Auf Produkt, nicht nur Verpackung', 'Konformitaetserklaerung erstellen', 'Technische Dokumentation bereithalten'], '[{"title":"GPSR","url":"https://eur-lex.europa.eu/eli/reg/2023/988"}]'::jsonb, ARRAY['Smartphone', 'Laptop', 'Fernseher', 'Monitor', 'Router', 'Drucker'], ARRAY['EU-Konformitaetserklaerung', 'Technische Dokumentation']),

('FR', 'electronics', 'RoHS-Konformitaet sicherstellen', 'Beschraenkung gefaehrlicher Stoffe einhalten.', 'Die RoHS-Richtlinie beschraenkt die Verwendung gefaehrlicher Stoffe in Elektrogeraeten. Grenzwerte: 0,1% fuer Blei, Quecksilber, Cr VI, PBB, PBDE, DEHP, BBP, DBP; 0,01% fuer Cadmium. Materialanalysen und Lieferantenerklaerungen sind erforderlich. Die Dokumentation muss 10 Jahre aufbewahrt werden.', true, 'Chemikalien & Inhaltsstoffe', 'high', true, 2, 'RoHS 2011/65/EU, Delegierte Richtlinie (EU) 2015/863', 'DGCCRF (Direction generale de la concurrence, de la consommation et de la repression des fraudes)', 'Amende jusqu''a 150.000 EUR', ARRAY['XRF-Screening fuer Stichproben', 'Lieferantenerklaerungen einholen', 'DEHP, BBP, DBP und DIBP beachten', 'Dokumentation 10 Jahre aufbewahren'], '[{"title":"RoHS-Richtlinie","url":"https://eur-lex.europa.eu/eli/dir/2011/65"}]'::jsonb, ARRAY['Smartphone', 'Laptop', 'Monitor', 'Drucker', 'Router'], ARRAY['RoHS-Konformitaetserklaerung', 'Materialanalysen']),

('FR', 'electronics', 'REACH-SVHC-Pruefung', 'Besonders besorgniserregende Stoffe (SVHC) pruefen.', 'REACH verpflichtet zur Information ueber SVHC-Stoffe ueber 0,1%. Die SVHC-Kandidatenliste wird halbjaehrlich aktualisiert. SCIP-Meldepflicht an ECHA besteht. Fuer Elektronik sind Weichmacher, Flammschutzmittel und Loetlegierungen besonders relevant.', true, 'Chemikalien & Inhaltsstoffe', 'high', true, 3, 'REACH (EG) Nr. 1907/2006', 'DGCCRF (Direction generale de la concurrence, de la consommation et de la repression des fraudes)', 'Amende jusqu''a 150.000 EUR', ARRAY['SVHC-Kandidatenliste halbjaehrlich pruefen', 'SCIP-Meldung durchfuehren', 'Lieferantenbefragungen dokumentieren'], '[{"title":"REACH","url":"https://eur-lex.europa.eu/eli/reg/2006/1907"},{"title":"ECHA SVHC","url":"https://echa.europa.eu/candidate-list-table"}]'::jsonb, ARRAY['Smartphone', 'Laptop', 'Kopfhoerer', 'Kabel', 'Ladegeraet'], ARRAY['SVHC-Pruefbericht', 'SCIP-Meldung']),

('FR', 'electronics', 'DPP-Vorbereitung Elektronik', 'Vorbereitung auf den digitalen Produktpass.', 'Die ESPR fuehrt den digitalen Produktpass fuer Elektronik voraussichtlich ab 2028 ein. Der DPP muss Materialzusammensetzung, Reparierbarkeit, Recyclingfaehigkeit und CO2-Fussabdruck enthalten. Fruehzeitige Vorbereitung verschafft Wettbewerbsvorteile.', false, 'Digitaler Produktpass', 'medium', false, 8, 'ESPR (EU) 2024/1781', 'DGCCRF (Direction generale de la concurrence, de la consommation et de la repression des fraudes)', 'Amende jusqu''a 75.000 EUR', ARRAY['Produktdaten digital erfassen', 'CO2-Fussabdruck berechnen', 'QR-Code-Infrastruktur aufbauen', 'Lieferkettendaten verfuegbar machen'], '[{"title":"ESPR","url":"https://eur-lex.europa.eu/eli/reg/2024/1781"}]'::jsonb, ARRAY['Smartphone', 'Laptop', 'Monitor', 'Fernseher', 'Drucker'], ARRAY['DPP-Datentemplate']),

('FR', 'electronics', 'GPSR Wirtschaftsakteur-Pflichten', 'Pflichten als Wirtschaftsakteur nach GPSR erfuellen.', 'Die neue GPSR definiert Pflichten fuer Hersteller, Importeure und Haendler. Risikoanalyse, technische Dokumentation und Identifikation des verantwortlichen Wirtschaftsakteurs sind Pflicht. Bei Online-Verkauf gelten erweiterte Informationspflichten.', true, 'Dokumentation & Rueckverfolgbarkeit', 'high', true, 5, 'GPSR (EU) 2023/988', 'DGCCRF (Direction generale de la concurrence, de la consommation et de la repression des fraudes)', 'Amende jusqu''a 150.000 EUR', ARRAY['Eigene Rolle als Wirtschaftsakteur bestimmen', 'Kontaktdaten auf Produkt/Verpackung', 'Risikoanalyse dokumentieren', 'Rueckrufprozesse definieren'], '[{"title":"GPSR","url":"https://eur-lex.europa.eu/eli/reg/2023/988"}]'::jsonb, ARRAY['Smartphone', 'Laptop', 'Monitor', 'Router', 'Ladegeraet'], ARRAY['Risikoanalyse', 'Technische Dokumentation']),

('FR', 'electronics', 'Lieferkettensorgfalt Elektronik', 'Sorgfaltspflichten in der Elektronik-Lieferkette.', 'Die CSDDD verpflichtet zu Sorgfaltspflichten bezueglich Menschenrechte und Umwelt. Fuer Elektronik sind Konfliktmineralien und Arbeitsbedingungen besonders relevant. Risikoanalyse und Praeventionsmassnahmen sind erforderlich.', true, 'Soziale Verantwortung & Lieferkette', 'medium', true, 7, 'CSDDD (EU) 2024/1760', 'DGCCRF (Direction generale de la concurrence, de la consommation et de la repression des fraudes)', 'Amende jusqu''a 75.000 EUR', ARRAY['Risikoanalyse fuer Zulieferer', 'Konfliktmineralien nachverfolgen', 'Beschwerdemechanismus einrichten'], '[{"title":"CSDDD","url":"https://eur-lex.europa.eu/eli/dir/2024/1760"}]'::jsonb, ARRAY['Smartphone', 'Laptop', 'Monitor', 'Smartwatch'], ARRAY['Risikoanalyse', 'Due-Diligence-Bericht']),

('FR', 'textiles', 'Textilkennzeichnung Faserzusammensetzung', 'Faserzusammensetzung korrekt kennzeichnen.', 'Die EU-Textilkennzeichnungsverordnung schreibt die vollstaendige Angabe der Faserzusammensetzung vor. Fasern in absteigender Reihenfolge des Gewichtsanteils. Kennzeichnung in Landessprache erforderlich.', true, 'Kennzeichnung & Verpackung', 'critical', false, 1, 'Textilkennzeichnungsverordnung (EU) Nr. 1007/2011', 'DGCCRF', 'Amende jusqu''a 300.000 EUR et 2 ans d''emprisonnement', ARRAY['Fasern absteigend nach Gewichtsanteil', 'Vorgeschriebene Faserbezeichnungen verwenden', 'Etikett waschbestaendig', 'In Landessprache kennzeichnen'], '[{"title":"Textilkennzeichnung","url":"https://eur-lex.europa.eu/eli/reg/2011/1007"}]'::jsonb, ARRAY['T-Shirt', 'Hemd', 'Jeans', 'Kleid', 'Jacke', 'Bettwaesche'], ARRAY['Textilkennzeichnung', 'Faseranalyse']),

('FR', 'textiles', 'REACH-Konformitaet Textilien', 'REACH-Beschraenkungen fuer Textilchemikalien einhalten.', 'AZO-Farbstoffe (max. 30 mg/kg), Formaldehyd, Nickel und Flammschutzmittel muessen geprueft werden. SVHC-Kommunikation bei ueber 0,1%. SCIP-Meldepflicht besteht.', true, 'Chemikalien & Inhaltsstoffe', 'critical', true, 2, 'REACH (EG) Nr. 1907/2006', 'DGCCRF', 'Amende jusqu''a 300.000 EUR et 2 ans d''emprisonnement', ARRAY['AZO-Farbstoffe-Test durchfuehren', 'Formaldehyd-Grenzwerte beachten', 'SVHC-Kandidatenliste pruefen', 'OEKO-TEX Zertifizierung erwaegen'], '[{"title":"REACH","url":"https://eur-lex.europa.eu/eli/reg/2006/1907"}]'::jsonb, ARRAY['T-Shirt', 'Hemd', 'Jeans', 'Kinderbekleidung', 'Bettwaesche'], ARRAY['REACH-Pruefbericht', 'AZO-Analyse']),

('FR', 'textiles', 'DPP-Vorbereitung Textilien', 'Digitalen Produktpass fuer Textilien vorbereiten.', 'Der DPP fuer Textilien wird voraussichtlich ab 2027 verpflichtend. Faserzusammensetzung, Lieferkette, Umweltauswirkungen und Recyclingfaehigkeit muessen digital erfasst werden.', false, 'Digitaler Produktpass', 'medium', false, 6, 'ESPR (EU) 2024/1781', 'DGCCRF', 'Amende jusqu''a 75.000 EUR', ARRAY['Faserdaten digital erfassen', 'Lieferkette dokumentieren', 'CO2-Fussabdruck berechnen', 'QR-Code vorbereiten'], '[{"title":"ESPR","url":"https://eur-lex.europa.eu/eli/reg/2024/1781"}]'::jsonb, ARRAY['T-Shirt', 'Jeans', 'Kleid', 'Jacke', 'Schuhe'], ARRAY['DPP-Datentemplate']),

('FR', 'batteries', 'EU-Batterieverordnung Konformitaet', 'EU-Batterieverordnung umfassend einhalten.', 'Die EU-Batterieverordnung 2023/1542 stellt umfassende Anforderungen: Kennzeichnung, CO2-Fussabdruck, Recyclatanteil, Sorgfaltspflichten und digitaler Batteriepass. Stufenweise Einfuehrung ab 2024.', true, 'Produktsicherheit & CE-Konformitaet', 'critical', true, 1, 'EU-Batterieverordnung (EU) 2023/1542', 'ADEME (Agence de la transition ecologique)', 'Amende jusqu''a 300.000 EUR et 2 ans d''emprisonnement', ARRAY['Stufenplan erstellen', 'CO2-Berechnung vorbereiten', 'Recyclatanteil-Nachweis aufbauen', 'Batteriepass-Infrastruktur planen'], '[{"title":"Batterieverordnung","url":"https://eur-lex.europa.eu/eli/reg/2023/1542"}]'::jsonb, ARRAY['Li-Ion 18650', 'EV-Batteriepack', 'E-Bike-Akku', 'Smartphone-Akku'], ARRAY['EU-Konformitaetserklaerung', 'CO2-Bericht']),

('FR', 'batteries', 'Digitaler Batteriepass', 'QR-Code mit Batteriepass ab 2027.', 'Ab 18. Februar 2027 brauchen Industriebatterien und EV-Batterien einen digitalen Batteriepass ueber QR-Code. Informationen zu Kapazitaet, Leistung, CO2-Fussabdruck und Recyclingfaehigkeit.', true, 'Digitaler Produktpass', 'critical', true, 2, 'EU-Batterieverordnung (EU) 2023/1542, Art. 77', 'ADEME (Agence de la transition ecologique)', 'Amende jusqu''a 300.000 EUR et 2 ans d''emprisonnement', ARRAY['QR-Code-System implementieren', 'Datenerhebung aufbauen', 'Lebenszyklus-Tracking einrichten'], '[{"title":"Battery Pass","url":"https://thebatterypass.eu/"}]'::jsonb, ARRAY['EV-Batteriepack', 'E-Bike-Akku', 'Solarspeicher', 'Traktionsbatterie'], ARRAY['Digitaler Batteriepass', 'QR-Code-Nachweis']),

('FR', 'packaging', 'Recyclingfaehigkeit sicherstellen', 'Design-for-Recycling fuer Verpackungen umsetzen.', 'Die PPWR stellt verbindliche Anforderungen an die Recyclingfaehigkeit. Monomaterial bevorzugen, Verbundmaterialien vermeiden, Trennbarkeit sicherstellen. Recyclingfaehigkeit wird kuenftig Voraussetzung fuer das Inverkehrbringen.', true, 'Umwelt & Nachhaltigkeit', 'high', true, 3, 'PPWR (EU)', 'ADEME, Citeo', 'Amende jusqu''a 150.000 EUR', ARRAY['Monomaterial bevorzugen', 'Carbon-Black vermeiden', 'Etiketten abloesbar gestalten', 'Recyclingfaehigkeit bewerten lassen'], '[{"title":"PPWR","url":"https://eur-lex.europa.eu/legal-content/DE/TXT/?uri=CELEX:52022PC0677"}]'::jsonb, ARRAY['PET-Flasche', 'Wellpappkarton', 'Standbodenbeutel', 'Faltschachtel'], ARRAY['Recyclingfaehigkeits-Gutachten']),

('FR', 'packaging', 'DPP-Vorbereitung Verpackungen', 'DPP fuer Verpackungen vorbereiten.', 'ESPR und PPWR erfordern kuenftig DPP fuer Verpackungen mit Materialzusammensetzung, Recyclingfaehigkeit und Entsorgungshinweisen.', false, 'Digitaler Produktpass', 'medium', false, 6, 'ESPR (EU) 2024/1781, PPWR (EU)', 'ADEME, Citeo', 'Amende jusqu''a 75.000 EUR', ARRAY['Materialzusammensetzung digital erfassen', 'Recyclingfaehigkeit dokumentieren', 'QR-Code vorbereiten'], '[{"title":"ESPR","url":"https://eur-lex.europa.eu/eli/reg/2024/1781"}]'::jsonb, ARRAY['PET-Flasche', 'Wellpappkarton', 'Glasflasche', 'Standbodenbeutel'], ARRAY['DPP-Datentemplate']),

('FR', 'furniture', 'Formaldehyd-Emissionsgrenzwerte', 'Formaldehyd-Grenzwerte in Holzwerkstoffen einhalten.', 'Holzbasierte Moebel duerfen maximal E1-Emissionswerte aufweisen. Spanplatten, MDF und Sperrholz muessen geprueft werden. REACH Anhang XVII reguliert Formaldehyd.', true, 'Chemikalien & Inhaltsstoffe', 'critical', true, 1, 'REACH (EG) Nr. 1907/2006 Anhang XVII', 'DGCCRF', 'Amende jusqu''a 300.000 EUR et 2 ans d''emprisonnement', ARRAY['E1-Klassifizierung sicherstellen', 'Pruefberichte anfordern', 'Lacke und Klebstoffe pruefen'], '[{"title":"REACH","url":"https://eur-lex.europa.eu/eli/reg/2006/1907"}]'::jsonb, ARRAY['Kleiderschrank', 'Kommode', 'Esstisch', 'Schreibtisch', 'Kinderbett'], ARRAY['Formaldehyd-Pruefung', 'E1-Zertifikat']),

('FR', 'furniture', 'EUDR Holzherkunft nachweisen', 'Nachhaltige und legale Holzherkunft dokumentieren.', 'Die EUDR verlangt Entwaldungsfreiheit und legale Holzherkunft. Geolokalisierung und Sorgfaltspflichten erforderlich. FSC/PEFC erleichtern den Nachweis.', true, 'Umwelt & Nachhaltigkeit', 'high', true, 3, 'EUDR (EU) 2023/1115', 'DGCCRF', 'Amende jusqu''a 150.000 EUR', ARRAY['Sorgfaltspflicht-System aufbauen', 'Geolokalisierung dokumentieren', 'FSC/PEFC Zertifizierung erwaegen'], '[{"title":"EUDR","url":"https://eur-lex.europa.eu/eli/reg/2023/1115"}]'::jsonb, ARRAY['Esstisch', 'Kleiderschrank', 'Bücherregal', 'Kommode'], ARRAY['EUDR-Sorgfaltserklaerung', 'FSC/PEFC-Zertifikat']),

('FR', 'cosmetics', 'CPNP-Notifizierung', 'Notifizierung im CPNP vor dem Inverkehrbringen.', 'Jedes kosmetische Produkt muss im CPNP notifiziert werden. Rahmenrezeptur, Kennzeichnung und verantwortliche Person muessen angegeben werden. Aktualisierung bei Formulierungsaenderungen.', true, 'Dokumentation & Rueckverfolgbarkeit', 'critical', true, 1, 'Kosmetikverordnung (EG) Nr. 1223/2009, Art. 13', 'ANSM (Agence nationale de securite du medicament)', 'Amende jusqu''a 300.000 EUR et 2 ans d''emprisonnement', ARRAY['CPNP-Zugang beantragen', 'Rahmenrezeptur vollstaendig eingeben', 'Kennzeichnung hochladen', 'Bei Aenderungen aktualisieren'], '[{"title":"CPNP","url":"https://ec.europa.eu/growth/tools-databases/cosing/cpnp/"}]'::jsonb, ARRAY['Tagescreme', 'Shampoo', 'Duschgel', 'Lippenstift', 'Sonnencreme'], ARRAY['CPNP-Notifizierung']),

('FR', 'cosmetics', 'Sicherheitsbewertung', 'Sicherheitsbewertung durch qualifizierten Bewerter.', 'Qualifizierter Sicherheitsbewerter muss Sicherheitsbericht (Teil A und B) erstellen. Alle Inhaltsstoffe, Exposition und Gesamtsicherheit werden bewertet. Muss vor Inverkehrbringen vorliegen.', true, 'Produktsicherheit & CE-Konformitaet', 'critical', true, 2, 'Kosmetikverordnung (EG) Nr. 1223/2009, Art. 10', 'ANSM (Agence nationale de securite du medicament)', 'Amende jusqu''a 300.000 EUR et 2 ans d''emprisonnement', ARRAY['Qualifizierten Bewerter beauftragen', 'Stabilitaetsdaten bereitstellen', 'PAO festlegen lassen', 'Bei Aenderungen aktualisieren'], '[{"title":"Kosmetikverordnung","url":"https://eur-lex.europa.eu/eli/reg/2009/1223"}]'::jsonb, ARRAY['Tagescreme', 'Shampoo', 'Sonnencreme', 'Haarfarbe', 'Parfum'], ARRAY['Sicherheitsbericht']),

('FR', 'toys', 'CE-Kennzeichnung Spielzeugrichtlinie', 'CE-Kennzeichnung nach Spielzeugrichtlinie.', 'Pruefung nach EN 71 (mechanisch, entflammbar, chemisch). CE-Zeichen sichtbar und dauerhaft anbringen. Konformitaetserklaerung vor Inverkehrbringen.', true, 'Produktsicherheit & CE-Konformitaet', 'critical', true, 1, 'Spielzeugrichtlinie 2009/48/EG', 'DGCCRF', 'Amende jusqu''a 300.000 EUR et 2 ans d''emprisonnement', ARRAY['EN 71 Pruefungen durchfuehren', 'CE-Zeichen mindestens 5 mm', 'Konformitaetserklaerung erstellen', 'Technische Dokumentation aufbauen'], '[{"title":"Spielzeugrichtlinie","url":"https://eur-lex.europa.eu/eli/dir/2009/48"}]'::jsonb, ARRAY['LEGO-Set', 'Puppe', 'Plüschtier', 'Spielzeugauto', 'Puzzle'], ARRAY['EN 71 Pruefberichte', 'EU-Konformitaetserklaerung']),

('FR', 'toys', 'Chemische Sicherheit EN 71-3', 'Migration bestimmter Elemente pruefen.', 'EN 71-3 legt Grenzwerte fuer 19 Elemente fest. Drei Materialkategorien werden unterschieden. Besonders fuer lackiertes Spielzeug und mundfaehige Produkte relevant.', true, 'Chemikalien & Inhaltsstoffe', 'critical', true, 2, 'EN 71-3, Spielzeugrichtlinie 2009/48/EG', 'DGCCRF', 'Amende jusqu''a 300.000 EUR et 2 ans d''emprisonnement', ARRAY['Alle 19 Elemente pruefen', 'Drei Materialkategorien beachten', 'Lackiertes Spielzeug testen'], '[{"title":"EN 71-3","url":"https://eur-lex.europa.eu/eli/dir/2009/48"}]'::jsonb, ARRAY['Puppe', 'Spielzeugauto', 'Holzbausteine', 'Fingerfarben', 'Knetmasse'], ARRAY['EN 71-3 Pruefbericht']),

('FR', 'food', 'LMIV-Kennzeichnung', 'Lebensmittelkennzeichnung nach LMIV.', 'Pflichtkennzeichnung: Bezeichnung, Zutaten, Allergene, Nettofuellmenge, MHD, Naehrwert, Herkunft, Lebensmittelunternehmer. In Landessprache erforderlich.', true, 'Kennzeichnung & Verpackung', 'critical', true, 1, 'LMIV (EU) Nr. 1169/2011', 'DGCCRF, DGAL (Direction generale de l''alimentation)', 'Amende jusqu''a 300.000 EUR et 2 ans d''emprisonnement', ARRAY['14 Allergene hervorheben', 'Naehrwerttabelle vollstaendig', 'Mindestschriftgroesse 1,2 mm', 'In Landessprache kennzeichnen'], '[{"title":"LMIV","url":"https://eur-lex.europa.eu/eli/reg/2011/1169"}]'::jsonb, ARRAY['Milchprodukte', 'Fleisch', 'Brot', 'Gemuese', 'Tiefkuehlprodukte'], ARRAY['Kennzeichnungsentwurf', 'Naehrwertanalyse']),

('FR', 'food', 'HACCP-System', 'HACCP-Konzept implementieren.', 'Gefahrenanalyse, kritische Kontrollpunkte, Grenzwerte, Ueberwachung, Korrekturmassnahmen, Verifizierung und Dokumentation. Jaehrliche Revision.', true, 'Produktsicherheit & CE-Konformitaet', 'critical', true, 2, 'Verordnung (EG) Nr. 852/2004', 'DGCCRF, DGAL (Direction generale de l''alimentation)', 'Amende jusqu''a 300.000 EUR et 2 ans d''emprisonnement', ARRAY['Sieben HACCP-Grundsaetze anwenden', 'CCP identifizieren und ueberwachen', 'Personal schulen', 'Jaehrliche Revision'], '[{"title":"Hygieneverordnung","url":"https://eur-lex.europa.eu/eli/reg/2004/852"}]'::jsonb, ARRAY['Milch', 'Fleisch', 'Fisch', 'Brot', 'Tiefkuehlprodukte'], ARRAY['HACCP-Plan', 'Schulungsnachweise']),

('FR', 'construction', 'CE-Kennzeichnung Bauprodukte', 'CE-Kennzeichnung nach Bauproduktenverordnung.', 'Harmonisierte Bauprodukte muessen CE-Kennzeichnung mit Leistungserklaerung (DoP) tragen. Wesentliche Merkmale deklarieren. AVCP-System beachten.', true, 'Produktsicherheit & CE-Konformitaet', 'critical', true, 1, 'Bauproduktenverordnung (EU) Nr. 305/2011', 'CSTB (Centre Scientifique et Technique du Batiment)', 'Amende jusqu''a 300.000 EUR et 2 ans d''emprisonnement', ARRAY['Harmonisierte Norm identifizieren', 'DoP erstellen', 'AVCP-System beachten', 'Notifizierte Stelle einschalten'], '[{"title":"Bauproduktenverordnung","url":"https://eur-lex.europa.eu/eli/reg/2011/305"}]'::jsonb, ARRAY['Mauerziegel', 'Dachziegel', 'Daemmplatte', 'Fenster', 'Bodenfliese'], ARRAY['Leistungserklaerung (DoP)', 'Pruefberichte']),

('FR', 'construction', 'REACH Baustoffe', 'REACH-Anforderungen fuer Bauchemikalien.', 'SVHC in Klebstoffen, Dichtstoffen und Beschichtungen beachten. SCIP-Meldepflicht. Sicherheitsdatenblaetter bereithalten.', true, 'Chemikalien & Inhaltsstoffe', 'high', true, 3, 'REACH (EG) Nr. 1907/2006', 'CSTB (Centre Scientifique et Technique du Batiment)', 'Amende jusqu''a 150.000 EUR', ARRAY['SVHC-Screening fuer Bauchemie', 'SCIP-Meldung durchfuehren', 'Sicherheitsdatenblaetter bereithalten'], '[{"title":"REACH","url":"https://eur-lex.europa.eu/eli/reg/2006/1907"}]'::jsonb, ARRAY['Montagekleber', 'Silikon', 'Bauschaum', 'Wandfarbe', 'Grundierung'], ARRAY['REACH-Konformitaet', 'Sicherheitsdatenblaetter']),

('FR', 'machinery', 'Maschinenverordnung Konformitaet', 'Neue EU-Maschinenverordnung 2023/1230 einhalten.', 'Ab Januar 2027 ersetzt die neue Maschinenverordnung die Maschinenrichtlinie. Neue Anforderungen an Cybersicherheit, KI und digitale Anleitungen. Risikobeurteilung nach EN ISO 12100 zentral.', true, 'Produktsicherheit & CE-Konformitaet', 'critical', true, 1, 'Maschinenverordnung (EU) 2023/1230', 'DGCCRF, DIRECCTE', 'Amende jusqu''a 300.000 EUR et 2 ans d''emprisonnement', ARRAY['Risikobeurteilung nach EN ISO 12100', 'Cybersicherheit beachten', 'Digitale Betriebsanleitung ab 2027', 'Notifizierte Stelle fuer Anhang-I-Maschinen'], '[{"title":"Maschinenverordnung","url":"https://eur-lex.europa.eu/eli/reg/2023/1230"}]'::jsonb, ARRAY['CNC-Fraesmaschine', 'Kreissaege', 'Abkantpresse', 'Industrieroboter', 'Gabelstapler'], ARRAY['Konformitaetserklaerung', 'Risikobeurteilung']),

('FR', 'machinery', 'Betriebsanleitung in Landessprache', 'Betriebsanleitung in der Landessprache bereitstellen.', 'Betriebsanleitung muss in der Amtssprache des Verwenderlandes vorliegen. Sicherheitshinweise, bestimmungsgemaesser Gebrauch, Wartung und Entsorgung muessen enthalten sein.', true, 'Dokumentation & Rueckverfolgbarkeit', 'high', true, 2, 'Maschinenverordnung (EU) 2023/1230, Anhang III', 'DGCCRF, DIRECCTE', 'Amende jusqu''a 150.000 EUR', ARRAY['Alle Anhang-III-Inhalte aufnehmen', 'Restrisiken dokumentieren', 'Bildliche Darstellungen verwenden', 'Wartungsintervalle definieren'], '[{"title":"Maschinenverordnung","url":"https://eur-lex.europa.eu/eli/reg/2023/1230"}]'::jsonb, ARRAY['CNC-Fraesmaschine', 'Kreissaege', 'Abkantpresse', 'Gabelstapler', 'Kompressor'], ARRAY['Betriebsanleitung', 'Sicherheitsdatenblatt']);


-- ============================================
-- OESTERREICH (AT) - ALLE KATEGORIEN
-- ============================================

-- Laenderspezifische Anforderungen AT
INSERT INTO checklist_templates (country_code, category_key, title, description, detailed_description, mandatory, category, priority, document_required, sort_order, legal_basis, authority, penalties, tips, links, applicable_products, document_types) VALUES
('AT', 'electronics', 'EAG-VO Registrierung Oesterreich', 'Registrierung als Hersteller von Elektrogeraeten gemaess EAG-VO.', 'In Oesterreich muessen Hersteller und Importeure von Elektrogeraeten sich beim Elektroaltgeraete Koordinierungsstelle (EAK) registrieren. Die oesterreichische EAG-VO setzt die WEEE-Richtlinie um. Die Registrierung erfolgt ueber das EDM-Portal des Umweltbundesamtes. Geraete muessen nach Sammelkategorien klassifiziert werden.', true, 'Umwelt & Nachhaltigkeit', 'critical', true, 1, 'Elektroaltgeraeteverordnung (EAG-VO), WEEE 2012/19/EU', 'EAK, Umweltbundesamt (UBA AT)', 'Geldstrafe bis zu 50.000 EUR', ARRAY['Registrierung im EDM-Portal des UBA', 'Geraetekategorien korrekt zuordnen', 'Sammelmengen jaehrlich melden', 'Finanzierungsgarantie hinterlegen'], '[{"title":"EAK","url":"https://www.eak-austria.at/"},{"title":"EDM Portal","url":"https://edm.gv.at/"}]'::jsonb, ARRAY['Smartphone', 'Laptop', 'Fernseher', 'Drucker', 'Kopfhoerer', 'Router'], ARRAY['EAK-Registrierung', 'EDM-Meldung']),

('AT', 'packaging', 'ARA-Lizenzierung Verpackungen Oesterreich', 'Lizenzierung bei ARA (Altstoff Recycling Austria) fuer Verpackungen.', 'In Oesterreich muessen alle Erstinverkehrbringer von Verpackungen bei einem Sammel- und Verwertungssystem lizenziert sein. ARA ist der groesste Anbieter. Die Verpackungsverordnung 2014 regelt die Pflichten. Die Mengenmeldung erfolgt nach Materialfraktionen. Oesterreich hat traditionell hohe Recyclingquoten.', true, 'Umwelt & Nachhaltigkeit', 'critical', true, 1, 'Verpackungsverordnung 2014 (VerpackVO), AWG 2002', 'ARA, Umweltbundesamt (UBA AT)', 'Geldstrafe bis zu 36.000 EUR', ARRAY['ARA-Lizenzvertrag abschliessen', 'Alternativ bei Reclay oder Interseroh AT', 'Mengenmeldung nach Materialfraktionen', 'Oesterreichische Sammelsysteme beachten'], '[{"title":"ARA","url":"https://www.ara.at/"}]'::jsonb, ARRAY['Wellpappkarton', 'PET-Flasche', 'Glasflasche', 'Faltschachtel', 'Kunststoffverpackung'], ARRAY['ARA-Lizenzvertrag', 'Mengenmeldung']),

('AT', 'food', 'AGES Lebensmittelkontrolle Oesterreich', 'AGES als zentrale Behoerde fuer Lebensmittelsicherheit in Oesterreich.', 'Die AGES (Agentur fuer Gesundheit und Ernaehrungssicherheit) ist die zentrale Behoerde fuer Lebensmittelsicherheit in Oesterreich. Sie fuehrt Kontrollen durch, analysiert Lebensmittelproben und bewertet Risiken. Die Lebensmittelaufsicht wird von den Laendern durchgefuehrt, die AGES koordiniert die Massnahmen auf Bundesebene.', true, 'Produktsicherheit & CE-Konformitaet', 'high', true, 1, 'LMSVG (Lebensmittelsicherheits- und Verbraucherschutzgesetz), LMIV (EU) 1169/2011', 'AGES, Lebensmittelaufsicht der Laender', 'Geldstrafe bis zu 50.000 EUR', ARRAY['LMSVG-Anforderungen beachten', 'Eigenkontrollen nach HACCP dokumentieren', 'Oesterreichisches Lebensmittelbuch (Codex Alimentarius Austriacus) beachten', 'AGES-Leitlinien verfolgen'], '[{"title":"AGES","url":"https://www.ages.at/"}]'::jsonb, ARRAY['Frischmilch', 'Kaese', 'Wurst', 'Schokolade', 'Brot'], ARRAY['HACCP-Dokumentation', 'Analysezertifikate']);

-- EU-weite Anforderungen fuer AT
INSERT INTO checklist_templates (country_code, category_key, title, description, detailed_description, mandatory, category, priority, document_required, sort_order, legal_basis, authority, penalties, tips, links, applicable_products, document_types) VALUES
('AT', 'electronics', 'CE-Kennzeichnung pruefen', 'CE-Zeichen korrekt und sichtbar anbringen.', 'Die CE-Kennzeichnung ist fuer alle Elektronikprodukte im EU-Binnenmarkt verpflichtend. Sie muss mindestens 5 mm hoch, sichtbar und dauerhaft angebracht sein. Der Hersteller uebernimmt die Verantwortung fuer die EU-Konformitaet. Die Kennzeichnung muss vor dem Inverkehrbringen angebracht werden.', true, 'Produktsicherheit & CE-Konformitaet', 'critical', true, 1, 'Verordnung (EU) 2023/988 (GPSR)', 'AUVA (Allgemeine Unfallversicherungsanstalt), Elektroaltgeraete Koordinierungsstelle (EAK)', 'Geldstrafe bis zu 50.000 EUR', ARRAY['CE-Zeichen mindestens 5 mm hoch', 'Auf Produkt, nicht nur Verpackung', 'Konformitaetserklaerung erstellen', 'Technische Dokumentation bereithalten'], '[{"title":"GPSR","url":"https://eur-lex.europa.eu/eli/reg/2023/988"}]'::jsonb, ARRAY['Smartphone', 'Laptop', 'Fernseher', 'Monitor', 'Router', 'Drucker'], ARRAY['EU-Konformitaetserklaerung', 'Technische Dokumentation']),

('AT', 'electronics', 'RoHS-Konformitaet sicherstellen', 'Beschraenkung gefaehrlicher Stoffe einhalten.', 'Die RoHS-Richtlinie beschraenkt die Verwendung gefaehrlicher Stoffe in Elektrogeraeten. Grenzwerte: 0,1% fuer Blei, Quecksilber, Cr VI, PBB, PBDE, DEHP, BBP, DBP; 0,01% fuer Cadmium. Materialanalysen und Lieferantenerklaerungen sind erforderlich. Die Dokumentation muss 10 Jahre aufbewahrt werden.', true, 'Chemikalien & Inhaltsstoffe', 'high', true, 2, 'RoHS 2011/65/EU, Delegierte Richtlinie (EU) 2015/863', 'AUVA (Allgemeine Unfallversicherungsanstalt), Elektroaltgeraete Koordinierungsstelle (EAK)', 'Geldstrafe bis zu 36.000 EUR', ARRAY['XRF-Screening fuer Stichproben', 'Lieferantenerklaerungen einholen', 'DEHP, BBP, DBP und DIBP beachten', 'Dokumentation 10 Jahre aufbewahren'], '[{"title":"RoHS-Richtlinie","url":"https://eur-lex.europa.eu/eli/dir/2011/65"}]'::jsonb, ARRAY['Smartphone', 'Laptop', 'Monitor', 'Drucker', 'Router'], ARRAY['RoHS-Konformitaetserklaerung', 'Materialanalysen']),

('AT', 'electronics', 'REACH-SVHC-Pruefung', 'Besonders besorgniserregende Stoffe (SVHC) pruefen.', 'REACH verpflichtet zur Information ueber SVHC-Stoffe ueber 0,1%. Die SVHC-Kandidatenliste wird halbjaehrlich aktualisiert. SCIP-Meldepflicht an ECHA besteht. Fuer Elektronik sind Weichmacher, Flammschutzmittel und Loetlegierungen besonders relevant.', true, 'Chemikalien & Inhaltsstoffe', 'high', true, 3, 'REACH (EG) Nr. 1907/2006', 'AUVA (Allgemeine Unfallversicherungsanstalt), Elektroaltgeraete Koordinierungsstelle (EAK)', 'Geldstrafe bis zu 36.000 EUR', ARRAY['SVHC-Kandidatenliste halbjaehrlich pruefen', 'SCIP-Meldung durchfuehren', 'Lieferantenbefragungen dokumentieren'], '[{"title":"REACH","url":"https://eur-lex.europa.eu/eli/reg/2006/1907"},{"title":"ECHA SVHC","url":"https://echa.europa.eu/candidate-list-table"}]'::jsonb, ARRAY['Smartphone', 'Laptop', 'Kopfhoerer', 'Kabel', 'Ladegeraet'], ARRAY['SVHC-Pruefbericht', 'SCIP-Meldung']),

('AT', 'electronics', 'DPP-Vorbereitung Elektronik', 'Vorbereitung auf den digitalen Produktpass.', 'Die ESPR fuehrt den digitalen Produktpass fuer Elektronik voraussichtlich ab 2028 ein. Der DPP muss Materialzusammensetzung, Reparierbarkeit, Recyclingfaehigkeit und CO2-Fussabdruck enthalten. Fruehzeitige Vorbereitung verschafft Wettbewerbsvorteile.', false, 'Digitaler Produktpass', 'medium', false, 8, 'ESPR (EU) 2024/1781', 'AUVA (Allgemeine Unfallversicherungsanstalt), Elektroaltgeraete Koordinierungsstelle (EAK)', 'Geldstrafe bis zu 25.000 EUR', ARRAY['Produktdaten digital erfassen', 'CO2-Fussabdruck berechnen', 'QR-Code-Infrastruktur aufbauen', 'Lieferkettendaten verfuegbar machen'], '[{"title":"ESPR","url":"https://eur-lex.europa.eu/eli/reg/2024/1781"}]'::jsonb, ARRAY['Smartphone', 'Laptop', 'Monitor', 'Fernseher', 'Drucker'], ARRAY['DPP-Datentemplate']),

('AT', 'electronics', 'GPSR Wirtschaftsakteur-Pflichten', 'Pflichten als Wirtschaftsakteur nach GPSR erfuellen.', 'Die neue GPSR definiert Pflichten fuer Hersteller, Importeure und Haendler. Risikoanalyse, technische Dokumentation und Identifikation des verantwortlichen Wirtschaftsakteurs sind Pflicht. Bei Online-Verkauf gelten erweiterte Informationspflichten.', true, 'Dokumentation & Rueckverfolgbarkeit', 'high', true, 5, 'GPSR (EU) 2023/988', 'AUVA (Allgemeine Unfallversicherungsanstalt), Elektroaltgeraete Koordinierungsstelle (EAK)', 'Geldstrafe bis zu 36.000 EUR', ARRAY['Eigene Rolle als Wirtschaftsakteur bestimmen', 'Kontaktdaten auf Produkt/Verpackung', 'Risikoanalyse dokumentieren', 'Rueckrufprozesse definieren'], '[{"title":"GPSR","url":"https://eur-lex.europa.eu/eli/reg/2023/988"}]'::jsonb, ARRAY['Smartphone', 'Laptop', 'Monitor', 'Router', 'Ladegeraet'], ARRAY['Risikoanalyse', 'Technische Dokumentation']),

('AT', 'electronics', 'Lieferkettensorgfalt Elektronik', 'Sorgfaltspflichten in der Elektronik-Lieferkette.', 'Die CSDDD verpflichtet zu Sorgfaltspflichten bezueglich Menschenrechte und Umwelt. Fuer Elektronik sind Konfliktmineralien und Arbeitsbedingungen besonders relevant. Risikoanalyse und Praeventionsmassnahmen sind erforderlich.', true, 'Soziale Verantwortung & Lieferkette', 'medium', true, 7, 'CSDDD (EU) 2024/1760', 'AUVA (Allgemeine Unfallversicherungsanstalt), Elektroaltgeraete Koordinierungsstelle (EAK)', 'Geldstrafe bis zu 25.000 EUR', ARRAY['Risikoanalyse fuer Zulieferer', 'Konfliktmineralien nachverfolgen', 'Beschwerdemechanismus einrichten'], '[{"title":"CSDDD","url":"https://eur-lex.europa.eu/eli/dir/2024/1760"}]'::jsonb, ARRAY['Smartphone', 'Laptop', 'Monitor', 'Smartwatch'], ARRAY['Risikoanalyse', 'Due-Diligence-Bericht']),

('AT', 'textiles', 'Textilkennzeichnung Faserzusammensetzung', 'Faserzusammensetzung korrekt kennzeichnen.', 'Die EU-Textilkennzeichnungsverordnung schreibt die vollstaendige Angabe der Faserzusammensetzung vor. Fasern in absteigender Reihenfolge des Gewichtsanteils. Kennzeichnung in Landessprache erforderlich.', true, 'Kennzeichnung & Verpackung', 'critical', false, 1, 'Textilkennzeichnungsverordnung (EU) Nr. 1007/2011', 'AGES (Agentur fuer Gesundheit und Ernaehrungssicherheit)', 'Geldstrafe bis zu 50.000 EUR', ARRAY['Fasern absteigend nach Gewichtsanteil', 'Vorgeschriebene Faserbezeichnungen verwenden', 'Etikett waschbestaendig', 'In Landessprache kennzeichnen'], '[{"title":"Textilkennzeichnung","url":"https://eur-lex.europa.eu/eli/reg/2011/1007"}]'::jsonb, ARRAY['T-Shirt', 'Hemd', 'Jeans', 'Kleid', 'Jacke', 'Bettwaesche'], ARRAY['Textilkennzeichnung', 'Faseranalyse']),

('AT', 'textiles', 'REACH-Konformitaet Textilien', 'REACH-Beschraenkungen fuer Textilchemikalien einhalten.', 'AZO-Farbstoffe (max. 30 mg/kg), Formaldehyd, Nickel und Flammschutzmittel muessen geprueft werden. SVHC-Kommunikation bei ueber 0,1%. SCIP-Meldepflicht besteht.', true, 'Chemikalien & Inhaltsstoffe', 'critical', true, 2, 'REACH (EG) Nr. 1907/2006', 'AGES (Agentur fuer Gesundheit und Ernaehrungssicherheit)', 'Geldstrafe bis zu 50.000 EUR', ARRAY['AZO-Farbstoffe-Test durchfuehren', 'Formaldehyd-Grenzwerte beachten', 'SVHC-Kandidatenliste pruefen', 'OEKO-TEX Zertifizierung erwaegen'], '[{"title":"REACH","url":"https://eur-lex.europa.eu/eli/reg/2006/1907"}]'::jsonb, ARRAY['T-Shirt', 'Hemd', 'Jeans', 'Kinderbekleidung', 'Bettwaesche'], ARRAY['REACH-Pruefbericht', 'AZO-Analyse']),

('AT', 'textiles', 'DPP-Vorbereitung Textilien', 'Digitalen Produktpass fuer Textilien vorbereiten.', 'Der DPP fuer Textilien wird voraussichtlich ab 2027 verpflichtend. Faserzusammensetzung, Lieferkette, Umweltauswirkungen und Recyclingfaehigkeit muessen digital erfasst werden.', false, 'Digitaler Produktpass', 'medium', false, 6, 'ESPR (EU) 2024/1781', 'AGES (Agentur fuer Gesundheit und Ernaehrungssicherheit)', 'Geldstrafe bis zu 25.000 EUR', ARRAY['Faserdaten digital erfassen', 'Lieferkette dokumentieren', 'CO2-Fussabdruck berechnen', 'QR-Code vorbereiten'], '[{"title":"ESPR","url":"https://eur-lex.europa.eu/eli/reg/2024/1781"}]'::jsonb, ARRAY['T-Shirt', 'Jeans', 'Kleid', 'Jacke', 'Schuhe'], ARRAY['DPP-Datentemplate']),

('AT', 'batteries', 'EU-Batterieverordnung Konformitaet', 'EU-Batterieverordnung umfassend einhalten.', 'Die EU-Batterieverordnung 2023/1542 stellt umfassende Anforderungen: Kennzeichnung, CO2-Fussabdruck, Recyclatanteil, Sorgfaltspflichten und digitaler Batteriepass. Stufenweise Einfuehrung ab 2024.', true, 'Produktsicherheit & CE-Konformitaet', 'critical', true, 1, 'EU-Batterieverordnung (EU) 2023/1542', 'Umweltbundesamt (UBA AT), ERA (Elektroaltgeraete Koordinierungsstelle)', 'Geldstrafe bis zu 50.000 EUR', ARRAY['Stufenplan erstellen', 'CO2-Berechnung vorbereiten', 'Recyclatanteil-Nachweis aufbauen', 'Batteriepass-Infrastruktur planen'], '[{"title":"Batterieverordnung","url":"https://eur-lex.europa.eu/eli/reg/2023/1542"}]'::jsonb, ARRAY['Li-Ion 18650', 'EV-Batteriepack', 'E-Bike-Akku', 'Smartphone-Akku'], ARRAY['EU-Konformitaetserklaerung', 'CO2-Bericht']),

('AT', 'batteries', 'Digitaler Batteriepass', 'QR-Code mit Batteriepass ab 2027.', 'Ab 18. Februar 2027 brauchen Industriebatterien und EV-Batterien einen digitalen Batteriepass ueber QR-Code. Informationen zu Kapazitaet, Leistung, CO2-Fussabdruck und Recyclingfaehigkeit.', true, 'Digitaler Produktpass', 'critical', true, 2, 'EU-Batterieverordnung (EU) 2023/1542, Art. 77', 'Umweltbundesamt (UBA AT), ERA (Elektroaltgeraete Koordinierungsstelle)', 'Geldstrafe bis zu 50.000 EUR', ARRAY['QR-Code-System implementieren', 'Datenerhebung aufbauen', 'Lebenszyklus-Tracking einrichten'], '[{"title":"Battery Pass","url":"https://thebatterypass.eu/"}]'::jsonb, ARRAY['EV-Batteriepack', 'E-Bike-Akku', 'Solarspeicher', 'Traktionsbatterie'], ARRAY['Digitaler Batteriepass', 'QR-Code-Nachweis']),

('AT', 'packaging', 'Recyclingfaehigkeit sicherstellen', 'Design-for-Recycling fuer Verpackungen umsetzen.', 'Die PPWR stellt verbindliche Anforderungen an die Recyclingfaehigkeit. Monomaterial bevorzugen, Verbundmaterialien vermeiden, Trennbarkeit sicherstellen. Recyclingfaehigkeit wird kuenftig Voraussetzung fuer das Inverkehrbringen.', true, 'Umwelt & Nachhaltigkeit', 'high', true, 3, 'PPWR (EU)', 'ARA (Altstoff Recycling Austria)', 'Geldstrafe bis zu 36.000 EUR', ARRAY['Monomaterial bevorzugen', 'Carbon-Black vermeiden', 'Etiketten abloesbar gestalten', 'Recyclingfaehigkeit bewerten lassen'], '[{"title":"PPWR","url":"https://eur-lex.europa.eu/legal-content/DE/TXT/?uri=CELEX:52022PC0677"}]'::jsonb, ARRAY['PET-Flasche', 'Wellpappkarton', 'Standbodenbeutel', 'Faltschachtel'], ARRAY['Recyclingfaehigkeits-Gutachten']),

('AT', 'packaging', 'DPP-Vorbereitung Verpackungen', 'DPP fuer Verpackungen vorbereiten.', 'ESPR und PPWR erfordern kuenftig DPP fuer Verpackungen mit Materialzusammensetzung, Recyclingfaehigkeit und Entsorgungshinweisen.', false, 'Digitaler Produktpass', 'medium', false, 6, 'ESPR (EU) 2024/1781, PPWR (EU)', 'ARA (Altstoff Recycling Austria)', 'Geldstrafe bis zu 25.000 EUR', ARRAY['Materialzusammensetzung digital erfassen', 'Recyclingfaehigkeit dokumentieren', 'QR-Code vorbereiten'], '[{"title":"ESPR","url":"https://eur-lex.europa.eu/eli/reg/2024/1781"}]'::jsonb, ARRAY['PET-Flasche', 'Wellpappkarton', 'Glasflasche', 'Standbodenbeutel'], ARRAY['DPP-Datentemplate']),

('AT', 'furniture', 'Formaldehyd-Emissionsgrenzwerte', 'Formaldehyd-Grenzwerte in Holzwerkstoffen einhalten.', 'Holzbasierte Moebel duerfen maximal E1-Emissionswerte aufweisen. Spanplatten, MDF und Sperrholz muessen geprueft werden. REACH Anhang XVII reguliert Formaldehyd.', true, 'Chemikalien & Inhaltsstoffe', 'critical', true, 1, 'REACH (EG) Nr. 1907/2006 Anhang XVII', 'AUVA, Landesbehoerden', 'Geldstrafe bis zu 50.000 EUR', ARRAY['E1-Klassifizierung sicherstellen', 'Pruefberichte anfordern', 'Lacke und Klebstoffe pruefen'], '[{"title":"REACH","url":"https://eur-lex.europa.eu/eli/reg/2006/1907"}]'::jsonb, ARRAY['Kleiderschrank', 'Kommode', 'Esstisch', 'Schreibtisch', 'Kinderbett'], ARRAY['Formaldehyd-Pruefung', 'E1-Zertifikat']),

('AT', 'furniture', 'EUDR Holzherkunft nachweisen', 'Nachhaltige und legale Holzherkunft dokumentieren.', 'Die EUDR verlangt Entwaldungsfreiheit und legale Holzherkunft. Geolokalisierung und Sorgfaltspflichten erforderlich. FSC/PEFC erleichtern den Nachweis.', true, 'Umwelt & Nachhaltigkeit', 'high', true, 3, 'EUDR (EU) 2023/1115', 'AUVA, Landesbehoerden', 'Geldstrafe bis zu 36.000 EUR', ARRAY['Sorgfaltspflicht-System aufbauen', 'Geolokalisierung dokumentieren', 'FSC/PEFC Zertifizierung erwaegen'], '[{"title":"EUDR","url":"https://eur-lex.europa.eu/eli/reg/2023/1115"}]'::jsonb, ARRAY['Esstisch', 'Kleiderschrank', 'Bücherregal', 'Kommode'], ARRAY['EUDR-Sorgfaltserklaerung', 'FSC/PEFC-Zertifikat']),

('AT', 'cosmetics', 'CPNP-Notifizierung', 'Notifizierung im CPNP vor dem Inverkehrbringen.', 'Jedes kosmetische Produkt muss im CPNP notifiziert werden. Rahmenrezeptur, Kennzeichnung und verantwortliche Person muessen angegeben werden. Aktualisierung bei Formulierungsaenderungen.', true, 'Dokumentation & Rueckverfolgbarkeit', 'critical', true, 1, 'Kosmetikverordnung (EG) Nr. 1223/2009, Art. 13', 'AGES, BASG (Bundesamt fuer Sicherheit im Gesundheitswesen)', 'Geldstrafe bis zu 50.000 EUR', ARRAY['CPNP-Zugang beantragen', 'Rahmenrezeptur vollstaendig eingeben', 'Kennzeichnung hochladen', 'Bei Aenderungen aktualisieren'], '[{"title":"CPNP","url":"https://ec.europa.eu/growth/tools-databases/cosing/cpnp/"}]'::jsonb, ARRAY['Tagescreme', 'Shampoo', 'Duschgel', 'Lippenstift', 'Sonnencreme'], ARRAY['CPNP-Notifizierung']),

('AT', 'cosmetics', 'Sicherheitsbewertung', 'Sicherheitsbewertung durch qualifizierten Bewerter.', 'Qualifizierter Sicherheitsbewerter muss Sicherheitsbericht (Teil A und B) erstellen. Alle Inhaltsstoffe, Exposition und Gesamtsicherheit werden bewertet. Muss vor Inverkehrbringen vorliegen.', true, 'Produktsicherheit & CE-Konformitaet', 'critical', true, 2, 'Kosmetikverordnung (EG) Nr. 1223/2009, Art. 10', 'AGES, BASG (Bundesamt fuer Sicherheit im Gesundheitswesen)', 'Geldstrafe bis zu 50.000 EUR', ARRAY['Qualifizierten Bewerter beauftragen', 'Stabilitaetsdaten bereitstellen', 'PAO festlegen lassen', 'Bei Aenderungen aktualisieren'], '[{"title":"Kosmetikverordnung","url":"https://eur-lex.europa.eu/eli/reg/2009/1223"}]'::jsonb, ARRAY['Tagescreme', 'Shampoo', 'Sonnencreme', 'Haarfarbe', 'Parfum'], ARRAY['Sicherheitsbericht']),

('AT', 'toys', 'CE-Kennzeichnung Spielzeugrichtlinie', 'CE-Kennzeichnung nach Spielzeugrichtlinie.', 'Pruefung nach EN 71 (mechanisch, entflammbar, chemisch). CE-Zeichen sichtbar und dauerhaft anbringen. Konformitaetserklaerung vor Inverkehrbringen.', true, 'Produktsicherheit & CE-Konformitaet', 'critical', true, 1, 'Spielzeugrichtlinie 2009/48/EG', 'AUVA, Landesbehoerden', 'Geldstrafe bis zu 50.000 EUR', ARRAY['EN 71 Pruefungen durchfuehren', 'CE-Zeichen mindestens 5 mm', 'Konformitaetserklaerung erstellen', 'Technische Dokumentation aufbauen'], '[{"title":"Spielzeugrichtlinie","url":"https://eur-lex.europa.eu/eli/dir/2009/48"}]'::jsonb, ARRAY['LEGO-Set', 'Puppe', 'Plüschtier', 'Spielzeugauto', 'Puzzle'], ARRAY['EN 71 Pruefberichte', 'EU-Konformitaetserklaerung']),

('AT', 'toys', 'Chemische Sicherheit EN 71-3', 'Migration bestimmter Elemente pruefen.', 'EN 71-3 legt Grenzwerte fuer 19 Elemente fest. Drei Materialkategorien werden unterschieden. Besonders fuer lackiertes Spielzeug und mundfaehige Produkte relevant.', true, 'Chemikalien & Inhaltsstoffe', 'critical', true, 2, 'EN 71-3, Spielzeugrichtlinie 2009/48/EG', 'AUVA, Landesbehoerden', 'Geldstrafe bis zu 50.000 EUR', ARRAY['Alle 19 Elemente pruefen', 'Drei Materialkategorien beachten', 'Lackiertes Spielzeug testen'], '[{"title":"EN 71-3","url":"https://eur-lex.europa.eu/eli/dir/2009/48"}]'::jsonb, ARRAY['Puppe', 'Spielzeugauto', 'Holzbausteine', 'Fingerfarben', 'Knetmasse'], ARRAY['EN 71-3 Pruefbericht']),

('AT', 'food', 'LMIV-Kennzeichnung', 'Lebensmittelkennzeichnung nach LMIV.', 'Pflichtkennzeichnung: Bezeichnung, Zutaten, Allergene, Nettofuellmenge, MHD, Naehrwert, Herkunft, Lebensmittelunternehmer. In Landessprache erforderlich.', true, 'Kennzeichnung & Verpackung', 'critical', true, 1, 'LMIV (EU) Nr. 1169/2011', 'AGES, Lebensmittelaufsicht der Bundeslaender', 'Geldstrafe bis zu 50.000 EUR', ARRAY['14 Allergene hervorheben', 'Naehrwerttabelle vollstaendig', 'Mindestschriftgroesse 1,2 mm', 'In Landessprache kennzeichnen'], '[{"title":"LMIV","url":"https://eur-lex.europa.eu/eli/reg/2011/1169"}]'::jsonb, ARRAY['Milchprodukte', 'Fleisch', 'Brot', 'Gemuese', 'Tiefkuehlprodukte'], ARRAY['Kennzeichnungsentwurf', 'Naehrwertanalyse']),

('AT', 'food', 'HACCP-System', 'HACCP-Konzept implementieren.', 'Gefahrenanalyse, kritische Kontrollpunkte, Grenzwerte, Ueberwachung, Korrekturmassnahmen, Verifizierung und Dokumentation. Jaehrliche Revision.', true, 'Produktsicherheit & CE-Konformitaet', 'critical', true, 2, 'Verordnung (EG) Nr. 852/2004', 'AGES, Lebensmittelaufsicht der Bundeslaender', 'Geldstrafe bis zu 50.000 EUR', ARRAY['Sieben HACCP-Grundsaetze anwenden', 'CCP identifizieren und ueberwachen', 'Personal schulen', 'Jaehrliche Revision'], '[{"title":"Hygieneverordnung","url":"https://eur-lex.europa.eu/eli/reg/2004/852"}]'::jsonb, ARRAY['Milch', 'Fleisch', 'Fisch', 'Brot', 'Tiefkuehlprodukte'], ARRAY['HACCP-Plan', 'Schulungsnachweise']),

('AT', 'construction', 'CE-Kennzeichnung Bauprodukte', 'CE-Kennzeichnung nach Bauproduktenverordnung.', 'Harmonisierte Bauprodukte muessen CE-Kennzeichnung mit Leistungserklaerung (DoP) tragen. Wesentliche Merkmale deklarieren. AVCP-System beachten.', true, 'Produktsicherheit & CE-Konformitaet', 'critical', true, 1, 'Bauproduktenverordnung (EU) Nr. 305/2011', 'OIB (Oesterreichisches Institut fuer Bautechnik)', 'Geldstrafe bis zu 50.000 EUR', ARRAY['Harmonisierte Norm identifizieren', 'DoP erstellen', 'AVCP-System beachten', 'Notifizierte Stelle einschalten'], '[{"title":"Bauproduktenverordnung","url":"https://eur-lex.europa.eu/eli/reg/2011/305"}]'::jsonb, ARRAY['Mauerziegel', 'Dachziegel', 'Daemmplatte', 'Fenster', 'Bodenfliese'], ARRAY['Leistungserklaerung (DoP)', 'Pruefberichte']),

('AT', 'construction', 'REACH Baustoffe', 'REACH-Anforderungen fuer Bauchemikalien.', 'SVHC in Klebstoffen, Dichtstoffen und Beschichtungen beachten. SCIP-Meldepflicht. Sicherheitsdatenblaetter bereithalten.', true, 'Chemikalien & Inhaltsstoffe', 'high', true, 3, 'REACH (EG) Nr. 1907/2006', 'OIB (Oesterreichisches Institut fuer Bautechnik)', 'Geldstrafe bis zu 36.000 EUR', ARRAY['SVHC-Screening fuer Bauchemie', 'SCIP-Meldung durchfuehren', 'Sicherheitsdatenblaetter bereithalten'], '[{"title":"REACH","url":"https://eur-lex.europa.eu/eli/reg/2006/1907"}]'::jsonb, ARRAY['Montagekleber', 'Silikon', 'Bauschaum', 'Wandfarbe', 'Grundierung'], ARRAY['REACH-Konformitaet', 'Sicherheitsdatenblaetter']),

('AT', 'machinery', 'Maschinenverordnung Konformitaet', 'Neue EU-Maschinenverordnung 2023/1230 einhalten.', 'Ab Januar 2027 ersetzt die neue Maschinenverordnung die Maschinenrichtlinie. Neue Anforderungen an Cybersicherheit, KI und digitale Anleitungen. Risikobeurteilung nach EN ISO 12100 zentral.', true, 'Produktsicherheit & CE-Konformitaet', 'critical', true, 1, 'Maschinenverordnung (EU) 2023/1230', 'AUVA, Arbeitsinspektorat', 'Geldstrafe bis zu 50.000 EUR', ARRAY['Risikobeurteilung nach EN ISO 12100', 'Cybersicherheit beachten', 'Digitale Betriebsanleitung ab 2027', 'Notifizierte Stelle fuer Anhang-I-Maschinen'], '[{"title":"Maschinenverordnung","url":"https://eur-lex.europa.eu/eli/reg/2023/1230"}]'::jsonb, ARRAY['CNC-Fraesmaschine', 'Kreissaege', 'Abkantpresse', 'Industrieroboter', 'Gabelstapler'], ARRAY['Konformitaetserklaerung', 'Risikobeurteilung']),

('AT', 'machinery', 'Betriebsanleitung in Landessprache', 'Betriebsanleitung in der Landessprache bereitstellen.', 'Betriebsanleitung muss in der Amtssprache des Verwenderlandes vorliegen. Sicherheitshinweise, bestimmungsgemaesser Gebrauch, Wartung und Entsorgung muessen enthalten sein.', true, 'Dokumentation & Rueckverfolgbarkeit', 'high', true, 2, 'Maschinenverordnung (EU) 2023/1230, Anhang III', 'AUVA, Arbeitsinspektorat', 'Geldstrafe bis zu 36.000 EUR', ARRAY['Alle Anhang-III-Inhalte aufnehmen', 'Restrisiken dokumentieren', 'Bildliche Darstellungen verwenden', 'Wartungsintervalle definieren'], '[{"title":"Maschinenverordnung","url":"https://eur-lex.europa.eu/eli/reg/2023/1230"}]'::jsonb, ARRAY['CNC-Fraesmaschine', 'Kreissaege', 'Abkantpresse', 'Gabelstapler', 'Kompressor'], ARRAY['Betriebsanleitung', 'Sicherheitsdatenblatt']);


-- ============================================
-- ITALIEN (IT) - ALLE KATEGORIEN
-- ============================================

-- Laenderspezifische Anforderungen IT
INSERT INTO checklist_templates (country_code, category_key, title, description, detailed_description, mandatory, category, priority, document_required, sort_order, legal_basis, authority, penalties, tips, links, applicable_products, document_types) VALUES
('IT', 'electronics', 'RAEE-Registrierung Italien', 'Registrierung im italienischen RAEE-System fuer Elektroaltgeraete.', 'In Italien muessen Hersteller und Importeure von Elektrogeraeten sich im nationalen RAEE-Register registrieren. Die Registrierung erfolgt beim Centro di Coordinamento RAEE. Italien hat ein System kollektiver Verantwortung mit mehreren Konsortien (Ecodom, Remedia, etc.). Die Registrierung umfasst Geraetekategorien, Mengen und Finanzgarantien.', true, 'Umwelt & Nachhaltigkeit', 'critical', true, 1, 'D.Lgs. 49/2014, WEEE 2012/19/EU', 'Centro di Coordinamento RAEE, MISE', 'Sanzione fino a 100.000 EUR', ARRAY['Beim Centro di Coordinamento RAEE registrieren', 'Konsortium waehlen (Ecodom, Remedia, etc.)', 'Jahresmeldungen fristgerecht einreichen', 'Visible Fee auf Rechnungen beachten'], '[{"title":"RAEE Register","url":"https://www.cdcraee.it/"}]'::jsonb, ARRAY['Smartphone', 'Laptop', 'Fernseher', 'Drucker', 'Kopfhoerer'], ARRAY['RAEE-Registrierung', 'Konsortium-Mitgliedschaft']),

('IT', 'packaging', 'CONAI-Registrierung Italien', 'Pflichtregistrierung bei CONAI fuer Verpackungen in Italien.', 'In Italien muessen alle Unternehmen, die Verpackungen herstellen oder verwenden, CONAI-Mitglied sein. Der Umweltbeitrag (Contributo Ambientale CONAI - CAC) wird nach Materialart berechnet. Seit 2023 gilt die erweiterte Umweltkennzeichnungspflicht fuer alle Verpackungen (Decreto legislativo 116/2020). Die Kennzeichnung muss Materialart und Entsorgungshinweise enthalten.', true, 'Umwelt & Nachhaltigkeit', 'critical', true, 1, 'D.Lgs. 116/2020, D.Lgs. 152/2006 (Codice dell''Ambiente)', 'CONAI, ISPRA', 'Sanzione fino a 100.000 EUR', ARRAY['CONAI-Mitgliedschaft beantragen', 'Umweltbeitrag CAC berechnen und zahlen', 'Verpackungskennzeichnung nach D.Lgs. 116/2020', 'Materialkodierung nach UNI EN Normen'], '[{"title":"CONAI","url":"https://www.conai.org/"}]'::jsonb, ARRAY['Wellpappkarton', 'PET-Flasche', 'Glasflasche', 'Getränkedose', 'Kunststoffverpackung'], ARRAY['CONAI-Mitgliedschaft', 'CAC-Beitragsnachweis']),

('IT', 'food', 'Italienische Herkunftskennzeichnung Lebensmittel', 'Erweiterte Herkunftskennzeichnung fuer bestimmte Lebensmittel in Italien.', 'Italien hat strengere Herkunftskennzeichnungsanforderungen als die EU vorschreibt. Fuer bestimmte Produkte wie Pasta, Reis, Tomaten und Milchprodukte muss das Herkunftsland der Rohstoffe angegeben werden. Die Kennzeichnung muss den Ursprung des primaeren Rohstoffs und den Verarbeitungsort umfassen.', true, 'Kennzeichnung & Verpackung', 'high', false, 1, 'Decreto legislativo 145/2017, LMIV (EU) 1169/2011', 'ICQRF (Ispettorato Centrale Qualita), NAS', 'Sanzione fino a 50.000 EUR', ARRAY['Herkunft primaerer Rohstoffe angeben', 'Made in Italy nur bei tatsaechlicher Herstellung', 'DOC/DOP/IGP Bezeichnungen korrekt verwenden', 'Italienisches Lebensmittelrecht beachten'], '[{"title":"ICQRF","url":"https://www.politicheagricole.it/flex/cm/pages/ServeBLOB.php/L/IT/IDPagina/4043"}]'::jsonb, ARRAY['Pasta', 'Olivenoel', 'Reis', 'Tomaten', 'Kaese', 'Schinken'], ARRAY['Herkunftsnachweis', 'Lieferkettennachweis']);

-- EU-weite Anforderungen fuer IT
INSERT INTO checklist_templates (country_code, category_key, title, description, detailed_description, mandatory, category, priority, document_required, sort_order, legal_basis, authority, penalties, tips, links, applicable_products, document_types) VALUES
('IT', 'electronics', 'CE-Kennzeichnung pruefen', 'CE-Zeichen korrekt und sichtbar anbringen.', 'Die CE-Kennzeichnung ist fuer alle Elektronikprodukte im EU-Binnenmarkt verpflichtend. Sie muss mindestens 5 mm hoch, sichtbar und dauerhaft angebracht sein. Der Hersteller uebernimmt die Verantwortung fuer die EU-Konformitaet. Die Kennzeichnung muss vor dem Inverkehrbringen angebracht werden.', true, 'Produktsicherheit & CE-Konformitaet', 'critical', true, 1, 'Verordnung (EU) 2023/988 (GPSR)', 'MISE (Ministero delle Imprese e del Made in Italy)', 'Sanzione fino a 150.000 EUR', ARRAY['CE-Zeichen mindestens 5 mm hoch', 'Auf Produkt, nicht nur Verpackung', 'Konformitaetserklaerung erstellen', 'Technische Dokumentation bereithalten'], '[{"title":"GPSR","url":"https://eur-lex.europa.eu/eli/reg/2023/988"}]'::jsonb, ARRAY['Smartphone', 'Laptop', 'Fernseher', 'Monitor', 'Router', 'Drucker'], ARRAY['EU-Konformitaetserklaerung', 'Technische Dokumentation']),

('IT', 'electronics', 'RoHS-Konformitaet sicherstellen', 'Beschraenkung gefaehrlicher Stoffe einhalten.', 'Die RoHS-Richtlinie beschraenkt die Verwendung gefaehrlicher Stoffe in Elektrogeraeten. Grenzwerte: 0,1% fuer Blei, Quecksilber, Cr VI, PBB, PBDE, DEHP, BBP, DBP; 0,01% fuer Cadmium. Materialanalysen und Lieferantenerklaerungen sind erforderlich. Die Dokumentation muss 10 Jahre aufbewahrt werden.', true, 'Chemikalien & Inhaltsstoffe', 'high', true, 2, 'RoHS 2011/65/EU, Delegierte Richtlinie (EU) 2015/863', 'MISE (Ministero delle Imprese e del Made in Italy)', 'Sanzione fino a 100.000 EUR', ARRAY['XRF-Screening fuer Stichproben', 'Lieferantenerklaerungen einholen', 'DEHP, BBP, DBP und DIBP beachten', 'Dokumentation 10 Jahre aufbewahren'], '[{"title":"RoHS-Richtlinie","url":"https://eur-lex.europa.eu/eli/dir/2011/65"}]'::jsonb, ARRAY['Smartphone', 'Laptop', 'Monitor', 'Drucker', 'Router'], ARRAY['RoHS-Konformitaetserklaerung', 'Materialanalysen']),

('IT', 'electronics', 'REACH-SVHC-Pruefung', 'Besonders besorgniserregende Stoffe (SVHC) pruefen.', 'REACH verpflichtet zur Information ueber SVHC-Stoffe ueber 0,1%. Die SVHC-Kandidatenliste wird halbjaehrlich aktualisiert. SCIP-Meldepflicht an ECHA besteht. Fuer Elektronik sind Weichmacher, Flammschutzmittel und Loetlegierungen besonders relevant.', true, 'Chemikalien & Inhaltsstoffe', 'high', true, 3, 'REACH (EG) Nr. 1907/2006', 'MISE (Ministero delle Imprese e del Made in Italy)', 'Sanzione fino a 100.000 EUR', ARRAY['SVHC-Kandidatenliste halbjaehrlich pruefen', 'SCIP-Meldung durchfuehren', 'Lieferantenbefragungen dokumentieren'], '[{"title":"REACH","url":"https://eur-lex.europa.eu/eli/reg/2006/1907"},{"title":"ECHA SVHC","url":"https://echa.europa.eu/candidate-list-table"}]'::jsonb, ARRAY['Smartphone', 'Laptop', 'Kopfhoerer', 'Kabel', 'Ladegeraet'], ARRAY['SVHC-Pruefbericht', 'SCIP-Meldung']),

('IT', 'electronics', 'DPP-Vorbereitung Elektronik', 'Vorbereitung auf den digitalen Produktpass.', 'Die ESPR fuehrt den digitalen Produktpass fuer Elektronik voraussichtlich ab 2028 ein. Der DPP muss Materialzusammensetzung, Reparierbarkeit, Recyclingfaehigkeit und CO2-Fussabdruck enthalten. Fruehzeitige Vorbereitung verschafft Wettbewerbsvorteile.', false, 'Digitaler Produktpass', 'medium', false, 8, 'ESPR (EU) 2024/1781', 'MISE (Ministero delle Imprese e del Made in Italy)', 'Sanzione fino a 50.000 EUR', ARRAY['Produktdaten digital erfassen', 'CO2-Fussabdruck berechnen', 'QR-Code-Infrastruktur aufbauen', 'Lieferkettendaten verfuegbar machen'], '[{"title":"ESPR","url":"https://eur-lex.europa.eu/eli/reg/2024/1781"}]'::jsonb, ARRAY['Smartphone', 'Laptop', 'Monitor', 'Fernseher', 'Drucker'], ARRAY['DPP-Datentemplate']),

('IT', 'electronics', 'GPSR Wirtschaftsakteur-Pflichten', 'Pflichten als Wirtschaftsakteur nach GPSR erfuellen.', 'Die neue GPSR definiert Pflichten fuer Hersteller, Importeure und Haendler. Risikoanalyse, technische Dokumentation und Identifikation des verantwortlichen Wirtschaftsakteurs sind Pflicht. Bei Online-Verkauf gelten erweiterte Informationspflichten.', true, 'Dokumentation & Rueckverfolgbarkeit', 'high', true, 5, 'GPSR (EU) 2023/988', 'MISE (Ministero delle Imprese e del Made in Italy)', 'Sanzione fino a 100.000 EUR', ARRAY['Eigene Rolle als Wirtschaftsakteur bestimmen', 'Kontaktdaten auf Produkt/Verpackung', 'Risikoanalyse dokumentieren', 'Rueckrufprozesse definieren'], '[{"title":"GPSR","url":"https://eur-lex.europa.eu/eli/reg/2023/988"}]'::jsonb, ARRAY['Smartphone', 'Laptop', 'Monitor', 'Router', 'Ladegeraet'], ARRAY['Risikoanalyse', 'Technische Dokumentation']),

('IT', 'electronics', 'Lieferkettensorgfalt Elektronik', 'Sorgfaltspflichten in der Elektronik-Lieferkette.', 'Die CSDDD verpflichtet zu Sorgfaltspflichten bezueglich Menschenrechte und Umwelt. Fuer Elektronik sind Konfliktmineralien und Arbeitsbedingungen besonders relevant. Risikoanalyse und Praeventionsmassnahmen sind erforderlich.', true, 'Soziale Verantwortung & Lieferkette', 'medium', true, 7, 'CSDDD (EU) 2024/1760', 'MISE (Ministero delle Imprese e del Made in Italy)', 'Sanzione fino a 50.000 EUR', ARRAY['Risikoanalyse fuer Zulieferer', 'Konfliktmineralien nachverfolgen', 'Beschwerdemechanismus einrichten'], '[{"title":"CSDDD","url":"https://eur-lex.europa.eu/eli/dir/2024/1760"}]'::jsonb, ARRAY['Smartphone', 'Laptop', 'Monitor', 'Smartwatch'], ARRAY['Risikoanalyse', 'Due-Diligence-Bericht']),

('IT', 'textiles', 'Textilkennzeichnung Faserzusammensetzung', 'Faserzusammensetzung korrekt kennzeichnen.', 'Die EU-Textilkennzeichnungsverordnung schreibt die vollstaendige Angabe der Faserzusammensetzung vor. Fasern in absteigender Reihenfolge des Gewichtsanteils. Kennzeichnung in Landessprache erforderlich.', true, 'Kennzeichnung & Verpackung', 'critical', false, 1, 'Textilkennzeichnungsverordnung (EU) Nr. 1007/2011', 'MISE, Guardia di Finanza', 'Sanzione fino a 150.000 EUR', ARRAY['Fasern absteigend nach Gewichtsanteil', 'Vorgeschriebene Faserbezeichnungen verwenden', 'Etikett waschbestaendig', 'In Landessprache kennzeichnen'], '[{"title":"Textilkennzeichnung","url":"https://eur-lex.europa.eu/eli/reg/2011/1007"}]'::jsonb, ARRAY['T-Shirt', 'Hemd', 'Jeans', 'Kleid', 'Jacke', 'Bettwaesche'], ARRAY['Textilkennzeichnung', 'Faseranalyse']),

('IT', 'textiles', 'REACH-Konformitaet Textilien', 'REACH-Beschraenkungen fuer Textilchemikalien einhalten.', 'AZO-Farbstoffe (max. 30 mg/kg), Formaldehyd, Nickel und Flammschutzmittel muessen geprueft werden. SVHC-Kommunikation bei ueber 0,1%. SCIP-Meldepflicht besteht.', true, 'Chemikalien & Inhaltsstoffe', 'critical', true, 2, 'REACH (EG) Nr. 1907/2006', 'MISE, Guardia di Finanza', 'Sanzione fino a 150.000 EUR', ARRAY['AZO-Farbstoffe-Test durchfuehren', 'Formaldehyd-Grenzwerte beachten', 'SVHC-Kandidatenliste pruefen', 'OEKO-TEX Zertifizierung erwaegen'], '[{"title":"REACH","url":"https://eur-lex.europa.eu/eli/reg/2006/1907"}]'::jsonb, ARRAY['T-Shirt', 'Hemd', 'Jeans', 'Kinderbekleidung', 'Bettwaesche'], ARRAY['REACH-Pruefbericht', 'AZO-Analyse']),

('IT', 'textiles', 'DPP-Vorbereitung Textilien', 'Digitalen Produktpass fuer Textilien vorbereiten.', 'Der DPP fuer Textilien wird voraussichtlich ab 2027 verpflichtend. Faserzusammensetzung, Lieferkette, Umweltauswirkungen und Recyclingfaehigkeit muessen digital erfasst werden.', false, 'Digitaler Produktpass', 'medium', false, 6, 'ESPR (EU) 2024/1781', 'MISE, Guardia di Finanza', 'Sanzione fino a 50.000 EUR', ARRAY['Faserdaten digital erfassen', 'Lieferkette dokumentieren', 'CO2-Fussabdruck berechnen', 'QR-Code vorbereiten'], '[{"title":"ESPR","url":"https://eur-lex.europa.eu/eli/reg/2024/1781"}]'::jsonb, ARRAY['T-Shirt', 'Jeans', 'Kleid', 'Jacke', 'Schuhe'], ARRAY['DPP-Datentemplate']),

('IT', 'batteries', 'EU-Batterieverordnung Konformitaet', 'EU-Batterieverordnung umfassend einhalten.', 'Die EU-Batterieverordnung 2023/1542 stellt umfassende Anforderungen: Kennzeichnung, CO2-Fussabdruck, Recyclatanteil, Sorgfaltspflichten und digitaler Batteriepass. Stufenweise Einfuehrung ab 2024.', true, 'Produktsicherheit & CE-Konformitaet', 'critical', true, 1, 'EU-Batterieverordnung (EU) 2023/1542', 'ISPRA (Istituto Superiore per la Protezione e la Ricerca Ambientale)', 'Sanzione fino a 150.000 EUR', ARRAY['Stufenplan erstellen', 'CO2-Berechnung vorbereiten', 'Recyclatanteil-Nachweis aufbauen', 'Batteriepass-Infrastruktur planen'], '[{"title":"Batterieverordnung","url":"https://eur-lex.europa.eu/eli/reg/2023/1542"}]'::jsonb, ARRAY['Li-Ion 18650', 'EV-Batteriepack', 'E-Bike-Akku', 'Smartphone-Akku'], ARRAY['EU-Konformitaetserklaerung', 'CO2-Bericht']),

('IT', 'batteries', 'Digitaler Batteriepass', 'QR-Code mit Batteriepass ab 2027.', 'Ab 18. Februar 2027 brauchen Industriebatterien und EV-Batterien einen digitalen Batteriepass ueber QR-Code. Informationen zu Kapazitaet, Leistung, CO2-Fussabdruck und Recyclingfaehigkeit.', true, 'Digitaler Produktpass', 'critical', true, 2, 'EU-Batterieverordnung (EU) 2023/1542, Art. 77', 'ISPRA (Istituto Superiore per la Protezione e la Ricerca Ambientale)', 'Sanzione fino a 150.000 EUR', ARRAY['QR-Code-System implementieren', 'Datenerhebung aufbauen', 'Lebenszyklus-Tracking einrichten'], '[{"title":"Battery Pass","url":"https://thebatterypass.eu/"}]'::jsonb, ARRAY['EV-Batteriepack', 'E-Bike-Akku', 'Solarspeicher', 'Traktionsbatterie'], ARRAY['Digitaler Batteriepass', 'QR-Code-Nachweis']),

('IT', 'packaging', 'Recyclingfaehigkeit sicherstellen', 'Design-for-Recycling fuer Verpackungen umsetzen.', 'Die PPWR stellt verbindliche Anforderungen an die Recyclingfaehigkeit. Monomaterial bevorzugen, Verbundmaterialien vermeiden, Trennbarkeit sicherstellen. Recyclingfaehigkeit wird kuenftig Voraussetzung fuer das Inverkehrbringen.', true, 'Umwelt & Nachhaltigkeit', 'high', true, 3, 'PPWR (EU)', 'CONAI (Consorzio Nazionale Imballaggi)', 'Sanzione fino a 100.000 EUR', ARRAY['Monomaterial bevorzugen', 'Carbon-Black vermeiden', 'Etiketten abloesbar gestalten', 'Recyclingfaehigkeit bewerten lassen'], '[{"title":"PPWR","url":"https://eur-lex.europa.eu/legal-content/DE/TXT/?uri=CELEX:52022PC0677"}]'::jsonb, ARRAY['PET-Flasche', 'Wellpappkarton', 'Standbodenbeutel', 'Faltschachtel'], ARRAY['Recyclingfaehigkeits-Gutachten']),

('IT', 'packaging', 'DPP-Vorbereitung Verpackungen', 'DPP fuer Verpackungen vorbereiten.', 'ESPR und PPWR erfordern kuenftig DPP fuer Verpackungen mit Materialzusammensetzung, Recyclingfaehigkeit und Entsorgungshinweisen.', false, 'Digitaler Produktpass', 'medium', false, 6, 'ESPR (EU) 2024/1781, PPWR (EU)', 'CONAI (Consorzio Nazionale Imballaggi)', 'Sanzione fino a 50.000 EUR', ARRAY['Materialzusammensetzung digital erfassen', 'Recyclingfaehigkeit dokumentieren', 'QR-Code vorbereiten'], '[{"title":"ESPR","url":"https://eur-lex.europa.eu/eli/reg/2024/1781"}]'::jsonb, ARRAY['PET-Flasche', 'Wellpappkarton', 'Glasflasche', 'Standbodenbeutel'], ARRAY['DPP-Datentemplate']),

('IT', 'furniture', 'Formaldehyd-Emissionsgrenzwerte', 'Formaldehyd-Grenzwerte in Holzwerkstoffen einhalten.', 'Holzbasierte Moebel duerfen maximal E1-Emissionswerte aufweisen. Spanplatten, MDF und Sperrholz muessen geprueft werden. REACH Anhang XVII reguliert Formaldehyd.', true, 'Chemikalien & Inhaltsstoffe', 'critical', true, 1, 'REACH (EG) Nr. 1907/2006 Anhang XVII', 'MISE, Camere di Commercio', 'Sanzione fino a 150.000 EUR', ARRAY['E1-Klassifizierung sicherstellen', 'Pruefberichte anfordern', 'Lacke und Klebstoffe pruefen'], '[{"title":"REACH","url":"https://eur-lex.europa.eu/eli/reg/2006/1907"}]'::jsonb, ARRAY['Kleiderschrank', 'Kommode', 'Esstisch', 'Schreibtisch', 'Kinderbett'], ARRAY['Formaldehyd-Pruefung', 'E1-Zertifikat']),

('IT', 'furniture', 'EUDR Holzherkunft nachweisen', 'Nachhaltige und legale Holzherkunft dokumentieren.', 'Die EUDR verlangt Entwaldungsfreiheit und legale Holzherkunft. Geolokalisierung und Sorgfaltspflichten erforderlich. FSC/PEFC erleichtern den Nachweis.', true, 'Umwelt & Nachhaltigkeit', 'high', true, 3, 'EUDR (EU) 2023/1115', 'MISE, Camere di Commercio', 'Sanzione fino a 100.000 EUR', ARRAY['Sorgfaltspflicht-System aufbauen', 'Geolokalisierung dokumentieren', 'FSC/PEFC Zertifizierung erwaegen'], '[{"title":"EUDR","url":"https://eur-lex.europa.eu/eli/reg/2023/1115"}]'::jsonb, ARRAY['Esstisch', 'Kleiderschrank', 'Bücherregal', 'Kommode'], ARRAY['EUDR-Sorgfaltserklaerung', 'FSC/PEFC-Zertifikat']),

('IT', 'cosmetics', 'CPNP-Notifizierung', 'Notifizierung im CPNP vor dem Inverkehrbringen.', 'Jedes kosmetische Produkt muss im CPNP notifiziert werden. Rahmenrezeptur, Kennzeichnung und verantwortliche Person muessen angegeben werden. Aktualisierung bei Formulierungsaenderungen.', true, 'Dokumentation & Rueckverfolgbarkeit', 'critical', true, 1, 'Kosmetikverordnung (EG) Nr. 1223/2009, Art. 13', 'Ministero della Salute', 'Sanzione fino a 150.000 EUR', ARRAY['CPNP-Zugang beantragen', 'Rahmenrezeptur vollstaendig eingeben', 'Kennzeichnung hochladen', 'Bei Aenderungen aktualisieren'], '[{"title":"CPNP","url":"https://ec.europa.eu/growth/tools-databases/cosing/cpnp/"}]'::jsonb, ARRAY['Tagescreme', 'Shampoo', 'Duschgel', 'Lippenstift', 'Sonnencreme'], ARRAY['CPNP-Notifizierung']),

('IT', 'cosmetics', 'Sicherheitsbewertung', 'Sicherheitsbewertung durch qualifizierten Bewerter.', 'Qualifizierter Sicherheitsbewerter muss Sicherheitsbericht (Teil A und B) erstellen. Alle Inhaltsstoffe, Exposition und Gesamtsicherheit werden bewertet. Muss vor Inverkehrbringen vorliegen.', true, 'Produktsicherheit & CE-Konformitaet', 'critical', true, 2, 'Kosmetikverordnung (EG) Nr. 1223/2009, Art. 10', 'Ministero della Salute', 'Sanzione fino a 150.000 EUR', ARRAY['Qualifizierten Bewerter beauftragen', 'Stabilitaetsdaten bereitstellen', 'PAO festlegen lassen', 'Bei Aenderungen aktualisieren'], '[{"title":"Kosmetikverordnung","url":"https://eur-lex.europa.eu/eli/reg/2009/1223"}]'::jsonb, ARRAY['Tagescreme', 'Shampoo', 'Sonnencreme', 'Haarfarbe', 'Parfum'], ARRAY['Sicherheitsbericht']),

('IT', 'toys', 'CE-Kennzeichnung Spielzeugrichtlinie', 'CE-Kennzeichnung nach Spielzeugrichtlinie.', 'Pruefung nach EN 71 (mechanisch, entflammbar, chemisch). CE-Zeichen sichtbar und dauerhaft anbringen. Konformitaetserklaerung vor Inverkehrbringen.', true, 'Produktsicherheit & CE-Konformitaet', 'critical', true, 1, 'Spielzeugrichtlinie 2009/48/EG', 'MISE, Camere di Commercio', 'Sanzione fino a 150.000 EUR', ARRAY['EN 71 Pruefungen durchfuehren', 'CE-Zeichen mindestens 5 mm', 'Konformitaetserklaerung erstellen', 'Technische Dokumentation aufbauen'], '[{"title":"Spielzeugrichtlinie","url":"https://eur-lex.europa.eu/eli/dir/2009/48"}]'::jsonb, ARRAY['LEGO-Set', 'Puppe', 'Plüschtier', 'Spielzeugauto', 'Puzzle'], ARRAY['EN 71 Pruefberichte', 'EU-Konformitaetserklaerung']),

('IT', 'toys', 'Chemische Sicherheit EN 71-3', 'Migration bestimmter Elemente pruefen.', 'EN 71-3 legt Grenzwerte fuer 19 Elemente fest. Drei Materialkategorien werden unterschieden. Besonders fuer lackiertes Spielzeug und mundfaehige Produkte relevant.', true, 'Chemikalien & Inhaltsstoffe', 'critical', true, 2, 'EN 71-3, Spielzeugrichtlinie 2009/48/EG', 'MISE, Camere di Commercio', 'Sanzione fino a 150.000 EUR', ARRAY['Alle 19 Elemente pruefen', 'Drei Materialkategorien beachten', 'Lackiertes Spielzeug testen'], '[{"title":"EN 71-3","url":"https://eur-lex.europa.eu/eli/dir/2009/48"}]'::jsonb, ARRAY['Puppe', 'Spielzeugauto', 'Holzbausteine', 'Fingerfarben', 'Knetmasse'], ARRAY['EN 71-3 Pruefbericht']),

('IT', 'food', 'LMIV-Kennzeichnung', 'Lebensmittelkennzeichnung nach LMIV.', 'Pflichtkennzeichnung: Bezeichnung, Zutaten, Allergene, Nettofuellmenge, MHD, Naehrwert, Herkunft, Lebensmittelunternehmer. In Landessprache erforderlich.', true, 'Kennzeichnung & Verpackung', 'critical', true, 1, 'LMIV (EU) Nr. 1169/2011', 'Ministero della Salute, NAS (Nuclei Antisofisticazione)', 'Sanzione fino a 150.000 EUR', ARRAY['14 Allergene hervorheben', 'Naehrwerttabelle vollstaendig', 'Mindestschriftgroesse 1,2 mm', 'In Landessprache kennzeichnen'], '[{"title":"LMIV","url":"https://eur-lex.europa.eu/eli/reg/2011/1169"}]'::jsonb, ARRAY['Milchprodukte', 'Fleisch', 'Brot', 'Gemuese', 'Tiefkuehlprodukte'], ARRAY['Kennzeichnungsentwurf', 'Naehrwertanalyse']),

('IT', 'food', 'HACCP-System', 'HACCP-Konzept implementieren.', 'Gefahrenanalyse, kritische Kontrollpunkte, Grenzwerte, Ueberwachung, Korrekturmassnahmen, Verifizierung und Dokumentation. Jaehrliche Revision.', true, 'Produktsicherheit & CE-Konformitaet', 'critical', true, 2, 'Verordnung (EG) Nr. 852/2004', 'Ministero della Salute, NAS (Nuclei Antisofisticazione)', 'Sanzione fino a 150.000 EUR', ARRAY['Sieben HACCP-Grundsaetze anwenden', 'CCP identifizieren und ueberwachen', 'Personal schulen', 'Jaehrliche Revision'], '[{"title":"Hygieneverordnung","url":"https://eur-lex.europa.eu/eli/reg/2004/852"}]'::jsonb, ARRAY['Milch', 'Fleisch', 'Fisch', 'Brot', 'Tiefkuehlprodukte'], ARRAY['HACCP-Plan', 'Schulungsnachweise']),

('IT', 'construction', 'CE-Kennzeichnung Bauprodukte', 'CE-Kennzeichnung nach Bauproduktenverordnung.', 'Harmonisierte Bauprodukte muessen CE-Kennzeichnung mit Leistungserklaerung (DoP) tragen. Wesentliche Merkmale deklarieren. AVCP-System beachten.', true, 'Produktsicherheit & CE-Konformitaet', 'critical', true, 1, 'Bauproduktenverordnung (EU) Nr. 305/2011', 'Consiglio Superiore dei Lavori Pubblici', 'Sanzione fino a 150.000 EUR', ARRAY['Harmonisierte Norm identifizieren', 'DoP erstellen', 'AVCP-System beachten', 'Notifizierte Stelle einschalten'], '[{"title":"Bauproduktenverordnung","url":"https://eur-lex.europa.eu/eli/reg/2011/305"}]'::jsonb, ARRAY['Mauerziegel', 'Dachziegel', 'Daemmplatte', 'Fenster', 'Bodenfliese'], ARRAY['Leistungserklaerung (DoP)', 'Pruefberichte']),

('IT', 'construction', 'REACH Baustoffe', 'REACH-Anforderungen fuer Bauchemikalien.', 'SVHC in Klebstoffen, Dichtstoffen und Beschichtungen beachten. SCIP-Meldepflicht. Sicherheitsdatenblaetter bereithalten.', true, 'Chemikalien & Inhaltsstoffe', 'high', true, 3, 'REACH (EG) Nr. 1907/2006', 'Consiglio Superiore dei Lavori Pubblici', 'Sanzione fino a 100.000 EUR', ARRAY['SVHC-Screening fuer Bauchemie', 'SCIP-Meldung durchfuehren', 'Sicherheitsdatenblaetter bereithalten'], '[{"title":"REACH","url":"https://eur-lex.europa.eu/eli/reg/2006/1907"}]'::jsonb, ARRAY['Montagekleber', 'Silikon', 'Bauschaum', 'Wandfarbe', 'Grundierung'], ARRAY['REACH-Konformitaet', 'Sicherheitsdatenblaetter']),

('IT', 'machinery', 'Maschinenverordnung Konformitaet', 'Neue EU-Maschinenverordnung 2023/1230 einhalten.', 'Ab Januar 2027 ersetzt die neue Maschinenverordnung die Maschinenrichtlinie. Neue Anforderungen an Cybersicherheit, KI und digitale Anleitungen. Risikobeurteilung nach EN ISO 12100 zentral.', true, 'Produktsicherheit & CE-Konformitaet', 'critical', true, 1, 'Maschinenverordnung (EU) 2023/1230', 'INAIL (Istituto Nazionale Assicurazione Infortuni sul Lavoro)', 'Sanzione fino a 150.000 EUR', ARRAY['Risikobeurteilung nach EN ISO 12100', 'Cybersicherheit beachten', 'Digitale Betriebsanleitung ab 2027', 'Notifizierte Stelle fuer Anhang-I-Maschinen'], '[{"title":"Maschinenverordnung","url":"https://eur-lex.europa.eu/eli/reg/2023/1230"}]'::jsonb, ARRAY['CNC-Fraesmaschine', 'Kreissaege', 'Abkantpresse', 'Industrieroboter', 'Gabelstapler'], ARRAY['Konformitaetserklaerung', 'Risikobeurteilung']),

('IT', 'machinery', 'Betriebsanleitung in Landessprache', 'Betriebsanleitung in der Landessprache bereitstellen.', 'Betriebsanleitung muss in der Amtssprache des Verwenderlandes vorliegen. Sicherheitshinweise, bestimmungsgemaesser Gebrauch, Wartung und Entsorgung muessen enthalten sein.', true, 'Dokumentation & Rueckverfolgbarkeit', 'high', true, 2, 'Maschinenverordnung (EU) 2023/1230, Anhang III', 'INAIL (Istituto Nazionale Assicurazione Infortuni sul Lavoro)', 'Sanzione fino a 100.000 EUR', ARRAY['Alle Anhang-III-Inhalte aufnehmen', 'Restrisiken dokumentieren', 'Bildliche Darstellungen verwenden', 'Wartungsintervalle definieren'], '[{"title":"Maschinenverordnung","url":"https://eur-lex.europa.eu/eli/reg/2023/1230"}]'::jsonb, ARRAY['CNC-Fraesmaschine', 'Kreissaege', 'Abkantpresse', 'Gabelstapler', 'Kompressor'], ARRAY['Betriebsanleitung', 'Sicherheitsdatenblatt']);


-- ============================================
-- SPANIEN (ES) - ALLE KATEGORIEN
-- ============================================

-- Laenderspezifische Anforderungen ES
INSERT INTO checklist_templates (country_code, category_key, title, description, detailed_description, mandatory, category, priority, document_required, sort_order, legal_basis, authority, penalties, tips, links, applicable_products, document_types) VALUES
('ES', 'electronics', 'RAEE-Registrierung Spanien', 'Registrierung im spanischen RAEE-System (Registro de aparatos electricos y electronicos).', 'In Spanien muessen Hersteller und Importeure von Elektrogeraeten sich im nationalen RAEE-Register registrieren und einem zugelassenen Collectivsystem (SIG) beitreten. Die wichtigsten SIGs sind Ecotic, Ecolec und Ecofimatica. Die Registrierung erfolgt beim MITERD. Die spanische Umsetzung hat einige nationale Besonderheiten bei den Geraetekategorien und Meldepflichten.', true, 'Umwelt & Nachhaltigkeit', 'critical', true, 1, 'Real Decreto 110/2015, WEEE 2012/19/EU', 'MITERD', 'Multa hasta 600.000 EUR', ARRAY['Im RAEE-Register des MITERD registrieren', 'SIG waehlen (Ecotic, Ecolec)', 'Jahresmeldungen fristgerecht einreichen', 'Visible Fee auf Rechnungen beachten'], '[{"title":"MITERD RAEE","url":"https://www.miteco.gob.es/es/calidad-y-evaluacion-ambiental/temas/prevencion-y-gestion-residuos/flujos/aparatos-electricos/"}]'::jsonb, ARRAY['Smartphone', 'Laptop', 'Fernseher', 'Drucker', 'Router'], ARRAY['RAEE-Registrierung', 'SIG-Mitgliedschaft']),

('ES', 'packaging', 'Ecoembes-Registrierung Spanien', 'Pflichtregistrierung bei Ecoembes fuer Verpackungen in Spanien.', 'In Spanien muessen Hersteller bei Ecoembes (fuer Haushalt) oder Ecovidrio (fuer Glas) registriert sein. Die Punto Verde-Gebuehren finanzieren das Sammel- und Recyclingsystem. Die Registrierung und Mengenmeldung erfolgt online. Spanien setzt die PPWR mit nationalen Besonderheiten um.', true, 'Umwelt & Nachhaltigkeit', 'critical', true, 1, 'Ley 7/2022, Real Decreto 1055/2022', 'MITERD, Ecoembes', 'Multa hasta 600.000 EUR', ARRAY['Bei Ecoembes und/oder Ecovidrio registrieren', 'Punto Verde-Gebuehren zahlen', 'Mengenmeldung nach Materialfraktion', 'Spanische Recyclingquoten beachten'], '[{"title":"Ecoembes","url":"https://www.ecoembes.com/"},{"title":"Ecovidrio","url":"https://www.ecovidrio.es/"}]'::jsonb, ARRAY['Wellpappkarton', 'PET-Flasche', 'Glasflasche', 'Getränkedose', 'Kunststoffverpackung'], ARRAY['Ecoembes-Registrierung', 'Mengenmeldung']),

('ES', 'food', 'AESAN Lebensmittelsicherheit Spanien', 'Spanische Lebensmittelsicherheitsanforderungen der AESAN beachten.', 'Die AESAN ist die spanische Behoerde fuer Lebensmittelsicherheit. Sie koordiniert die Lebensmittelkontrolle und das Schnellwarnsystem. Spanien hat zusaetzliche Anforderungen an die Kennzeichnung in Spanisch und Katalanisch (in Katalonien). Die Lebensmittelhygiene wird von den autonomen Gemeinschaften ueberwacht.', true, 'Produktsicherheit & CE-Konformitaet', 'high', true, 1, 'Ley 17/2011 de Seguridad Alimentaria, LMIV (EU) 1169/2011', 'AESAN', 'Multa hasta 600.000 EUR', ARRAY['Kennzeichnung auf Spanisch (Castellano) erforderlich', 'In Katalonien zusaetzlich auf Katalanisch', 'AESAN-Leitlinien beachten', 'Schnellwarnsystem RASFF verfolgen'], '[{"title":"AESAN","url":"https://www.aesan.gob.es/"}]'::jsonb, ARRAY['Olivenoel', 'Schinken', 'Kaese', 'Wein', 'Meeresfrüchte'], ARRAY['Kennzeichnung (ES)', 'HACCP-Plan']);

-- EU-weite Anforderungen fuer ES
INSERT INTO checklist_templates (country_code, category_key, title, description, detailed_description, mandatory, category, priority, document_required, sort_order, legal_basis, authority, penalties, tips, links, applicable_products, document_types) VALUES
('ES', 'electronics', 'CE-Kennzeichnung pruefen', 'CE-Zeichen korrekt und sichtbar anbringen.', 'Die CE-Kennzeichnung ist fuer alle Elektronikprodukte im EU-Binnenmarkt verpflichtend. Sie muss mindestens 5 mm hoch, sichtbar und dauerhaft angebracht sein. Der Hersteller uebernimmt die Verantwortung fuer die EU-Konformitaet. Die Kennzeichnung muss vor dem Inverkehrbringen angebracht werden.', true, 'Produktsicherheit & CE-Konformitaet', 'critical', true, 1, 'Verordnung (EU) 2023/988 (GPSR)', 'Ministerio para la Transicion Ecologica y el Reto Demografico (MITERD)', 'Multa hasta 600.000 EUR', ARRAY['CE-Zeichen mindestens 5 mm hoch', 'Auf Produkt, nicht nur Verpackung', 'Konformitaetserklaerung erstellen', 'Technische Dokumentation bereithalten'], '[{"title":"GPSR","url":"https://eur-lex.europa.eu/eli/reg/2023/988"}]'::jsonb, ARRAY['Smartphone', 'Laptop', 'Fernseher', 'Monitor', 'Router', 'Drucker'], ARRAY['EU-Konformitaetserklaerung', 'Technische Dokumentation']),

('ES', 'electronics', 'RoHS-Konformitaet sicherstellen', 'Beschraenkung gefaehrlicher Stoffe einhalten.', 'Die RoHS-Richtlinie beschraenkt die Verwendung gefaehrlicher Stoffe in Elektrogeraeten. Grenzwerte: 0,1% fuer Blei, Quecksilber, Cr VI, PBB, PBDE, DEHP, BBP, DBP; 0,01% fuer Cadmium. Materialanalysen und Lieferantenerklaerungen sind erforderlich. Die Dokumentation muss 10 Jahre aufbewahrt werden.', true, 'Chemikalien & Inhaltsstoffe', 'high', true, 2, 'RoHS 2011/65/EU, Delegierte Richtlinie (EU) 2015/863', 'Ministerio para la Transicion Ecologica y el Reto Demografico (MITERD)', 'Multa hasta 200.000 EUR', ARRAY['XRF-Screening fuer Stichproben', 'Lieferantenerklaerungen einholen', 'DEHP, BBP, DBP und DIBP beachten', 'Dokumentation 10 Jahre aufbewahren'], '[{"title":"RoHS-Richtlinie","url":"https://eur-lex.europa.eu/eli/dir/2011/65"}]'::jsonb, ARRAY['Smartphone', 'Laptop', 'Monitor', 'Drucker', 'Router'], ARRAY['RoHS-Konformitaetserklaerung', 'Materialanalysen']),

('ES', 'electronics', 'REACH-SVHC-Pruefung', 'Besonders besorgniserregende Stoffe (SVHC) pruefen.', 'REACH verpflichtet zur Information ueber SVHC-Stoffe ueber 0,1%. Die SVHC-Kandidatenliste wird halbjaehrlich aktualisiert. SCIP-Meldepflicht an ECHA besteht. Fuer Elektronik sind Weichmacher, Flammschutzmittel und Loetlegierungen besonders relevant.', true, 'Chemikalien & Inhaltsstoffe', 'high', true, 3, 'REACH (EG) Nr. 1907/2006', 'Ministerio para la Transicion Ecologica y el Reto Demografico (MITERD)', 'Multa hasta 200.000 EUR', ARRAY['SVHC-Kandidatenliste halbjaehrlich pruefen', 'SCIP-Meldung durchfuehren', 'Lieferantenbefragungen dokumentieren'], '[{"title":"REACH","url":"https://eur-lex.europa.eu/eli/reg/2006/1907"},{"title":"ECHA SVHC","url":"https://echa.europa.eu/candidate-list-table"}]'::jsonb, ARRAY['Smartphone', 'Laptop', 'Kopfhoerer', 'Kabel', 'Ladegeraet'], ARRAY['SVHC-Pruefbericht', 'SCIP-Meldung']),

('ES', 'electronics', 'DPP-Vorbereitung Elektronik', 'Vorbereitung auf den digitalen Produktpass.', 'Die ESPR fuehrt den digitalen Produktpass fuer Elektronik voraussichtlich ab 2028 ein. Der DPP muss Materialzusammensetzung, Reparierbarkeit, Recyclingfaehigkeit und CO2-Fussabdruck enthalten. Fruehzeitige Vorbereitung verschafft Wettbewerbsvorteile.', false, 'Digitaler Produktpass', 'medium', false, 8, 'ESPR (EU) 2024/1781', 'Ministerio para la Transicion Ecologica y el Reto Demografico (MITERD)', 'Multa hasta 100.000 EUR', ARRAY['Produktdaten digital erfassen', 'CO2-Fussabdruck berechnen', 'QR-Code-Infrastruktur aufbauen', 'Lieferkettendaten verfuegbar machen'], '[{"title":"ESPR","url":"https://eur-lex.europa.eu/eli/reg/2024/1781"}]'::jsonb, ARRAY['Smartphone', 'Laptop', 'Monitor', 'Fernseher', 'Drucker'], ARRAY['DPP-Datentemplate']),

('ES', 'electronics', 'GPSR Wirtschaftsakteur-Pflichten', 'Pflichten als Wirtschaftsakteur nach GPSR erfuellen.', 'Die neue GPSR definiert Pflichten fuer Hersteller, Importeure und Haendler. Risikoanalyse, technische Dokumentation und Identifikation des verantwortlichen Wirtschaftsakteurs sind Pflicht. Bei Online-Verkauf gelten erweiterte Informationspflichten.', true, 'Dokumentation & Rueckverfolgbarkeit', 'high', true, 5, 'GPSR (EU) 2023/988', 'Ministerio para la Transicion Ecologica y el Reto Demografico (MITERD)', 'Multa hasta 200.000 EUR', ARRAY['Eigene Rolle als Wirtschaftsakteur bestimmen', 'Kontaktdaten auf Produkt/Verpackung', 'Risikoanalyse dokumentieren', 'Rueckrufprozesse definieren'], '[{"title":"GPSR","url":"https://eur-lex.europa.eu/eli/reg/2023/988"}]'::jsonb, ARRAY['Smartphone', 'Laptop', 'Monitor', 'Router', 'Ladegeraet'], ARRAY['Risikoanalyse', 'Technische Dokumentation']),

('ES', 'electronics', 'Lieferkettensorgfalt Elektronik', 'Sorgfaltspflichten in der Elektronik-Lieferkette.', 'Die CSDDD verpflichtet zu Sorgfaltspflichten bezueglich Menschenrechte und Umwelt. Fuer Elektronik sind Konfliktmineralien und Arbeitsbedingungen besonders relevant. Risikoanalyse und Praeventionsmassnahmen sind erforderlich.', true, 'Soziale Verantwortung & Lieferkette', 'medium', true, 7, 'CSDDD (EU) 2024/1760', 'Ministerio para la Transicion Ecologica y el Reto Demografico (MITERD)', 'Multa hasta 100.000 EUR', ARRAY['Risikoanalyse fuer Zulieferer', 'Konfliktmineralien nachverfolgen', 'Beschwerdemechanismus einrichten'], '[{"title":"CSDDD","url":"https://eur-lex.europa.eu/eli/dir/2024/1760"}]'::jsonb, ARRAY['Smartphone', 'Laptop', 'Monitor', 'Smartwatch'], ARRAY['Risikoanalyse', 'Due-Diligence-Bericht']),

('ES', 'textiles', 'Textilkennzeichnung Faserzusammensetzung', 'Faserzusammensetzung korrekt kennzeichnen.', 'Die EU-Textilkennzeichnungsverordnung schreibt die vollstaendige Angabe der Faserzusammensetzung vor. Fasern in absteigender Reihenfolge des Gewichtsanteils. Kennzeichnung in Landessprache erforderlich.', true, 'Kennzeichnung & Verpackung', 'critical', false, 1, 'Textilkennzeichnungsverordnung (EU) Nr. 1007/2011', 'AEMPS (Agencia Espanola de Medicamentos y Productos Sanitarios), MITERD', 'Multa hasta 600.000 EUR', ARRAY['Fasern absteigend nach Gewichtsanteil', 'Vorgeschriebene Faserbezeichnungen verwenden', 'Etikett waschbestaendig', 'In Landessprache kennzeichnen'], '[{"title":"Textilkennzeichnung","url":"https://eur-lex.europa.eu/eli/reg/2011/1007"}]'::jsonb, ARRAY['T-Shirt', 'Hemd', 'Jeans', 'Kleid', 'Jacke', 'Bettwaesche'], ARRAY['Textilkennzeichnung', 'Faseranalyse']),

('ES', 'textiles', 'REACH-Konformitaet Textilien', 'REACH-Beschraenkungen fuer Textilchemikalien einhalten.', 'AZO-Farbstoffe (max. 30 mg/kg), Formaldehyd, Nickel und Flammschutzmittel muessen geprueft werden. SVHC-Kommunikation bei ueber 0,1%. SCIP-Meldepflicht besteht.', true, 'Chemikalien & Inhaltsstoffe', 'critical', true, 2, 'REACH (EG) Nr. 1907/2006', 'AEMPS (Agencia Espanola de Medicamentos y Productos Sanitarios), MITERD', 'Multa hasta 600.000 EUR', ARRAY['AZO-Farbstoffe-Test durchfuehren', 'Formaldehyd-Grenzwerte beachten', 'SVHC-Kandidatenliste pruefen', 'OEKO-TEX Zertifizierung erwaegen'], '[{"title":"REACH","url":"https://eur-lex.europa.eu/eli/reg/2006/1907"}]'::jsonb, ARRAY['T-Shirt', 'Hemd', 'Jeans', 'Kinderbekleidung', 'Bettwaesche'], ARRAY['REACH-Pruefbericht', 'AZO-Analyse']),

('ES', 'textiles', 'DPP-Vorbereitung Textilien', 'Digitalen Produktpass fuer Textilien vorbereiten.', 'Der DPP fuer Textilien wird voraussichtlich ab 2027 verpflichtend. Faserzusammensetzung, Lieferkette, Umweltauswirkungen und Recyclingfaehigkeit muessen digital erfasst werden.', false, 'Digitaler Produktpass', 'medium', false, 6, 'ESPR (EU) 2024/1781', 'AEMPS (Agencia Espanola de Medicamentos y Productos Sanitarios), MITERD', 'Multa hasta 100.000 EUR', ARRAY['Faserdaten digital erfassen', 'Lieferkette dokumentieren', 'CO2-Fussabdruck berechnen', 'QR-Code vorbereiten'], '[{"title":"ESPR","url":"https://eur-lex.europa.eu/eli/reg/2024/1781"}]'::jsonb, ARRAY['T-Shirt', 'Jeans', 'Kleid', 'Jacke', 'Schuhe'], ARRAY['DPP-Datentemplate']),

('ES', 'batteries', 'EU-Batterieverordnung Konformitaet', 'EU-Batterieverordnung umfassend einhalten.', 'Die EU-Batterieverordnung 2023/1542 stellt umfassende Anforderungen: Kennzeichnung, CO2-Fussabdruck, Recyclatanteil, Sorgfaltspflichten und digitaler Batteriepass. Stufenweise Einfuehrung ab 2024.', true, 'Produktsicherheit & CE-Konformitaet', 'critical', true, 1, 'EU-Batterieverordnung (EU) 2023/1542', 'MITERD', 'Multa hasta 600.000 EUR', ARRAY['Stufenplan erstellen', 'CO2-Berechnung vorbereiten', 'Recyclatanteil-Nachweis aufbauen', 'Batteriepass-Infrastruktur planen'], '[{"title":"Batterieverordnung","url":"https://eur-lex.europa.eu/eli/reg/2023/1542"}]'::jsonb, ARRAY['Li-Ion 18650', 'EV-Batteriepack', 'E-Bike-Akku', 'Smartphone-Akku'], ARRAY['EU-Konformitaetserklaerung', 'CO2-Bericht']),

('ES', 'batteries', 'Digitaler Batteriepass', 'QR-Code mit Batteriepass ab 2027.', 'Ab 18. Februar 2027 brauchen Industriebatterien und EV-Batterien einen digitalen Batteriepass ueber QR-Code. Informationen zu Kapazitaet, Leistung, CO2-Fussabdruck und Recyclingfaehigkeit.', true, 'Digitaler Produktpass', 'critical', true, 2, 'EU-Batterieverordnung (EU) 2023/1542, Art. 77', 'MITERD', 'Multa hasta 600.000 EUR', ARRAY['QR-Code-System implementieren', 'Datenerhebung aufbauen', 'Lebenszyklus-Tracking einrichten'], '[{"title":"Battery Pass","url":"https://thebatterypass.eu/"}]'::jsonb, ARRAY['EV-Batteriepack', 'E-Bike-Akku', 'Solarspeicher', 'Traktionsbatterie'], ARRAY['Digitaler Batteriepass', 'QR-Code-Nachweis']),

('ES', 'packaging', 'Recyclingfaehigkeit sicherstellen', 'Design-for-Recycling fuer Verpackungen umsetzen.', 'Die PPWR stellt verbindliche Anforderungen an die Recyclingfaehigkeit. Monomaterial bevorzugen, Verbundmaterialien vermeiden, Trennbarkeit sicherstellen. Recyclingfaehigkeit wird kuenftig Voraussetzung fuer das Inverkehrbringen.', true, 'Umwelt & Nachhaltigkeit', 'high', true, 3, 'PPWR (EU)', 'MITERD, Ecoembes', 'Multa hasta 200.000 EUR', ARRAY['Monomaterial bevorzugen', 'Carbon-Black vermeiden', 'Etiketten abloesbar gestalten', 'Recyclingfaehigkeit bewerten lassen'], '[{"title":"PPWR","url":"https://eur-lex.europa.eu/legal-content/DE/TXT/?uri=CELEX:52022PC0677"}]'::jsonb, ARRAY['PET-Flasche', 'Wellpappkarton', 'Standbodenbeutel', 'Faltschachtel'], ARRAY['Recyclingfaehigkeits-Gutachten']),

('ES', 'packaging', 'DPP-Vorbereitung Verpackungen', 'DPP fuer Verpackungen vorbereiten.', 'ESPR und PPWR erfordern kuenftig DPP fuer Verpackungen mit Materialzusammensetzung, Recyclingfaehigkeit und Entsorgungshinweisen.', false, 'Digitaler Produktpass', 'medium', false, 6, 'ESPR (EU) 2024/1781, PPWR (EU)', 'MITERD, Ecoembes', 'Multa hasta 100.000 EUR', ARRAY['Materialzusammensetzung digital erfassen', 'Recyclingfaehigkeit dokumentieren', 'QR-Code vorbereiten'], '[{"title":"ESPR","url":"https://eur-lex.europa.eu/eli/reg/2024/1781"}]'::jsonb, ARRAY['PET-Flasche', 'Wellpappkarton', 'Glasflasche', 'Standbodenbeutel'], ARRAY['DPP-Datentemplate']),

('ES', 'furniture', 'Formaldehyd-Emissionsgrenzwerte', 'Formaldehyd-Grenzwerte in Holzwerkstoffen einhalten.', 'Holzbasierte Moebel duerfen maximal E1-Emissionswerte aufweisen. Spanplatten, MDF und Sperrholz muessen geprueft werden. REACH Anhang XVII reguliert Formaldehyd.', true, 'Chemikalien & Inhaltsstoffe', 'critical', true, 1, 'REACH (EG) Nr. 1907/2006 Anhang XVII', 'Ministerio de Consumo', 'Multa hasta 600.000 EUR', ARRAY['E1-Klassifizierung sicherstellen', 'Pruefberichte anfordern', 'Lacke und Klebstoffe pruefen'], '[{"title":"REACH","url":"https://eur-lex.europa.eu/eli/reg/2006/1907"}]'::jsonb, ARRAY['Kleiderschrank', 'Kommode', 'Esstisch', 'Schreibtisch', 'Kinderbett'], ARRAY['Formaldehyd-Pruefung', 'E1-Zertifikat']),

('ES', 'furniture', 'EUDR Holzherkunft nachweisen', 'Nachhaltige und legale Holzherkunft dokumentieren.', 'Die EUDR verlangt Entwaldungsfreiheit und legale Holzherkunft. Geolokalisierung und Sorgfaltspflichten erforderlich. FSC/PEFC erleichtern den Nachweis.', true, 'Umwelt & Nachhaltigkeit', 'high', true, 3, 'EUDR (EU) 2023/1115', 'Ministerio de Consumo', 'Multa hasta 200.000 EUR', ARRAY['Sorgfaltspflicht-System aufbauen', 'Geolokalisierung dokumentieren', 'FSC/PEFC Zertifizierung erwaegen'], '[{"title":"EUDR","url":"https://eur-lex.europa.eu/eli/reg/2023/1115"}]'::jsonb, ARRAY['Esstisch', 'Kleiderschrank', 'Bücherregal', 'Kommode'], ARRAY['EUDR-Sorgfaltserklaerung', 'FSC/PEFC-Zertifikat']),

('ES', 'cosmetics', 'CPNP-Notifizierung', 'Notifizierung im CPNP vor dem Inverkehrbringen.', 'Jedes kosmetische Produkt muss im CPNP notifiziert werden. Rahmenrezeptur, Kennzeichnung und verantwortliche Person muessen angegeben werden. Aktualisierung bei Formulierungsaenderungen.', true, 'Dokumentation & Rueckverfolgbarkeit', 'critical', true, 1, 'Kosmetikverordnung (EG) Nr. 1223/2009, Art. 13', 'AEMPS', 'Multa hasta 600.000 EUR', ARRAY['CPNP-Zugang beantragen', 'Rahmenrezeptur vollstaendig eingeben', 'Kennzeichnung hochladen', 'Bei Aenderungen aktualisieren'], '[{"title":"CPNP","url":"https://ec.europa.eu/growth/tools-databases/cosing/cpnp/"}]'::jsonb, ARRAY['Tagescreme', 'Shampoo', 'Duschgel', 'Lippenstift', 'Sonnencreme'], ARRAY['CPNP-Notifizierung']),

('ES', 'cosmetics', 'Sicherheitsbewertung', 'Sicherheitsbewertung durch qualifizierten Bewerter.', 'Qualifizierter Sicherheitsbewerter muss Sicherheitsbericht (Teil A und B) erstellen. Alle Inhaltsstoffe, Exposition und Gesamtsicherheit werden bewertet. Muss vor Inverkehrbringen vorliegen.', true, 'Produktsicherheit & CE-Konformitaet', 'critical', true, 2, 'Kosmetikverordnung (EG) Nr. 1223/2009, Art. 10', 'AEMPS', 'Multa hasta 600.000 EUR', ARRAY['Qualifizierten Bewerter beauftragen', 'Stabilitaetsdaten bereitstellen', 'PAO festlegen lassen', 'Bei Aenderungen aktualisieren'], '[{"title":"Kosmetikverordnung","url":"https://eur-lex.europa.eu/eli/reg/2009/1223"}]'::jsonb, ARRAY['Tagescreme', 'Shampoo', 'Sonnencreme', 'Haarfarbe', 'Parfum'], ARRAY['Sicherheitsbericht']),

('ES', 'toys', 'CE-Kennzeichnung Spielzeugrichtlinie', 'CE-Kennzeichnung nach Spielzeugrichtlinie.', 'Pruefung nach EN 71 (mechanisch, entflammbar, chemisch). CE-Zeichen sichtbar und dauerhaft anbringen. Konformitaetserklaerung vor Inverkehrbringen.', true, 'Produktsicherheit & CE-Konformitaet', 'critical', true, 1, 'Spielzeugrichtlinie 2009/48/EG', 'Ministerio de Consumo', 'Multa hasta 600.000 EUR', ARRAY['EN 71 Pruefungen durchfuehren', 'CE-Zeichen mindestens 5 mm', 'Konformitaetserklaerung erstellen', 'Technische Dokumentation aufbauen'], '[{"title":"Spielzeugrichtlinie","url":"https://eur-lex.europa.eu/eli/dir/2009/48"}]'::jsonb, ARRAY['LEGO-Set', 'Puppe', 'Plüschtier', 'Spielzeugauto', 'Puzzle'], ARRAY['EN 71 Pruefberichte', 'EU-Konformitaetserklaerung']),

('ES', 'toys', 'Chemische Sicherheit EN 71-3', 'Migration bestimmter Elemente pruefen.', 'EN 71-3 legt Grenzwerte fuer 19 Elemente fest. Drei Materialkategorien werden unterschieden. Besonders fuer lackiertes Spielzeug und mundfaehige Produkte relevant.', true, 'Chemikalien & Inhaltsstoffe', 'critical', true, 2, 'EN 71-3, Spielzeugrichtlinie 2009/48/EG', 'Ministerio de Consumo', 'Multa hasta 600.000 EUR', ARRAY['Alle 19 Elemente pruefen', 'Drei Materialkategorien beachten', 'Lackiertes Spielzeug testen'], '[{"title":"EN 71-3","url":"https://eur-lex.europa.eu/eli/dir/2009/48"}]'::jsonb, ARRAY['Puppe', 'Spielzeugauto', 'Holzbausteine', 'Fingerfarben', 'Knetmasse'], ARRAY['EN 71-3 Pruefbericht']),

('ES', 'food', 'LMIV-Kennzeichnung', 'Lebensmittelkennzeichnung nach LMIV.', 'Pflichtkennzeichnung: Bezeichnung, Zutaten, Allergene, Nettofuellmenge, MHD, Naehrwert, Herkunft, Lebensmittelunternehmer. In Landessprache erforderlich.', true, 'Kennzeichnung & Verpackung', 'critical', true, 1, 'LMIV (EU) Nr. 1169/2011', 'AESAN (Agencia Espanola de Seguridad Alimentaria y Nutricion)', 'Multa hasta 600.000 EUR', ARRAY['14 Allergene hervorheben', 'Naehrwerttabelle vollstaendig', 'Mindestschriftgroesse 1,2 mm', 'In Landessprache kennzeichnen'], '[{"title":"LMIV","url":"https://eur-lex.europa.eu/eli/reg/2011/1169"}]'::jsonb, ARRAY['Milchprodukte', 'Fleisch', 'Brot', 'Gemuese', 'Tiefkuehlprodukte'], ARRAY['Kennzeichnungsentwurf', 'Naehrwertanalyse']),

('ES', 'food', 'HACCP-System', 'HACCP-Konzept implementieren.', 'Gefahrenanalyse, kritische Kontrollpunkte, Grenzwerte, Ueberwachung, Korrekturmassnahmen, Verifizierung und Dokumentation. Jaehrliche Revision.', true, 'Produktsicherheit & CE-Konformitaet', 'critical', true, 2, 'Verordnung (EG) Nr. 852/2004', 'AESAN (Agencia Espanola de Seguridad Alimentaria y Nutricion)', 'Multa hasta 600.000 EUR', ARRAY['Sieben HACCP-Grundsaetze anwenden', 'CCP identifizieren und ueberwachen', 'Personal schulen', 'Jaehrliche Revision'], '[{"title":"Hygieneverordnung","url":"https://eur-lex.europa.eu/eli/reg/2004/852"}]'::jsonb, ARRAY['Milch', 'Fleisch', 'Fisch', 'Brot', 'Tiefkuehlprodukte'], ARRAY['HACCP-Plan', 'Schulungsnachweise']),

('ES', 'construction', 'CE-Kennzeichnung Bauprodukte', 'CE-Kennzeichnung nach Bauproduktenverordnung.', 'Harmonisierte Bauprodukte muessen CE-Kennzeichnung mit Leistungserklaerung (DoP) tragen. Wesentliche Merkmale deklarieren. AVCP-System beachten.', true, 'Produktsicherheit & CE-Konformitaet', 'critical', true, 1, 'Bauproduktenverordnung (EU) Nr. 305/2011', 'CTE (Codigo Tecnico de la Edificacion)', 'Multa hasta 600.000 EUR', ARRAY['Harmonisierte Norm identifizieren', 'DoP erstellen', 'AVCP-System beachten', 'Notifizierte Stelle einschalten'], '[{"title":"Bauproduktenverordnung","url":"https://eur-lex.europa.eu/eli/reg/2011/305"}]'::jsonb, ARRAY['Mauerziegel', 'Dachziegel', 'Daemmplatte', 'Fenster', 'Bodenfliese'], ARRAY['Leistungserklaerung (DoP)', 'Pruefberichte']),

('ES', 'construction', 'REACH Baustoffe', 'REACH-Anforderungen fuer Bauchemikalien.', 'SVHC in Klebstoffen, Dichtstoffen und Beschichtungen beachten. SCIP-Meldepflicht. Sicherheitsdatenblaetter bereithalten.', true, 'Chemikalien & Inhaltsstoffe', 'high', true, 3, 'REACH (EG) Nr. 1907/2006', 'CTE (Codigo Tecnico de la Edificacion)', 'Multa hasta 200.000 EUR', ARRAY['SVHC-Screening fuer Bauchemie', 'SCIP-Meldung durchfuehren', 'Sicherheitsdatenblaetter bereithalten'], '[{"title":"REACH","url":"https://eur-lex.europa.eu/eli/reg/2006/1907"}]'::jsonb, ARRAY['Montagekleber', 'Silikon', 'Bauschaum', 'Wandfarbe', 'Grundierung'], ARRAY['REACH-Konformitaet', 'Sicherheitsdatenblaetter']),

('ES', 'machinery', 'Maschinenverordnung Konformitaet', 'Neue EU-Maschinenverordnung 2023/1230 einhalten.', 'Ab Januar 2027 ersetzt die neue Maschinenverordnung die Maschinenrichtlinie. Neue Anforderungen an Cybersicherheit, KI und digitale Anleitungen. Risikobeurteilung nach EN ISO 12100 zentral.', true, 'Produktsicherheit & CE-Konformitaet', 'critical', true, 1, 'Maschinenverordnung (EU) 2023/1230', 'INSST (Instituto Nacional de Seguridad y Salud en el Trabajo)', 'Multa hasta 600.000 EUR', ARRAY['Risikobeurteilung nach EN ISO 12100', 'Cybersicherheit beachten', 'Digitale Betriebsanleitung ab 2027', 'Notifizierte Stelle fuer Anhang-I-Maschinen'], '[{"title":"Maschinenverordnung","url":"https://eur-lex.europa.eu/eli/reg/2023/1230"}]'::jsonb, ARRAY['CNC-Fraesmaschine', 'Kreissaege', 'Abkantpresse', 'Industrieroboter', 'Gabelstapler'], ARRAY['Konformitaetserklaerung', 'Risikobeurteilung']),

('ES', 'machinery', 'Betriebsanleitung in Landessprache', 'Betriebsanleitung in der Landessprache bereitstellen.', 'Betriebsanleitung muss in der Amtssprache des Verwenderlandes vorliegen. Sicherheitshinweise, bestimmungsgemaesser Gebrauch, Wartung und Entsorgung muessen enthalten sein.', true, 'Dokumentation & Rueckverfolgbarkeit', 'high', true, 2, 'Maschinenverordnung (EU) 2023/1230, Anhang III', 'INSST (Instituto Nacional de Seguridad y Salud en el Trabajo)', 'Multa hasta 200.000 EUR', ARRAY['Alle Anhang-III-Inhalte aufnehmen', 'Restrisiken dokumentieren', 'Bildliche Darstellungen verwenden', 'Wartungsintervalle definieren'], '[{"title":"Maschinenverordnung","url":"https://eur-lex.europa.eu/eli/reg/2023/1230"}]'::jsonb, ARRAY['CNC-Fraesmaschine', 'Kreissaege', 'Abkantpresse', 'Gabelstapler', 'Kompressor'], ARRAY['Betriebsanleitung', 'Sicherheitsdatenblatt']);


-- ============================================
-- NIEDERLANDE (NL) - ALLE KATEGORIEN
-- ============================================

-- Laenderspezifische Anforderungen NL
INSERT INTO checklist_templates (country_code, category_key, title, description, detailed_description, mandatory, category, priority, document_required, sort_order, legal_basis, authority, penalties, tips, links, applicable_products, document_types) VALUES
('NL', 'electronics', 'Stichting OPEN Registrierung Niederlande', 'Registrierung bei Stichting OPEN fuer Elektroaltgeraete in den Niederlanden.', 'In den Niederlanden ist Stichting OPEN die Organisation fuer die Sammlung und das Recycling von Elektroaltgeraeten. Hersteller und Importeure muessen sich bei Stichting OPEN oder einem alternativen System registrieren. Die Niederlande haben traditionell hohe Sammelquoten und strenge Durchsetzung durch die ILT.', true, 'Umwelt & Nachhaltigkeit', 'critical', true, 1, 'Regeling AEEA (Afgedankte Elektrische en Elektronische Apparatuur), WEEE 2012/19/EU', 'ILT, Stichting OPEN', 'Boete tot 900.000 EUR', ARRAY['Bei Stichting OPEN registrieren', 'Produzentenverantwortung vollstaendig uebernehmen', 'Jahresmeldungen fristgerecht einreichen', 'ILT-Kontrollen beachten'], '[{"title":"Stichting OPEN","url":"https://www.producerverantwoordelijkheid.nl/"}]'::jsonb, ARRAY['Smartphone', 'Laptop', 'Fernseher', 'Drucker', 'Router', 'Kopfhoerer'], ARRAY['OPEN-Registrierung', 'Mengenmeldung']),

('NL', 'packaging', 'Afvalfonds Verpakkingen Niederlande', 'Registrierung beim Afvalfonds Verpakkingen fuer Verpackungen.', 'In den Niederlanden muessen alle Unternehmen, die verpackte Produkte in den Markt bringen, beim Afvalfonds Verpakkingen registriert sein und eine Verpackungsabgabe zahlen. Die Niederlande haben eines der fortschrittlichsten Recycling-Systeme in Europa. Die Kunststoffsteuer (plastic tax) gilt zusaetzlich fuer bestimmte Kunststoffverpackungen.', true, 'Umwelt & Nachhaltigkeit', 'critical', true, 1, 'Besluit verpakkingen, Belasting op kunststof verpakkingen', 'Afvalfonds Verpakkingen, ILT', 'Boete tot 900.000 EUR', ARRAY['Beim Afvalfonds Verpakkingen registrieren', 'Verpackungsabgabe berechnen und zahlen', 'Kunststoffsteuer fuer Kunststoffverpackungen beachten', 'Nederlandse Recyclingquoten erfuellen'], '[{"title":"Afvalfonds","url":"https://www.afvalfondsverpakkingen.nl/"}]'::jsonb, ARRAY['Wellpappkarton', 'PET-Flasche', 'Glasflasche', 'Getränkedose', 'Kunststoffverpackung'], ARRAY['Afvalfonds-Registrierung', 'Mengenmeldung']),

('NL', 'food', 'NVWA Lebensmittelkontrolle Niederlande', 'Niederlaendische Lebensmittelsicherheitsanforderungen der NVWA beachten.', 'Die NVWA (Nederlandse Voedsel- en Warenautoriteit) ist die zentrale Aufsichtsbehoerde fuer Lebensmittelsicherheit in den Niederlanden. Sie fuehrt risikoorientierte Kontrollen durch und setzt die EU-Lebensmittelvorschriften durch. Die Niederlande haben ein besonders strenges Kontrollregime und veroeffentlichen Kontrollergebnisse. Die Kennzeichnung muss auf Niederlaendisch sein.', true, 'Produktsicherheit & CE-Konformitaet', 'high', true, 1, 'Warenwet, LMIV (EU) 1169/2011', 'NVWA', 'Boete tot 900.000 EUR', ARRAY['Kennzeichnung auf Niederlaendisch erforderlich', 'NVWA-Kontrollen proaktiv vorbereiten', 'Warenwet-Anforderungen beachten', 'NVWA-Publikationen verfolgen'], '[{"title":"NVWA","url":"https://www.nvwa.nl/"}]'::jsonb, ARRAY['Kaese', 'Milchprodukte', 'Fleisch', 'Gemuese', 'Fisch'], ARRAY['Kennzeichnung (NL)', 'HACCP-Plan']),

('NL', 'textiles', 'Niederlaendische Textilinnovation und Kreislaufwirtschaft', 'Kreislaufwirtschafts-Anforderungen fuer Textilien in den Niederlanden.', 'Die Niederlande sind Vorreiter bei der Kreislaufwirtschaft fuer Textilien. Das niederlaendische Programm Nederland Circulair 2050 setzt ambitionierte Ziele. Ab 2025 wird eine erweiterte Herstellerverantwortung fuer Textilien eingefuehrt. Hersteller muessen zur Sammlung und zum Recycling von Alttextilien beitragen. Die Niederlande haben bereits eine gut ausgebaute Textil-Sammelinfrastruktur.', true, 'Umwelt & Nachhaltigkeit', 'high', true, 1, 'Besluit UPV textiel, Nederland Circulair 2050', 'ILT, RWS', 'Boete tot 450.000 EUR', ARRAY['UPV-Textiel Registrierung vorbereiten', 'Recyclingfaehige Materialien bevorzugen', 'Niederlaendische Sammelstrukturen nutzen', 'Circulair Textiel-Initiativen verfolgen'], '[{"title":"RWS Circulair","url":"https://www.rijkswaterstaat.nl/onderwerpen/circulaire-economie"}]'::jsonb, ARRAY['T-Shirt', 'Jeans', 'Kleid', 'Jacke', 'Schuhe'], ARRAY['UPV-Registrierung', 'Recyclingkonzept']);

-- EU-weite Anforderungen fuer NL
INSERT INTO checklist_templates (country_code, category_key, title, description, detailed_description, mandatory, category, priority, document_required, sort_order, legal_basis, authority, penalties, tips, links, applicable_products, document_types) VALUES
('NL', 'electronics', 'CE-Kennzeichnung pruefen', 'CE-Zeichen korrekt und sichtbar anbringen.', 'Die CE-Kennzeichnung ist fuer alle Elektronikprodukte im EU-Binnenmarkt verpflichtend. Sie muss mindestens 5 mm hoch, sichtbar und dauerhaft angebracht sein. Der Hersteller uebernimmt die Verantwortung fuer die EU-Konformitaet. Die Kennzeichnung muss vor dem Inverkehrbringen angebracht werden.', true, 'Produktsicherheit & CE-Konformitaet', 'critical', true, 1, 'Verordnung (EU) 2023/988 (GPSR)', 'RWS (Rijkswaterstaat), ILT (Inspectie Leefomgeving en Transport)', 'Boete tot 900.000 EUR', ARRAY['CE-Zeichen mindestens 5 mm hoch', 'Auf Produkt, nicht nur Verpackung', 'Konformitaetserklaerung erstellen', 'Technische Dokumentation bereithalten'], '[{"title":"GPSR","url":"https://eur-lex.europa.eu/eli/reg/2023/988"}]'::jsonb, ARRAY['Smartphone', 'Laptop', 'Fernseher', 'Monitor', 'Router', 'Drucker'], ARRAY['EU-Konformitaetserklaerung', 'Technische Dokumentation']),

('NL', 'electronics', 'RoHS-Konformitaet sicherstellen', 'Beschraenkung gefaehrlicher Stoffe einhalten.', 'Die RoHS-Richtlinie beschraenkt die Verwendung gefaehrlicher Stoffe in Elektrogeraeten. Grenzwerte: 0,1% fuer Blei, Quecksilber, Cr VI, PBB, PBDE, DEHP, BBP, DBP; 0,01% fuer Cadmium. Materialanalysen und Lieferantenerklaerungen sind erforderlich. Die Dokumentation muss 10 Jahre aufbewahrt werden.', true, 'Chemikalien & Inhaltsstoffe', 'high', true, 2, 'RoHS 2011/65/EU, Delegierte Richtlinie (EU) 2015/863', 'RWS (Rijkswaterstaat), ILT (Inspectie Leefomgeving en Transport)', 'Boete tot 450.000 EUR', ARRAY['XRF-Screening fuer Stichproben', 'Lieferantenerklaerungen einholen', 'DEHP, BBP, DBP und DIBP beachten', 'Dokumentation 10 Jahre aufbewahren'], '[{"title":"RoHS-Richtlinie","url":"https://eur-lex.europa.eu/eli/dir/2011/65"}]'::jsonb, ARRAY['Smartphone', 'Laptop', 'Monitor', 'Drucker', 'Router'], ARRAY['RoHS-Konformitaetserklaerung', 'Materialanalysen']),

('NL', 'electronics', 'REACH-SVHC-Pruefung', 'Besonders besorgniserregende Stoffe (SVHC) pruefen.', 'REACH verpflichtet zur Information ueber SVHC-Stoffe ueber 0,1%. Die SVHC-Kandidatenliste wird halbjaehrlich aktualisiert. SCIP-Meldepflicht an ECHA besteht. Fuer Elektronik sind Weichmacher, Flammschutzmittel und Loetlegierungen besonders relevant.', true, 'Chemikalien & Inhaltsstoffe', 'high', true, 3, 'REACH (EG) Nr. 1907/2006', 'RWS (Rijkswaterstaat), ILT (Inspectie Leefomgeving en Transport)', 'Boete tot 450.000 EUR', ARRAY['SVHC-Kandidatenliste halbjaehrlich pruefen', 'SCIP-Meldung durchfuehren', 'Lieferantenbefragungen dokumentieren'], '[{"title":"REACH","url":"https://eur-lex.europa.eu/eli/reg/2006/1907"},{"title":"ECHA SVHC","url":"https://echa.europa.eu/candidate-list-table"}]'::jsonb, ARRAY['Smartphone', 'Laptop', 'Kopfhoerer', 'Kabel', 'Ladegeraet'], ARRAY['SVHC-Pruefbericht', 'SCIP-Meldung']),

('NL', 'electronics', 'DPP-Vorbereitung Elektronik', 'Vorbereitung auf den digitalen Produktpass.', 'Die ESPR fuehrt den digitalen Produktpass fuer Elektronik voraussichtlich ab 2028 ein. Der DPP muss Materialzusammensetzung, Reparierbarkeit, Recyclingfaehigkeit und CO2-Fussabdruck enthalten. Fruehzeitige Vorbereitung verschafft Wettbewerbsvorteile.', false, 'Digitaler Produktpass', 'medium', false, 8, 'ESPR (EU) 2024/1781', 'RWS (Rijkswaterstaat), ILT (Inspectie Leefomgeving en Transport)', 'Boete tot 225.000 EUR', ARRAY['Produktdaten digital erfassen', 'CO2-Fussabdruck berechnen', 'QR-Code-Infrastruktur aufbauen', 'Lieferkettendaten verfuegbar machen'], '[{"title":"ESPR","url":"https://eur-lex.europa.eu/eli/reg/2024/1781"}]'::jsonb, ARRAY['Smartphone', 'Laptop', 'Monitor', 'Fernseher', 'Drucker'], ARRAY['DPP-Datentemplate']),

('NL', 'electronics', 'GPSR Wirtschaftsakteur-Pflichten', 'Pflichten als Wirtschaftsakteur nach GPSR erfuellen.', 'Die neue GPSR definiert Pflichten fuer Hersteller, Importeure und Haendler. Risikoanalyse, technische Dokumentation und Identifikation des verantwortlichen Wirtschaftsakteurs sind Pflicht. Bei Online-Verkauf gelten erweiterte Informationspflichten.', true, 'Dokumentation & Rueckverfolgbarkeit', 'high', true, 5, 'GPSR (EU) 2023/988', 'RWS (Rijkswaterstaat), ILT (Inspectie Leefomgeving en Transport)', 'Boete tot 450.000 EUR', ARRAY['Eigene Rolle als Wirtschaftsakteur bestimmen', 'Kontaktdaten auf Produkt/Verpackung', 'Risikoanalyse dokumentieren', 'Rueckrufprozesse definieren'], '[{"title":"GPSR","url":"https://eur-lex.europa.eu/eli/reg/2023/988"}]'::jsonb, ARRAY['Smartphone', 'Laptop', 'Monitor', 'Router', 'Ladegeraet'], ARRAY['Risikoanalyse', 'Technische Dokumentation']),

('NL', 'electronics', 'Lieferkettensorgfalt Elektronik', 'Sorgfaltspflichten in der Elektronik-Lieferkette.', 'Die CSDDD verpflichtet zu Sorgfaltspflichten bezueglich Menschenrechte und Umwelt. Fuer Elektronik sind Konfliktmineralien und Arbeitsbedingungen besonders relevant. Risikoanalyse und Praeventionsmassnahmen sind erforderlich.', true, 'Soziale Verantwortung & Lieferkette', 'medium', true, 7, 'CSDDD (EU) 2024/1760', 'RWS (Rijkswaterstaat), ILT (Inspectie Leefomgeving en Transport)', 'Boete tot 225.000 EUR', ARRAY['Risikoanalyse fuer Zulieferer', 'Konfliktmineralien nachverfolgen', 'Beschwerdemechanismus einrichten'], '[{"title":"CSDDD","url":"https://eur-lex.europa.eu/eli/dir/2024/1760"}]'::jsonb, ARRAY['Smartphone', 'Laptop', 'Monitor', 'Smartwatch'], ARRAY['Risikoanalyse', 'Due-Diligence-Bericht']),

('NL', 'textiles', 'Textilkennzeichnung Faserzusammensetzung', 'Faserzusammensetzung korrekt kennzeichnen.', 'Die EU-Textilkennzeichnungsverordnung schreibt die vollstaendige Angabe der Faserzusammensetzung vor. Fasern in absteigender Reihenfolge des Gewichtsanteils. Kennzeichnung in Landessprache erforderlich.', true, 'Kennzeichnung & Verpackung', 'critical', false, 1, 'Textilkennzeichnungsverordnung (EU) Nr. 1007/2011', 'NVWA (Nederlandse Voedsel- en Warenautoriteit), ILT', 'Boete tot 900.000 EUR', ARRAY['Fasern absteigend nach Gewichtsanteil', 'Vorgeschriebene Faserbezeichnungen verwenden', 'Etikett waschbestaendig', 'In Landessprache kennzeichnen'], '[{"title":"Textilkennzeichnung","url":"https://eur-lex.europa.eu/eli/reg/2011/1007"}]'::jsonb, ARRAY['T-Shirt', 'Hemd', 'Jeans', 'Kleid', 'Jacke', 'Bettwaesche'], ARRAY['Textilkennzeichnung', 'Faseranalyse']),

('NL', 'textiles', 'REACH-Konformitaet Textilien', 'REACH-Beschraenkungen fuer Textilchemikalien einhalten.', 'AZO-Farbstoffe (max. 30 mg/kg), Formaldehyd, Nickel und Flammschutzmittel muessen geprueft werden. SVHC-Kommunikation bei ueber 0,1%. SCIP-Meldepflicht besteht.', true, 'Chemikalien & Inhaltsstoffe', 'critical', true, 2, 'REACH (EG) Nr. 1907/2006', 'NVWA (Nederlandse Voedsel- en Warenautoriteit), ILT', 'Boete tot 900.000 EUR', ARRAY['AZO-Farbstoffe-Test durchfuehren', 'Formaldehyd-Grenzwerte beachten', 'SVHC-Kandidatenliste pruefen', 'OEKO-TEX Zertifizierung erwaegen'], '[{"title":"REACH","url":"https://eur-lex.europa.eu/eli/reg/2006/1907"}]'::jsonb, ARRAY['T-Shirt', 'Hemd', 'Jeans', 'Kinderbekleidung', 'Bettwaesche'], ARRAY['REACH-Pruefbericht', 'AZO-Analyse']),

('NL', 'textiles', 'DPP-Vorbereitung Textilien', 'Digitalen Produktpass fuer Textilien vorbereiten.', 'Der DPP fuer Textilien wird voraussichtlich ab 2027 verpflichtend. Faserzusammensetzung, Lieferkette, Umweltauswirkungen und Recyclingfaehigkeit muessen digital erfasst werden.', false, 'Digitaler Produktpass', 'medium', false, 6, 'ESPR (EU) 2024/1781', 'NVWA (Nederlandse Voedsel- en Warenautoriteit), ILT', 'Boete tot 225.000 EUR', ARRAY['Faserdaten digital erfassen', 'Lieferkette dokumentieren', 'CO2-Fussabdruck berechnen', 'QR-Code vorbereiten'], '[{"title":"ESPR","url":"https://eur-lex.europa.eu/eli/reg/2024/1781"}]'::jsonb, ARRAY['T-Shirt', 'Jeans', 'Kleid', 'Jacke', 'Schuhe'], ARRAY['DPP-Datentemplate']),

('NL', 'batteries', 'EU-Batterieverordnung Konformitaet', 'EU-Batterieverordnung umfassend einhalten.', 'Die EU-Batterieverordnung 2023/1542 stellt umfassende Anforderungen: Kennzeichnung, CO2-Fussabdruck, Recyclatanteil, Sorgfaltspflichten und digitaler Batteriepass. Stufenweise Einfuehrung ab 2024.', true, 'Produktsicherheit & CE-Konformitaet', 'critical', true, 1, 'EU-Batterieverordnung (EU) 2023/1542', 'ILT, Stichting Batterijen', 'Boete tot 900.000 EUR', ARRAY['Stufenplan erstellen', 'CO2-Berechnung vorbereiten', 'Recyclatanteil-Nachweis aufbauen', 'Batteriepass-Infrastruktur planen'], '[{"title":"Batterieverordnung","url":"https://eur-lex.europa.eu/eli/reg/2023/1542"}]'::jsonb, ARRAY['Li-Ion 18650', 'EV-Batteriepack', 'E-Bike-Akku', 'Smartphone-Akku'], ARRAY['EU-Konformitaetserklaerung', 'CO2-Bericht']),

('NL', 'batteries', 'Digitaler Batteriepass', 'QR-Code mit Batteriepass ab 2027.', 'Ab 18. Februar 2027 brauchen Industriebatterien und EV-Batterien einen digitalen Batteriepass ueber QR-Code. Informationen zu Kapazitaet, Leistung, CO2-Fussabdruck und Recyclingfaehigkeit.', true, 'Digitaler Produktpass', 'critical', true, 2, 'EU-Batterieverordnung (EU) 2023/1542, Art. 77', 'ILT, Stichting Batterijen', 'Boete tot 900.000 EUR', ARRAY['QR-Code-System implementieren', 'Datenerhebung aufbauen', 'Lebenszyklus-Tracking einrichten'], '[{"title":"Battery Pass","url":"https://thebatterypass.eu/"}]'::jsonb, ARRAY['EV-Batteriepack', 'E-Bike-Akku', 'Solarspeicher', 'Traktionsbatterie'], ARRAY['Digitaler Batteriepass', 'QR-Code-Nachweis']),

('NL', 'packaging', 'Recyclingfaehigkeit sicherstellen', 'Design-for-Recycling fuer Verpackungen umsetzen.', 'Die PPWR stellt verbindliche Anforderungen an die Recyclingfaehigkeit. Monomaterial bevorzugen, Verbundmaterialien vermeiden, Trennbarkeit sicherstellen. Recyclingfaehigkeit wird kuenftig Voraussetzung fuer das Inverkehrbringen.', true, 'Umwelt & Nachhaltigkeit', 'high', true, 3, 'PPWR (EU)', 'Afvalfonds Verpakkingen, ILT', 'Boete tot 450.000 EUR', ARRAY['Monomaterial bevorzugen', 'Carbon-Black vermeiden', 'Etiketten abloesbar gestalten', 'Recyclingfaehigkeit bewerten lassen'], '[{"title":"PPWR","url":"https://eur-lex.europa.eu/legal-content/DE/TXT/?uri=CELEX:52022PC0677"}]'::jsonb, ARRAY['PET-Flasche', 'Wellpappkarton', 'Standbodenbeutel', 'Faltschachtel'], ARRAY['Recyclingfaehigkeits-Gutachten']),

('NL', 'packaging', 'DPP-Vorbereitung Verpackungen', 'DPP fuer Verpackungen vorbereiten.', 'ESPR und PPWR erfordern kuenftig DPP fuer Verpackungen mit Materialzusammensetzung, Recyclingfaehigkeit und Entsorgungshinweisen.', false, 'Digitaler Produktpass', 'medium', false, 6, 'ESPR (EU) 2024/1781, PPWR (EU)', 'Afvalfonds Verpakkingen, ILT', 'Boete tot 225.000 EUR', ARRAY['Materialzusammensetzung digital erfassen', 'Recyclingfaehigkeit dokumentieren', 'QR-Code vorbereiten'], '[{"title":"ESPR","url":"https://eur-lex.europa.eu/eli/reg/2024/1781"}]'::jsonb, ARRAY['PET-Flasche', 'Wellpappkarton', 'Glasflasche', 'Standbodenbeutel'], ARRAY['DPP-Datentemplate']),

('NL', 'furniture', 'Formaldehyd-Emissionsgrenzwerte', 'Formaldehyd-Grenzwerte in Holzwerkstoffen einhalten.', 'Holzbasierte Moebel duerfen maximal E1-Emissionswerte aufweisen. Spanplatten, MDF und Sperrholz muessen geprueft werden. REACH Anhang XVII reguliert Formaldehyd.', true, 'Chemikalien & Inhaltsstoffe', 'critical', true, 1, 'REACH (EG) Nr. 1907/2006 Anhang XVII', 'NVWA, ILT', 'Boete tot 900.000 EUR', ARRAY['E1-Klassifizierung sicherstellen', 'Pruefberichte anfordern', 'Lacke und Klebstoffe pruefen'], '[{"title":"REACH","url":"https://eur-lex.europa.eu/eli/reg/2006/1907"}]'::jsonb, ARRAY['Kleiderschrank', 'Kommode', 'Esstisch', 'Schreibtisch', 'Kinderbett'], ARRAY['Formaldehyd-Pruefung', 'E1-Zertifikat']),

('NL', 'furniture', 'EUDR Holzherkunft nachweisen', 'Nachhaltige und legale Holzherkunft dokumentieren.', 'Die EUDR verlangt Entwaldungsfreiheit und legale Holzherkunft. Geolokalisierung und Sorgfaltspflichten erforderlich. FSC/PEFC erleichtern den Nachweis.', true, 'Umwelt & Nachhaltigkeit', 'high', true, 3, 'EUDR (EU) 2023/1115', 'NVWA, ILT', 'Boete tot 450.000 EUR', ARRAY['Sorgfaltspflicht-System aufbauen', 'Geolokalisierung dokumentieren', 'FSC/PEFC Zertifizierung erwaegen'], '[{"title":"EUDR","url":"https://eur-lex.europa.eu/eli/reg/2023/1115"}]'::jsonb, ARRAY['Esstisch', 'Kleiderschrank', 'Bücherregal', 'Kommode'], ARRAY['EUDR-Sorgfaltserklaerung', 'FSC/PEFC-Zertifikat']),

('NL', 'cosmetics', 'CPNP-Notifizierung', 'Notifizierung im CPNP vor dem Inverkehrbringen.', 'Jedes kosmetische Produkt muss im CPNP notifiziert werden. Rahmenrezeptur, Kennzeichnung und verantwortliche Person muessen angegeben werden. Aktualisierung bei Formulierungsaenderungen.', true, 'Dokumentation & Rueckverfolgbarkeit', 'critical', true, 1, 'Kosmetikverordnung (EG) Nr. 1223/2009, Art. 13', 'NVWA, IGJ (Inspectie Gezondheidszorg en Jeugd)', 'Boete tot 900.000 EUR', ARRAY['CPNP-Zugang beantragen', 'Rahmenrezeptur vollstaendig eingeben', 'Kennzeichnung hochladen', 'Bei Aenderungen aktualisieren'], '[{"title":"CPNP","url":"https://ec.europa.eu/growth/tools-databases/cosing/cpnp/"}]'::jsonb, ARRAY['Tagescreme', 'Shampoo', 'Duschgel', 'Lippenstift', 'Sonnencreme'], ARRAY['CPNP-Notifizierung']),

('NL', 'cosmetics', 'Sicherheitsbewertung', 'Sicherheitsbewertung durch qualifizierten Bewerter.', 'Qualifizierter Sicherheitsbewerter muss Sicherheitsbericht (Teil A und B) erstellen. Alle Inhaltsstoffe, Exposition und Gesamtsicherheit werden bewertet. Muss vor Inverkehrbringen vorliegen.', true, 'Produktsicherheit & CE-Konformitaet', 'critical', true, 2, 'Kosmetikverordnung (EG) Nr. 1223/2009, Art. 10', 'NVWA, IGJ (Inspectie Gezondheidszorg en Jeugd)', 'Boete tot 900.000 EUR', ARRAY['Qualifizierten Bewerter beauftragen', 'Stabilitaetsdaten bereitstellen', 'PAO festlegen lassen', 'Bei Aenderungen aktualisieren'], '[{"title":"Kosmetikverordnung","url":"https://eur-lex.europa.eu/eli/reg/2009/1223"}]'::jsonb, ARRAY['Tagescreme', 'Shampoo', 'Sonnencreme', 'Haarfarbe', 'Parfum'], ARRAY['Sicherheitsbericht']),

('NL', 'toys', 'CE-Kennzeichnung Spielzeugrichtlinie', 'CE-Kennzeichnung nach Spielzeugrichtlinie.', 'Pruefung nach EN 71 (mechanisch, entflammbar, chemisch). CE-Zeichen sichtbar und dauerhaft anbringen. Konformitaetserklaerung vor Inverkehrbringen.', true, 'Produktsicherheit & CE-Konformitaet', 'critical', true, 1, 'Spielzeugrichtlinie 2009/48/EG', 'NVWA', 'Boete tot 900.000 EUR', ARRAY['EN 71 Pruefungen durchfuehren', 'CE-Zeichen mindestens 5 mm', 'Konformitaetserklaerung erstellen', 'Technische Dokumentation aufbauen'], '[{"title":"Spielzeugrichtlinie","url":"https://eur-lex.europa.eu/eli/dir/2009/48"}]'::jsonb, ARRAY['LEGO-Set', 'Puppe', 'Plüschtier', 'Spielzeugauto', 'Puzzle'], ARRAY['EN 71 Pruefberichte', 'EU-Konformitaetserklaerung']),

('NL', 'toys', 'Chemische Sicherheit EN 71-3', 'Migration bestimmter Elemente pruefen.', 'EN 71-3 legt Grenzwerte fuer 19 Elemente fest. Drei Materialkategorien werden unterschieden. Besonders fuer lackiertes Spielzeug und mundfaehige Produkte relevant.', true, 'Chemikalien & Inhaltsstoffe', 'critical', true, 2, 'EN 71-3, Spielzeugrichtlinie 2009/48/EG', 'NVWA', 'Boete tot 900.000 EUR', ARRAY['Alle 19 Elemente pruefen', 'Drei Materialkategorien beachten', 'Lackiertes Spielzeug testen'], '[{"title":"EN 71-3","url":"https://eur-lex.europa.eu/eli/dir/2009/48"}]'::jsonb, ARRAY['Puppe', 'Spielzeugauto', 'Holzbausteine', 'Fingerfarben', 'Knetmasse'], ARRAY['EN 71-3 Pruefbericht']),

('NL', 'food', 'LMIV-Kennzeichnung', 'Lebensmittelkennzeichnung nach LMIV.', 'Pflichtkennzeichnung: Bezeichnung, Zutaten, Allergene, Nettofuellmenge, MHD, Naehrwert, Herkunft, Lebensmittelunternehmer. In Landessprache erforderlich.', true, 'Kennzeichnung & Verpackung', 'critical', true, 1, 'LMIV (EU) Nr. 1169/2011', 'NVWA', 'Boete tot 900.000 EUR', ARRAY['14 Allergene hervorheben', 'Naehrwerttabelle vollstaendig', 'Mindestschriftgroesse 1,2 mm', 'In Landessprache kennzeichnen'], '[{"title":"LMIV","url":"https://eur-lex.europa.eu/eli/reg/2011/1169"}]'::jsonb, ARRAY['Milchprodukte', 'Fleisch', 'Brot', 'Gemuese', 'Tiefkuehlprodukte'], ARRAY['Kennzeichnungsentwurf', 'Naehrwertanalyse']),

('NL', 'food', 'HACCP-System', 'HACCP-Konzept implementieren.', 'Gefahrenanalyse, kritische Kontrollpunkte, Grenzwerte, Ueberwachung, Korrekturmassnahmen, Verifizierung und Dokumentation. Jaehrliche Revision.', true, 'Produktsicherheit & CE-Konformitaet', 'critical', true, 2, 'Verordnung (EG) Nr. 852/2004', 'NVWA', 'Boete tot 900.000 EUR', ARRAY['Sieben HACCP-Grundsaetze anwenden', 'CCP identifizieren und ueberwachen', 'Personal schulen', 'Jaehrliche Revision'], '[{"title":"Hygieneverordnung","url":"https://eur-lex.europa.eu/eli/reg/2004/852"}]'::jsonb, ARRAY['Milch', 'Fleisch', 'Fisch', 'Brot', 'Tiefkuehlprodukte'], ARRAY['HACCP-Plan', 'Schulungsnachweise']),

('NL', 'construction', 'CE-Kennzeichnung Bauprodukte', 'CE-Kennzeichnung nach Bauproduktenverordnung.', 'Harmonisierte Bauprodukte muessen CE-Kennzeichnung mit Leistungserklaerung (DoP) tragen. Wesentliche Merkmale deklarieren. AVCP-System beachten.', true, 'Produktsicherheit & CE-Konformitaet', 'critical', true, 1, 'Bauproduktenverordnung (EU) Nr. 305/2011', 'RWS, ILT', 'Boete tot 900.000 EUR', ARRAY['Harmonisierte Norm identifizieren', 'DoP erstellen', 'AVCP-System beachten', 'Notifizierte Stelle einschalten'], '[{"title":"Bauproduktenverordnung","url":"https://eur-lex.europa.eu/eli/reg/2011/305"}]'::jsonb, ARRAY['Mauerziegel', 'Dachziegel', 'Daemmplatte', 'Fenster', 'Bodenfliese'], ARRAY['Leistungserklaerung (DoP)', 'Pruefberichte']),

('NL', 'construction', 'REACH Baustoffe', 'REACH-Anforderungen fuer Bauchemikalien.', 'SVHC in Klebstoffen, Dichtstoffen und Beschichtungen beachten. SCIP-Meldepflicht. Sicherheitsdatenblaetter bereithalten.', true, 'Chemikalien & Inhaltsstoffe', 'high', true, 3, 'REACH (EG) Nr. 1907/2006', 'RWS, ILT', 'Boete tot 450.000 EUR', ARRAY['SVHC-Screening fuer Bauchemie', 'SCIP-Meldung durchfuehren', 'Sicherheitsdatenblaetter bereithalten'], '[{"title":"REACH","url":"https://eur-lex.europa.eu/eli/reg/2006/1907"}]'::jsonb, ARRAY['Montagekleber', 'Silikon', 'Bauschaum', 'Wandfarbe', 'Grundierung'], ARRAY['REACH-Konformitaet', 'Sicherheitsdatenblaetter']),

('NL', 'machinery', 'Maschinenverordnung Konformitaet', 'Neue EU-Maschinenverordnung 2023/1230 einhalten.', 'Ab Januar 2027 ersetzt die neue Maschinenverordnung die Maschinenrichtlinie. Neue Anforderungen an Cybersicherheit, KI und digitale Anleitungen. Risikobeurteilung nach EN ISO 12100 zentral.', true, 'Produktsicherheit & CE-Konformitaet', 'critical', true, 1, 'Maschinenverordnung (EU) 2023/1230', 'Inspectie SZW (Arbeidsinspectie)', 'Boete tot 900.000 EUR', ARRAY['Risikobeurteilung nach EN ISO 12100', 'Cybersicherheit beachten', 'Digitale Betriebsanleitung ab 2027', 'Notifizierte Stelle fuer Anhang-I-Maschinen'], '[{"title":"Maschinenverordnung","url":"https://eur-lex.europa.eu/eli/reg/2023/1230"}]'::jsonb, ARRAY['CNC-Fraesmaschine', 'Kreissaege', 'Abkantpresse', 'Industrieroboter', 'Gabelstapler'], ARRAY['Konformitaetserklaerung', 'Risikobeurteilung']),

('NL', 'machinery', 'Betriebsanleitung in Landessprache', 'Betriebsanleitung in der Landessprache bereitstellen.', 'Betriebsanleitung muss in der Amtssprache des Verwenderlandes vorliegen. Sicherheitshinweise, bestimmungsgemaesser Gebrauch, Wartung und Entsorgung muessen enthalten sein.', true, 'Dokumentation & Rueckverfolgbarkeit', 'high', true, 2, 'Maschinenverordnung (EU) 2023/1230, Anhang III', 'Inspectie SZW (Arbeidsinspectie)', 'Boete tot 450.000 EUR', ARRAY['Alle Anhang-III-Inhalte aufnehmen', 'Restrisiken dokumentieren', 'Bildliche Darstellungen verwenden', 'Wartungsintervalle definieren'], '[{"title":"Maschinenverordnung","url":"https://eur-lex.europa.eu/eli/reg/2023/1230"}]'::jsonb, ARRAY['CNC-Fraesmaschine', 'Kreissaege', 'Abkantpresse', 'Gabelstapler', 'Kompressor'], ARRAY['Betriebsanleitung', 'Sicherheitsdatenblatt']);


-- ============================================
-- ZUSAETZLICHE LAENDERSPEZIFISCHE EINTRAEGE
-- ============================================

-- FRANKREICH (FR) - Zusaetzliche Eintraege
INSERT INTO checklist_templates (country_code, category_key, title, description, detailed_description, mandatory, category, priority, document_required, sort_order, legal_basis, authority, penalties, tips, links, applicable_products, document_types) VALUES

('FR', 'electronics', 'Energieverbrauchskennzeichnung Frankreich', 'EU-Energielabel und franzoesische Zusatzanforderungen.', 'Neben dem EU-Energielabel verlangt Frankreich die EPREL-Registrierung und spezifische Angaben zur Energieeffizienz am Verkaufspunkt. Die DGCCRF kontrolliert die korrekte Anbringung und Aktualitaet der Labels im stationaeren und Online-Handel.', true, 'Kennzeichnung & Verpackung', 'high', true, 4, 'Verordnung (EU) 2017/1369, Code de la consommation', 'DGCCRF', 'Amende bis zu 75.000 EUR', ARRAY['EPREL-Registrierung durchfuehren', 'Energielabel A-G verwenden', 'Im Online-Shop Produktdatenblatt bereitstellen', 'QR-Code auf Label pruefen'], '[{"title": "EPREL", "url": "https://eprel.ec.europa.eu/"}, {"title": "DGCCRF", "url": "https://www.economie.gouv.fr/dgccrf"}]'::jsonb, ARRAY['Fernseher', 'Monitor', 'Laptop', 'Beamer'], ARRAY['Energielabel', 'EPREL-Registrierung']),

('FR', 'electronics', 'Cyber Resilience Act Vorbereitung', 'Cybersicherheitsanforderungen fuer vernetzte Elektronik vorbereiten.', 'Der EU Cyber Resilience Act wird ab 2027 Sicherheitsanforderungen fuer alle vernetzten Produkte stellen. Frankreich als Vorreiter bei der digitalen Sicherheit ueberwacht die Umsetzung ueber ANSSI. Bereits jetzt sollten Hersteller Security-by-Design und Updateprozesse implementieren.', false, 'Dokumentation & Rueckverfolgbarkeit', 'medium', false, 9, 'Cyber Resilience Act (EU) 2024/2847', 'ANSSI (Agence nationale de la securite des systemes d''information)', 'Ab Deadline Vertriebsverbot', ARRAY['Security-by-Design implementieren', 'Sicherheitsupdates fuer min. 5 Jahre planen', 'Schwachstellenmanagement einrichten', 'SBOM (Software Bill of Materials) vorbereiten'], '[{"title": "CRA", "url": "https://eur-lex.europa.eu/eli/reg/2024/2847"}, {"title": "ANSSI", "url": "https://www.ssi.gouv.fr/"}]'::jsonb, ARRAY['Smartphone', 'Laptop', 'Router', 'Smart-Home-Hub', 'Ueberwachungskamera'], ARRAY['Cybersicherheitskonzept', 'SBOM']),

('FR', 'electronics', 'Verfuegbarkeit von Ersatzteilen (AGEC)', 'Ersatzteilversorgung gemaess AGEC-Gesetz sicherstellen.', 'Das franzoesische AGEC-Gesetz verpflichtet Hersteller bestimmter Elektronikprodukte, Ersatzteile fuer einen definierten Zeitraum verfuegbar zu machen. Fuer Smartphones betraegt die Frist 5 Jahre, fuer andere Elektronik variiert sie. Der Preis der Ersatzteile muss veroeffentlicht werden.', true, 'Umwelt & Nachhaltigkeit', 'high', false, 10, 'Loi AGEC Art. 19', 'DGCCRF', 'Amende bis zu 15.000 EUR', ARRAY['Ersatzteilversorgung fuer min. 5 Jahre sicherstellen', 'Ersatzteilpreise veroeffentlichen', 'Lieferzeiten unter 15 Werktagen halten', 'Ersatzteile auch fuer unabhaengige Reparateure bereitstellen'], '[{"title": "AGEC Ersatzteile", "url": "https://www.ecologie.gouv.fr/loi-anti-gaspillage-economie-circulaire"}]'::jsonb, ARRAY['Smartphone', 'Laptop', 'Fernseher', 'Tablet', 'Drucker'], ARRAY['Ersatzteilkatalog', 'Preisliste']),

('FR', 'textiles', 'Verbot der Vernichtung unverkaufter Textilien', 'AGEC-Verbot der Vernichtung unverkaufter Non-Food-Produkte.', 'Seit Januar 2022 verbietet das AGEC-Gesetz die Vernichtung unverkaufter Non-Food-Produkte, einschliesslich Textilien. Unverkaufte Ware muss gespendet, recycelt oder wiederverwendet werden. Frankreich ist Vorreiter bei diesem Verbot, das EU-weit Nachahmer findet.', true, 'Umwelt & Nachhaltigkeit', 'high', false, 5, 'Loi AGEC Art. 35', 'DGCCRF, ADEME', 'Amende bis zu 15.000 EUR pro Verstoss', ARRAY['Spendenstrategie fuer unverkaufte Ware entwickeln', 'Recycling-Partner identifizieren', 'Upcycling-Konzepte pruefen', 'Dokumentation der Verwertungswege'], '[{"title": "AGEC Vernichtungsverbot", "url": "https://www.ecologie.gouv.fr/loi-anti-gaspillage-economie-circulaire"}]'::jsonb, ARRAY['T-Shirt', 'Jeans', 'Kleid', 'Jacke', 'Schuhe', 'Pullover'], ARRAY['Verwertungsnachweis', 'Spendenbelege']),

('FR', 'packaging', 'Bonus-Malus fuer Verpackungen (Eco-Modulation)', 'Eco-Modulation der Citeo-Beitraege beachten.', 'Citeo wendet ein Bonus-Malus-System an: umweltfreundliche Verpackungen erhalten Beitragsreduktionen, problematische Verpackungen werden bestraft. Kriterien sind Recyclingfaehigkeit, Recyclatanteil, Reduzierung und Wiederverwendbarkeit. Schwarze Kunststoffe, PVC und nicht recycelbare Verbunde erhalten Malus.', true, 'Umwelt & Nachhaltigkeit', 'medium', false, 4, 'Code de l''environnement, Cahier des charges Citeo', 'ADEME, Citeo', 'Oekonomischer Malus bis zu +100% auf Beitraege', ARRAY['Recyclingfaehige Materialien bevorzugen', 'Schwarze Kunststoffe vermeiden', 'PVC-freie Alternativen waehlen', 'Recyclatanteil erhoehen fuer Bonus'], '[{"title": "Citeo Eco-Modulation", "url": "https://www.citeo.com/"}]'::jsonb, ARRAY['PET-Flasche', 'Wellpappkarton', 'Standbodenbeutel', 'Faltschachtel', 'Kunststofftüte'], ARRAY['Materialanalyse', 'Recyclingfaehigkeits-Bewertung']),

('FR', 'cosmetics', 'Franzoesische Inhaltsstoff-Beschraenkungen', 'Zusaetzliche franzoesische Beschraenkungen fuer Kosmetik-Inhaltsstoffe.', 'Frankreich hat in einigen Bereichen strengere Anforderungen als die EU-Kosmetikverordnung. Insbesondere bei Nanomaterialien, endokrinen Disruptoren und bestimmten Konservierungsstoffen gibt es nationale Empfehlungen und Beschraenkungen der ANSM. Die franzoesische Verbraucherschutzorganisation UFC-Que Choisir veroeffentlicht regelmaessig Bewertungen.', true, 'Chemikalien & Inhaltsstoffe', 'high', true, 5, 'Code de la sante publique, Kosmetikverordnung (EG) Nr. 1223/2009', 'ANSM', 'Amende bis zu 150.000 EUR', ARRAY['ANSM-Empfehlungen beachten', 'Endokrine Disruptoren substituieren', 'Nanomaterialien melden', 'UFC-Que Choisir Bewertungen verfolgen'], '[{"title": "ANSM Cosmetiques", "url": "https://ansm.sante.fr/page/cosmetiques"}]'::jsonb, ARRAY['Tagescreme', 'Shampoo', 'Sonnencreme', 'Haarfarbe', 'Deodorant'], ARRAY['ANSM-Konformitaetsnachweis', 'Inhaltsstoff-Analyse']),

('FR', 'food', 'Franzoesische Herkunftskennzeichnung Lebensmittel', 'Erweiterte Herkunftskennzeichnung fuer bestimmte Lebensmittel in Frankreich.', 'Frankreich hat fuer bestimmte Produktkategorien (Fleisch, Milch, Honig) erweiterte Herkunftskennzeichnungspflichten eingefuehrt. Die Angabe des Aufzuchtlandes und des Schlachtlandes ist bei Fleisch verpflichtend. Fuer Milch muss das Herkunftsland angegeben werden. Die Kennzeichnung muss auf Franzoesisch erfolgen.', true, 'Kennzeichnung & Verpackung', 'high', false, 3, 'Decret n° 2022-65, LMIV (EU) 1169/2011', 'DGCCRF, DGAL', 'Amende bis zu 75.000 EUR', ARRAY['Herkunftsland bei Fleisch und Milch angeben', 'Aufzucht- und Schlachtland getrennt', 'Franzoesische Sprache verwenden', 'AOP/AOC/IGP korrekt kennzeichnen'], '[{"title": "DGCCRF Origine", "url": "https://www.economie.gouv.fr/dgccrf"}]'::jsonb, ARRAY['Rindfleisch', 'Milch', 'Honig', 'Kaese', 'Wein'], ARRAY['Herkunftsnachweis', 'Kennzeichnungsentwurf']),

('FR', 'construction', 'RT2020 / RE2020 Energieeffizienz', 'Franzoesische Energievorschriften RE2020 fuer Bauprodukte beachten.', 'Die franzoesische RE2020 (Reglementation Environnementale 2020) stellt strenge Anforderungen an die Energieeffizienz und den CO2-Fussabdruck von Neubauten. Bauprodukte muessen mit ihren Umweltdeklarationen (FDES) zur Berechnung beitragen. Die FDES ist das franzoesische Aequivalent der EPD.', true, 'Umwelt & Nachhaltigkeit', 'high', true, 4, 'RE2020, Decret n° 2021-1004', 'CSTB, INIES-Datenbank', 'Kein direktes Bussgeld, aber Marktzugangsbeschraenkung', ARRAY['FDES (Fiche de Declaration Environnementale et Sanitaire) erstellen', 'In INIES-Datenbank registrieren', 'CO2-Werte fuer RE2020-Berechnung bereitstellen', 'Waermeleitfaehigkeit deklarieren'], '[{"title": "RE2020", "url": "https://www.ecologie.gouv.fr/reglementation-environnementale-re2020"}, {"title": "INIES", "url": "https://www.inies.fr/"}]'::jsonb, ARRAY['Daemmplatte', 'Fenster', 'Mauerziegel', 'Heizungssystem', 'Lueftungsanlage'], ARRAY['FDES', 'Waermeleitfaehigkeitsnachweis']);

-- OESTERREICH (AT) - Zusaetzliche Eintraege
INSERT INTO checklist_templates (country_code, category_key, title, description, detailed_description, mandatory, category, priority, document_required, sort_order, legal_basis, authority, penalties, tips, links, applicable_products, document_types) VALUES

('AT', 'electronics', 'Bundesnetzagentur AT (RTR) Funktechnik', 'Funktechnische Geraete bei der RTR registrieren.', 'In Oesterreich ueberwacht die Rundfunk und Telekom Regulierungs-GmbH (RTR) die Einhaltung der Funkanlagenrichtlinie. Funkgeraete muessen CE-konform sein und die oesterreichischen Frequenzzuteilungen beachten. Die RTR fuehrt Marktueberwachung durch und kann nicht-konforme Geraete vom Markt nehmen.', true, 'Produktsicherheit & CE-Konformitaet', 'high', true, 4, 'Telekommunikationsgesetz 2021, RED 2014/53/EU', 'RTR (Rundfunk und Telekom Regulierungs-GmbH)', 'Geldstrafe bis zu 50.000 EUR', ARRAY['RED-Konformitaet sicherstellen', 'Oesterreichische Frequenznutzung beachten', 'RTR-Anforderungen pruefen', 'CE-Kennzeichnung korrekt anbringen'], '[{"title": "RTR", "url": "https://www.rtr.at/"}, {"title": "RED", "url": "https://eur-lex.europa.eu/eli/dir/2014/53"}]'::jsonb, ARRAY['Smartphone', 'Router', 'WLAN-Repeater', 'Bluetooth-Lautsprecher', 'Smart-Home-Hub'], ARRAY['RED-Pruefbericht', 'Frequenznachweis']),

('AT', 'textiles', 'Oesterreichische Textilkennzeichnung', 'Textilkennzeichnung nach EU-Verordnung mit oesterreichischen Besonderheiten.', 'Die Textilkennzeichnung in Oesterreich folgt der EU-Verordnung, muss aber in deutscher Sprache erfolgen. Die AGES ueberwacht die Einhaltung. Oesterreich hat eine aktive Marktueberwachung fuer Textilien, insbesondere bei Kinderbekleidung und Arbeitsbekleidung.', true, 'Kennzeichnung & Verpackung', 'high', false, 3, 'Textilkennzeichnungsverordnung (EU) Nr. 1007/2011', 'AGES', 'Geldstrafe bis zu 25.000 EUR', ARRAY['Deutsche Sprache fuer Kennzeichnung', 'AGES-Anforderungen beachten', 'Kinderbekleidung besonders pruefen', 'Arbeitsbekleidung nach EN-Normen'], '[{"title": "Textilkennzeichnung", "url": "https://eur-lex.europa.eu/eli/reg/2011/1007"}, {"title": "AGES", "url": "https://www.ages.at/"}]'::jsonb, ARRAY['T-Shirt', 'Hemd', 'Jeans', 'Kinderbekleidung', 'Arbeitsbekleidung'], ARRAY['Textilkennzeichnung', 'Faseranalyse']),

('AT', 'batteries', 'Oesterreichische Batteriesammlung und -verwertung', 'Registrierung bei ERA und Beteiligung an Sammelsystem.', 'In Oesterreich muessen Batteriehersteller und -importeure bei der ERA (Elektroaltgeraete Koordinierungsstelle) registriert sein und sich an einem zugelassenen Sammelsystem beteiligen. Die oesterreichischen Sammelquoten sind ambitioniert und werden streng kontrolliert.', true, 'Umwelt & Nachhaltigkeit', 'critical', true, 3, 'Batterienverordnung, EU-Batterieverordnung (EU) 2023/1542', 'ERA, Umweltbundesamt (UBA AT)', 'Geldstrafe bis zu 50.000 EUR', ARRAY['ERA-Registrierung durchfuehren', 'Sammelsystem beitreten', 'Jahresmeldungen einreichen', 'Sammelquoten einhalten'], '[{"title": "ERA", "url": "https://www.era-gmbh.at/"}]'::jsonb, ARRAY['Li-Ion 18650', 'AA-Batterie', 'E-Bike-Akku', 'Smartphone-Akku', 'Starterbatterie'], ARRAY['ERA-Registrierung', 'Mengenmeldung']),

('AT', 'furniture', 'Oesterreichisches Umweltzeichen fuer Moebel', 'Oesterreichisches Umweltzeichen als Qualitaetsmerkmal.', 'Das Oesterreichische Umweltzeichen (UZ 06 fuer Moebel) ist ein staatliches Guetesiegel fuer umweltfreundliche Moebel. Es stellt strenge Anforderungen an Emissionen, Materialien und Herstellung. Das Umweltzeichen ist bei oeffentlichen Beschaffungen und umweltbewussten Verbrauchern anerkannt.', false, 'Umwelt & Nachhaltigkeit', 'medium', true, 4, 'Umweltzeichengesetz', 'Bundesministerium fuer Klimaschutz', 'Keine Pflicht, Marktvorteil', ARRAY['Umweltzeichen UZ 06 beantragen', 'Emissionsanforderungen einhalten', 'Holz aus nachhaltiger Forstwirtschaft', 'Loesemittelarme Lacke verwenden'], '[{"title": "Oesterreichisches Umweltzeichen", "url": "https://www.umweltzeichen.at/"}]'::jsonb, ARRAY['Esstisch', 'Kleiderschrank', 'Buerstuhl', 'Kinderbett', 'Sofa'], ARRAY['Umweltzeichen-Zertifikat']),

('AT', 'cosmetics', 'BASG Kosmetik-Ueberwachung Oesterreich', 'Kosmetikovigilanz und Marktueberwachung durch BASG/AGES.', 'In Oesterreich ueberwacht das BASG (Bundesamt fuer Sicherheit im Gesundheitswesen) zusammen mit der AGES die Sicherheit kosmetischer Produkte. Unerwuenschte Wirkungen muessen gemeldet werden. Die AGES fuehrt Produktanalysen und Marktueberwachung durch.', true, 'Produktsicherheit & CE-Konformitaet', 'high', true, 3, 'Kosmetikverordnung (EG) Nr. 1223/2009, LMSVG', 'BASG, AGES', 'Geldstrafe bis zu 36.000 EUR', ARRAY['Kosmetikovigilanz-System einrichten', 'Unerwuenschte Wirkungen an BASG melden', 'AGES-Leitlinien beachten', 'Produktdossier aktuell halten'], '[{"title": "BASG", "url": "https://www.basg.gv.at/"}, {"title": "AGES", "url": "https://www.ages.at/"}]'::jsonb, ARRAY['Tagescreme', 'Shampoo', 'Sonnencreme', 'Parfum', 'Zahnpasta'], ARRAY['Kosmetikovigilanz-Bericht', 'Sicherheitsbewertung']),

('AT', 'construction', 'OIB-Richtlinien Bauprodukte Oesterreich', 'Oesterreichische OIB-Richtlinien fuer Bauprodukte beachten.', 'Das Oesterreichische Institut fuer Bautechnik (OIB) erstellt die OIB-Richtlinien, die als harmonisierte nationale Bauvorschriften gelten. Sie definieren Anforderungen an Brandschutz, Waermeschutz, Schallschutz und Standsicherheit. Bauprodukte muessen diese Anforderungen erfuellen.', true, 'Produktsicherheit & CE-Konformitaet', 'high', true, 3, 'OIB-Richtlinien, Bauproduktenverordnung 305/2011', 'OIB, Landesbehoerden', 'Geldstrafe und baurechtliche Konsequenzen', ARRAY['OIB-Richtlinien 1-6 beachten', 'Brandschutzanforderungen nach OIB-RL 2', 'Waermeschutz nach OIB-RL 6', 'Oesterreichische Normen (OENORMEN) einhalten'], '[{"title": "OIB", "url": "https://www.oib.or.at/"}]'::jsonb, ARRAY['Daemmplatte', 'Fenster', 'Mauerziegel', 'Dachziegel', 'Bodenfliese'], ARRAY['OIB-Konformitaetsnachweis', 'Brandpruefbericht']);

-- ITALIEN (IT) - Zusaetzliche Eintraege
INSERT INTO checklist_templates (country_code, category_key, title, description, detailed_description, mandatory, category, priority, document_required, sort_order, legal_basis, authority, penalties, tips, links, applicable_products, document_types) VALUES

('IT', 'electronics', 'Italienische Verbraucherschutzanforderungen Elektronik', 'Codice del Consumo Anforderungen fuer Elektronik beachten.', 'Der Codice del Consumo (Verbrauchergesetzbuch) stellt in Italien erweiterte Anforderungen an Verbraucherschutz und Gewaehrleistung. Die gesetzliche Gewaehrleistung betraegt 2 Jahre. Fuer Elektronik gelten besondere Informationspflichten zu Garantie, Reparierbarkeit und Software-Updates.', true, 'Dokumentation & Rueckverfolgbarkeit', 'high', false, 5, 'Codice del Consumo (D.Lgs. 206/2005), GPSR (EU) 2023/988', 'MISE, Camere di Commercio', 'Sanzione fino a 100.000 EUR', ARRAY['2 Jahre Gewaehrleistung dokumentieren', 'Garantiebedingungen auf Italienisch', 'Informationen zu Software-Updates bereitstellen', 'Kontaktdaten fuer italienische Verbraucher'], '[{"title": "Codice del Consumo", "url": "https://www.normattiva.it/uri-res/N2Ls?urn:nir:stato:decreto.legislativo:2005-09-06;206"}]'::jsonb, ARRAY['Smartphone', 'Laptop', 'Fernseher', 'Tablet', 'Router'], ARRAY['Garantiebedingungen (IT)', 'Verbraucherinformationen']),

('IT', 'textiles', 'Italienische Made-in-Kennzeichnung', 'Made in Italy Kennzeichnung und Herkunftsschutz.', 'Italien hat strenge Regeln fuer die Made-in-Italy-Kennzeichnung. Missbraeuchliche Verwendung ist strafbar. Die Guardia di Finanza kontrolliert die Einhaltung. Bei Textilien muss die wesentliche Verarbeitung in Italien erfolgt sein, um Made in Italy verwenden zu duerfen.', true, 'Kennzeichnung & Verpackung', 'high', false, 4, 'D.L. 135/2009, Codice della proprieta industriale', 'MISE, Guardia di Finanza', 'Sanzione fino a 100.000 EUR', ARRAY['Made in Italy nur bei tatsaechlicher Fertigung in Italien', 'Herkunftsland korrekt angeben', 'Irrefuehrende Angaben vermeiden', 'Guardia di Finanza Kontrollen beachten'], '[{"title": "MISE Made in Italy", "url": "https://www.mise.gov.it/"}]'::jsonb, ARRAY['T-Shirt', 'Hemd', 'Anzug', 'Kleid', 'Schuhe', 'Lederjacke'], ARRAY['Herkunftsnachweis', 'Fertigungsnachweis']),

('IT', 'packaging', 'Italienische Verpackungskennzeichnung D.Lgs. 116/2020', 'Erweiterte Umweltkennzeichnung fuer Verpackungen in Italien.', 'Seit Januar 2023 ist die erweiterte Verpackungskennzeichnung in Italien verpflichtend. Alle Verpackungen muessen mit Materialidentifikation, Entsorgungshinweisen und dem richtigen Sammelsystem gekennzeichnet werden. Die Kennzeichnung muss auf Italienisch sein und dem CONAI-System entsprechen.', true, 'Kennzeichnung & Verpackung', 'critical', false, 3, 'D.Lgs. 116/2020, D.Lgs. 152/2006', 'CONAI, ISPRA', 'Sanzione fino a 100.000 EUR', ARRAY['Materialcodierung nach UNI EN-Normen', 'Entsorgungshinweise auf Italienisch', 'Trennhinweise fuer Verbundverpackungen', 'CONAI-Leitlinien beachten'], '[{"title": "D.Lgs. 116/2020", "url": "https://www.normattiva.it/"}]'::jsonb, ARRAY['PET-Flasche', 'Wellpappkarton', 'Glasflasche', 'Getränkedose', 'Faltschachtel'], ARRAY['Kennzeichnungsentwurf (IT)']),

('IT', 'cosmetics', 'Ministero della Salute Kosmetik-Registrierung', 'Italienische Kosmetik-Meldepflichten an Ministero della Salute.', 'Das italienische Gesundheitsministerium fuehrt ein nationales Register fuer kosmetische Mittel. Neben der CPNP-Notifizierung muessen bestimmte Produktkategorien auch national gemeldet werden. Die Kosmetikovigilanz erfolgt ueber das Ministero della Salute.', true, 'Dokumentation & Rueckverfolgbarkeit', 'high', true, 3, 'D.Lgs. 204/2015, Kosmetikverordnung (EG) Nr. 1223/2009', 'Ministero della Salute', 'Sanzione fino a 100.000 EUR', ARRAY['CPNP-Notifizierung UND nationale Meldung', 'Verantwortliche Person in der EU benennen', 'Italienische Kennzeichnung sicherstellen', 'Kosmetikovigilanz-System einrichten'], '[{"title": "Ministero Salute Cosmetici", "url": "https://www.salute.gov.it/portale/cosmetici/"}]'::jsonb, ARRAY['Tagescreme', 'Shampoo', 'Sonnencreme', 'Parfum', 'Lippenstift'], ARRAY['Nationale Meldung (IT)', 'CPNP-Notifizierung']),

('IT', 'construction', 'Italienische NTC Erdbebensicherheit', 'Norme Tecniche per le Costruzioni - Erdbebensicherheit.', 'Italien hat aufgrund der seismischen Aktivitaet besonders strenge Anforderungen an die Erdbebensicherheit. Die NTC (Norme Tecniche per le Costruzioni) definieren Anforderungen an die Erdbebensicherheit von Bauprodukten. Baustoffe muessen fuer die relevante Erdbebenzone geeignet sein.', true, 'Produktsicherheit & CE-Konformitaet', 'critical', true, 3, 'NTC 2018, D.M. 17.01.2018', 'Consiglio Superiore dei Lavori Pubblici', 'Sanzione und strafrechtliche Konsequenzen', ARRAY['Erdbebenzone des Einsatzortes beachten', 'NTC-konforme Materialien verwenden', 'Pruefzeugnisse fuer seismische Belastung', 'Italienische Zulassung fuer strukturelle Bauprodukte'], '[{"title": "NTC 2018", "url": "https://www.gazzettaufficiale.it/"}]'::jsonb, ARRAY['Fertigbeton', 'Mauerziegel', 'Stahltraeger', 'Bewehrungsstahl', 'Mauer'], ARRAY['NTC-Pruefzeugnis', 'Seismische Zulassung']);

-- SPANIEN (ES) - Zusaetzliche Eintraege
INSERT INTO checklist_templates (country_code, category_key, title, description, detailed_description, mandatory, category, priority, document_required, sort_order, legal_basis, authority, penalties, tips, links, applicable_products, document_types) VALUES

('ES', 'electronics', 'Spanische Kennzeichnung in Castellano', 'Produktinformationen und Anleitungen in Spanisch (Castellano).', 'Alle Produktinformationen, Bedienungsanleitungen und Sicherheitshinweise muessen in Spanisch (Castellano) bereitgestellt werden. In den autonomen Gemeinschaften mit Koamtssprachen (Katalonien, Baskenland, Galicien, Valencia) koennen zusaetzliche Sprachanforderungen gelten.', true, 'Kennzeichnung & Verpackung', 'high', false, 5, 'Ley General para la Defensa de los Consumidores, Real Decreto 1468/1988', 'Ministerio de Consumo', 'Multa hasta 100.000 EUR', ARRAY['Bedienungsanleitung auf Castellano bereitstellen', 'In Katalonien auch Katalanisch erwaegen', 'Sicherheitshinweise zweisprachig', 'Online-Informationen auf Spanisch'], '[{"title": "Consumo", "url": "https://www.consumo.gob.es/"}]'::jsonb, ARRAY['Smartphone', 'Laptop', 'Fernseher', 'Router', 'Drucker'], ARRAY['Bedienungsanleitung (ES)', 'Sicherheitshinweise (ES)']),

('ES', 'textiles', 'Spanische Textilkennzeichnung', 'Textilkennzeichnung in Castellano fuer den spanischen Markt.', 'Die Textilkennzeichnung muss in Spanien auf Castellano erfolgen. Die Faserzusammensetzung, Pflegehinweise und Herstellerangaben muessen in spanischer Sprache angegeben werden. Das Ministerio de Consumo ueberwacht die Einhaltung.', true, 'Kennzeichnung & Verpackung', 'high', false, 3, 'Textilkennzeichnungsverordnung (EU) Nr. 1007/2011, Real Decreto 928/1987', 'Ministerio de Consumo', 'Multa hasta 100.000 EUR', ARRAY['Kennzeichnung auf Castellano', 'Spanische Faserbezeichnungen verwenden', 'Pflegesymbole nach ISO 3758', 'Handelsregisterangaben beachten'], '[{"title": "Textilkennzeichnung", "url": "https://eur-lex.europa.eu/eli/reg/2011/1007"}]'::jsonb, ARRAY['T-Shirt', 'Hemd', 'Jeans', 'Kleid', 'Jacke', 'Schuhe'], ARRAY['Textilkennzeichnung (ES)']),

('ES', 'batteries', 'Spanische Batterie-Registrierung', 'Registrierung im spanischen System fuer Altbatterien.', 'In Spanien muessen Batteriehersteller und -importeure sich beim MITERD registrieren und einem zugelassenen Ruecknahmesystem beitreten. Ecopilas und ERP Espana sind die wichtigsten Sammelorganisationen.', true, 'Umwelt & Nachhaltigkeit', 'critical', true, 3, 'Real Decreto 106/2008, EU-Batterieverordnung (EU) 2023/1542', 'MITERD', 'Multa hasta 600.000 EUR', ARRAY['Beim MITERD registrieren', 'Ecopilas oder ERP Espana beitreten', 'Jahresmeldungen einreichen', 'Sammelquoten einhalten'], '[{"title": "Ecopilas", "url": "https://www.ecopilas.es/"}]'::jsonb, ARRAY['Li-Ion 18650', 'AA-Batterie', 'E-Bike-Akku', 'Smartphone-Akku'], ARRAY['MITERD-Registrierung', 'Sammelsystem-Mitgliedschaft']),

('ES', 'cosmetics', 'AEMPS Kosmetik-Ueberwachung Spanien', 'Kosmetikovigilanz und Marktueberwachung durch AEMPS.', 'Die AEMPS (Agencia Espanola de Medicamentos y Productos Sanitarios) ueberwacht die Sicherheit kosmetischer Produkte in Spanien. Unerwuenschte Wirkungen muessen gemeldet werden. Die Kennzeichnung muss auf Castellano sein.', true, 'Produktsicherheit & CE-Konformitaet', 'high', true, 3, 'Real Decreto 1599/1997, Kosmetikverordnung (EG) Nr. 1223/2009', 'AEMPS', 'Multa hasta 200.000 EUR', ARRAY['AEMPS-Anforderungen beachten', 'Unerwuenschte Wirkungen melden', 'Kennzeichnung auf Castellano', 'Verantwortliche Person in EU benennen'], '[{"title": "AEMPS Cosmeticos", "url": "https://www.aemps.gob.es/cosmeticosHigiene/cosmeticos/home.htm"}]'::jsonb, ARRAY['Tagescreme', 'Shampoo', 'Sonnencreme', 'Parfum', 'Zahnpasta'], ARRAY['AEMPS-Meldung', 'Sicherheitsbewertung']),

('ES', 'construction', 'CTE (Codigo Tecnico de la Edificacion)', 'Spanische Bauvorschriften CTE fuer Bauprodukte beachten.', 'Der CTE ist das zentrale technische Regelwerk fuer das Bauen in Spanien. Er definiert Anforderungen an Standsicherheit, Brandschutz, Gesundheit, Energieeffizienz und Laermschutz. Bauprodukte muessen den CTE-Anforderungen entsprechen und die entsprechenden Nachweise erbringen.', true, 'Produktsicherheit & CE-Konformitaet', 'high', true, 3, 'Codigo Tecnico de la Edificacion (RD 314/2006)', 'Ministerio de Transportes, Movilidad y Agenda Urbana', 'Sancion und baurechtliche Konsequenzen', ARRAY['CTE-Anforderungen fuer die Produktkategorie pruefen', 'Spanische UNE-Normen beachten', 'Brandschutz DB-SI Anforderungen einhalten', 'Energieeffizienz DB-HE Anforderungen erfuellen'], '[{"title": "CTE", "url": "https://www.codigotecnico.org/"}]'::jsonb, ARRAY['Daemmplatte', 'Fenster', 'Mauerziegel', 'Dachziegel', 'Bodenfliese'], ARRAY['CTE-Konformitaetsnachweis', 'UNE-Pruefberichte']);

-- NIEDERLANDE (NL) - Zusaetzliche Eintraege
INSERT INTO checklist_templates (country_code, category_key, title, description, detailed_description, mandatory, category, priority, document_required, sort_order, legal_basis, authority, penalties, tips, links, applicable_products, document_types) VALUES

('NL', 'electronics', 'ILT Marktueberwachung Niederlande', 'Niederlaendische Marktueberwachung durch ILT beachten.', 'Die ILT (Inspectie Leefomgeving en Transport) fuehrt in den Niederlanden eine strenge Marktueberwachung durch. Sie prueft CE-Kennzeichnung, RoHS-Konformitaet, WEEE-Registrierung und Energielabel. Die Niederlande sind bekannt fuer eine konsequente Durchsetzung mit hohen Bussgeldern.', true, 'Dokumentation & Rueckverfolgbarkeit', 'high', true, 5, 'Warenwet, WEEE 2012/19/EU, RoHS 2011/65/EU', 'ILT', 'Boete tot 900.000 EUR', ARRAY['Alle EU-Konformitaetsdokumente vorhalten', 'ILT-Anfragen innerhalb der Frist beantworten', 'Niederlaendische Kennzeichnungsanforderungen beachten', 'Proaktive Compliance-Pruefung durchfuehren'], '[{"title": "ILT", "url": "https://www.ilent.nl/"}]'::jsonb, ARRAY['Smartphone', 'Laptop', 'Fernseher', 'Router', 'Drucker'], ARRAY['Technische Dokumentation', 'Konformitaetserklaerung']),

('NL', 'batteries', 'Stichting Batterijen Niederlande', 'Registrierung bei Stichting Batterijen fuer das niederlaendische Sammelsystem.', 'In den Niederlanden ist Stichting Batterijen die Organisation fuer die Sammlung und das Recycling von Altbatterien. Hersteller und Importeure muessen sich registrieren und Beitraege zahlen. Die Niederlande haben ambitionierte Sammelquoten.', true, 'Umwelt & Nachhaltigkeit', 'critical', true, 3, 'Besluit batterijen en accu''s, EU-Batterieverordnung (EU) 2023/1542', 'ILT, Stichting Batterijen', 'Boete tot 900.000 EUR', ARRAY['Bei Stichting Batterijen registrieren', 'Beitraege zahlen', 'Sammelquoten einhalten', 'Jahresmeldungen einreichen'], '[{"title": "Stichting Batterijen", "url": "https://www.stichtingbatterijen.nl/"}]'::jsonb, ARRAY['Li-Ion 18650', 'AA-Batterie', 'E-Bike-Akku', 'Smartphone-Akku'], ARRAY['Stichting-Batterijen-Registrierung', 'Mengenmeldung']),

('NL', 'cosmetics', 'NVWA Kosmetik-Kontrolle Niederlande', 'Niederlaendische Marktkontrolle fuer Kosmetik durch NVWA.', 'Die NVWA kontrolliert kosmetische Produkte in den Niederlanden. Die Kontrollen umfassen Kennzeichnung, verbotene Inhaltsstoffe und Sicherheitsbewertung. Die Niederlande haben ein transparentes Kontrollsystem mit Veroeffentlichung der Ergebnisse.', true, 'Produktsicherheit & CE-Konformitaet', 'high', true, 3, 'Warenwet, Kosmetikverordnung (EG) Nr. 1223/2009', 'NVWA', 'Boete tot 450.000 EUR', ARRAY['NVWA-Anforderungen beachten', 'Kennzeichnung auf Niederlaendisch', 'Sicherheitsbewertung aktuell halten', 'NVWA-Kontrollen proaktiv vorbereiten'], '[{"title": "NVWA Cosmetica", "url": "https://www.nvwa.nl/onderwerpen/cosmetica"}]'::jsonb, ARRAY['Tagescreme', 'Shampoo', 'Sonnencreme', 'Parfum', 'Zahnpasta'], ARRAY['Sicherheitsbewertung', 'Kennzeichnung (NL)']),

('NL', 'packaging', 'Niederlaendische Kunststoffsteuer', 'Plastic Tax fuer Kunststoffverpackungen in den Niederlanden.', 'Die Niederlande erheben eine Steuer auf Kunststoffverpackungen (plastbelasting). Diese Steuer kommt zusaetzlich zu den Afvalfonds-Beitraegen. Sie soll den Einsatz von Recycling-Kunststoff und alternativen Materialien foerdern. Die Steuerpflicht trifft den Erstinverkehrbringer.', true, 'Umwelt & Nachhaltigkeit', 'high', true, 4, 'Wet belasting op kunststof', 'Belastingdienst, ILT', 'Boete tot 450.000 EUR', ARRAY['Kunststoffsteuer berechnen und zahlen', 'Recyclatanteil zur Reduktion nutzen', 'Alternative Materialien pruefen', 'Steuerpflicht beim Erstinverkehrbringen'], '[{"title": "Plastic Tax NL", "url": "https://www.belastingdienst.nl/"}]'::jsonb, ARRAY['PET-Flasche', 'Kunststofftüte', 'Blisterverpackung', 'PP-Becher', 'Standbodenbeutel'], ARRAY['Steuermeldung', 'Materialanalyse']),

('NL', 'construction', 'Bouwbesluit Anforderungen Niederlande', 'Niederlaendische Bauvorschriften Bouwbesluit beachten.', 'Das Bouwbesluit (Bauverordnung) definiert die technischen Anforderungen an Bauwerke in den Niederlanden. Es regelt Standsicherheit, Brandschutz, Gesundheit, Energieeffizienz und Umwelt. Bauprodukte muessen den Bouwbesluit-Anforderungen entsprechen. Die Niederlande haben strenge Energieeffizienzanforderungen.', true, 'Produktsicherheit & CE-Konformitaet', 'high', true, 4, 'Bouwbesluit 2012, Bauproduktenverordnung 305/2011', 'RWS, ILT', 'Boete tot 450.000 EUR', ARRAY['Bouwbesluit-Anforderungen pruefen', 'NEN-Normen einhalten', 'Energieeffizienz nach BENG', 'Umweltleistungsdatenbank Milieudatabase nutzen'], '[{"title": "Bouwbesluit", "url": "https://www.rijksoverheid.nl/onderwerpen/bouwregelgeving"}]'::jsonb, ARRAY['Daemmplatte', 'Fenster', 'Mauerziegel', 'Dachziegel', 'Bodenfliese'], ARRAY['Bouwbesluit-Konformitaet', 'NEN-Pruefberichte']),

('NL', 'food', 'Niederlaendische Nahrungsergaenzungsmittel', 'Niederlaendische Anforderungen an Nahrungsergaenzungsmittel.', 'Die Niederlande haben ueber die NVWA ein strenges Kontrollregime fuer Nahrungsergaenzungsmittel. Das Warenwet-Besluit regelt die zulaessigen Vitamine, Mineralstoffe und Hoechstmengen. Novel Food muss gesondert zugelassen werden. Die Kennzeichnung muss auf Niederlaendisch sein.', true, 'Kennzeichnung & Verpackung', 'high', true, 4, 'Warenwet-Besluit Voedingssupplementen, LMIV (EU) 1169/2011', 'NVWA', 'Boete tot 450.000 EUR', ARRAY['Warenwet-Besluit Anforderungen pruefen', 'Zulaessige Hoechstmengen einhalten', 'Novel-Food-Status pruefen', 'Kennzeichnung auf Niederlaendisch'], '[{"title": "NVWA Supplementen", "url": "https://www.nvwa.nl/onderwerpen/voedingssupplementen"}]'::jsonb, ARRAY['Proteinriegel', 'Vitaminpraeparat', 'Nahrungsergaenzungsmittel'], ARRAY['Zusammensetzungsanalyse', 'Kennzeichnung (NL)']);

-- ============================================
-- SEED ABGESCHLOSSEN
-- ============================================
SELECT 'Checklist Templates erfolgreich eingefuegt: ' || COUNT(*) || ' Eintraege' AS status FROM checklist_templates;
